INSERT INTO `sirdar_yarn` VALUES ('F023', 'INC', 'Snuggly Snowflake Dk', 'DK', 'Baby', 'f023-large.jpg', 40, 35, 55, 25, 5, 11, '85m/92y', '100% Polyester', 'Snuggly Snowflake Dk has been updated in a 25g format. It is more economical for you to use several of these delightfully sunny colours in one knit, as you can buy more accurate amounts of yarn with less left over.');
INSERT INTO `sirdar_yarn` VALUES ('F096', 'INC', 'Snuggly Kisses Dk', 'DK', 'Baby', 'f096-large.jpg', 40, 35, 56, 50, 1, 11, '165m/179y', '55% Nylon, 45% Acrylic', 'Snuggly Kisses Dk is a gorgeously soft double knitting baby yarn with a delicate stripe effect that appear as you knit. This pretty yarn comes in a beautiful palette of soft, toning shades and makes adorable knits for little ones. Kisses Dk will knit to all Sirdar baby double knitting patterns.');
