INSERT INTO `sirdar_yarn` VALUES ('F017', 'INC', 'Snuggly Snowdrops Chunky', 'Chunky', 'Baby', 'f017-large.jpg', 43, 35, 62, 50, 1, 13, '80m/88y', '60% Acrylic, 40% Polyester', 'Snuggly Snowdrops Chunky is a soft and cosy baby yarn scattered with tiny white ?snowdrops'' and comes in a fabulous range of snow white, cosy cream, biscuit and four pretty baby pastels &mdash; pink, lilac, mint and blue. You can create fabulously quick baby knits using 6mm needles with Snowdrops Chunky &mdash; it is perfect for pretty little cardies, trendy little hats and adorable baby blankets. This cosy baby yarn comes in a blend of 60% acrylic and 40% polyester, making it and machine washable and easy care.', NULL);
