INSERT INTO `sirdar_yarn` VALUES ('F042', 'INC', 'Connemara Chunky', 'Fashion', 'Fashion', 'f042-large.jpg', 33, 33, 70, 50, 1, 12, '75m/81y', '51% Wool, 49% Acrylic', 'Inspired by the wistful and romantic west coast of Ireland, Connemara chunky is a breathtaking, softly coloured tweed effect yarn. It comes in 6 hushed shades with misty blues, gentle heathers and natural smoky browns &mdash; a beautiful palette of tweedy colours for men, women and girls.  Connemara is a true chunky yarn that knits on 6&frac12; mm needles and can be knitted to all our chunky patterns. It comes in a beautifully natural, wool rich blend of 51% wool 49% acrylic, in a conventional 50g ball. Connemara''s gorgeous tweedy colours look fabulous knitted into the simplest stocking stitch, cables and textures.', NULL);
INSERT INTO `sirdar_yarn` VALUES ('F062', 'INC', 'Indie', 'Fashion', 'Fashion', 'f062-large.jpg', 33, 33, 71, 50, 1, 8, '43m/47y', '51% Wool, 49% Acrylic', 'Sirdar INDIE is a gorgeous, light and lofty yarn with a riot of magnificent colours in every ball - the fabulous colour effect appears as you knit. INDIE comes in a beautifully lightweight blend of 51% wool and 49% acrylic and comes in a conventional 50g ball. INDIE is fantastically quick to knit on 12mm needles and is easy for children and new knitters alike.', NULL);
