INSERT INTO `sirdar_yarn` VALUES ('F052', 'INC', 'Nomad', 'Fashion', 'Fashion', 'f052-large.jpg', 33, 33, 74, 50, 1, 3, '140m/153y', '60% Wool, 25% Alpaca, 15% Nylon', 'Nomad is our new, incredibly light and textured yarn made from a blend of 60% beautifully soft wool, 25% alpaca and 15% nylon &mdash; and has been created in a subtle palette of 6 gentle shades inspired by the natural winter landscape. With soft greys and three easy to wear neutrals &mdash; our Nomad shades are beautiful, natural and expensive-looking colours.', NULL);
