/** Insert description */
INSERT INTO products_description( products_id, language_id, products_name, products_description, products_viewed )
SELECT p.products_id, 1, p.dataload_name, null, 1
FROM products p
WHERE p.dataload_category_id = 106
AND NOT EXISTS (
  SELECT 1
  FROM products_description
  WHERE products_id = p.products_id
)


/** Assign to correct yarn category */
INSERT INTO products_to_categories (
  products_id, categories_id
)
select p.products_id, p.dataload_category_id
from products p
WHERE p.dataload_category_id = 106;

update `products` set
`manufacturers_id` = 3, products_price=8.4681, products_tax_class_id=1
WHERE `dataload_gender` = 'Rico Can Can'

