INSERT INTO `mse_bible_footnote` VALUES (133, 1, 46, 1, 20, 'd', '<I>Kosmos</I>, the usual word for world: see [scripture id="1co?n=3:19"]chs. 3:19[/scripture]; [scripture id="1co?n=6:2"]6:2[/scripture]; [scripture id="heb?n=9:1"]Heb. 9:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (134, 1, 46, 1, 21, 'e', '''<I>The</I> preaching'' gives a different sense from ''preaching.'' Without the article it is the instrument, the foolishness of such a means. Here ''the preaching'' is the actual thing preached; and such is the force of the Greek.');
