INSERT INTO `mse_bible_footnote` VALUES (665, 1, 9, 18, 5, 'b', 'Or ''behaved wisely:'' see [scripture id="1sa?n=18:30"]ver. 30[/scripture]; [scripture id="jer?n=23:5"]Jer. 23:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (666, 1, 9, 18, 6, 'c', 'The word is differently understood; some interpret it ''noble songs.'' It is as ''excellent things'' in [scripture id="pr?n=22:20"]Prov. 22:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (667, 1, 9, 18, 11, 'd', 'Lit. ''said;'' so [scripture id="1sa?n=18:17"]ver. 17[/scripture]; [scripture id="1sa?n=20:3"]ch. 20:3[/scripture], [scripture id="1sa?n=20:26"]26[/scripture].');
