INSERT INTO `mse_bible_footnote` VALUES (1262, 1, 61, 2, 20, 'b', '<I>Epignosis</I>, see [footnote id="n_2pe?n=1:2"]Note i, ch. 1:3[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (1263, 1, 61, 2, 22, 'c', 'See [scripture id="pr?n=26:11"]Prov. 26:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1264, 1, 61, 3, 4, 'd', 'Or ''as they were.'' For this rendering as the practical sense, see ''as he was,'' [scripture id="joh?n=4:6"]John 4:6[/scripture]. The force is the present state: as that they have continued.');
INSERT INTO `mse_bible_footnote` VALUES (1265, 1, 61, 3, 7, 'e', 'Lit. ''the now heavens,'' in contrast to the ''then world,'' [scripture id="2pe?n=3:6"]ver. 6[/scripture].');
