INSERT INTO `mse_bible_footnote` VALUES (1267, 1, 61, 3, 11, 'g', 'Conversation (manner of life) and godliness are both plural in Greek.');
INSERT INTO `mse_bible_footnote` VALUES (1268, 1, 61, 3, 12, 'h', 'The absence of the article is poetic here, ''because of which inflamed heavens shall be dissolved, and burning elements shall melt.''');
INSERT INTO `mse_bible_footnote` VALUES (1269, 1, 61, 3, 17, 'a', '<I>Ginosko</I>, as [scripture id="2pe?n=3:3"]ver. 3[/scripture]; [scripture id="2pe?n=1:20"]ch. 1:20[/scripture]. It is <I>oida</I> in [scripture id="2pe?n=1:12"]ch. 1:12[/scripture], [scripture id="2pe?n=1:14"]14[/scripture]; [scripture id="2pe?n=2:9"]2:9[/scripture]: see [scripture id="1co?n=8:1"]1Cor. 8:1[/scripture].');
