INSERT INTO `mse_bible_footnote` VALUES (1667, 1, 44, 23, 34, 'a', 'Province: as [scripture id="ac?n=25:1"]ch. 25:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1668, 1, 44, 24, 5, 'a', 'The speech is elliptical: finding him so, they would have judged him.');
INSERT INTO `mse_bible_footnote` VALUES (1669, 1, 44, 24, 5, 'b', 'Lit. ''habitable world,'' as [scripture id="re?n=12:9"]Rev. 12:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1670, 1, 44, 25, 5, 'a', 'Or ''if there be anything amiss in the man.''');
INSERT INTO `mse_bible_footnote` VALUES (1671, 1, 44, 25, 10, 'b', 'Or ''thou knowest better than that;'' that is, than to say, ''wilt thou go up to Jerusalem and be judged?''');
INSERT INTO `mse_bible_footnote` VALUES (1672, 1, 44, 25, 11, 'c', 'Lit. ''I do.'' The present tense and characteristic: see [footnote id="n_ga?n=2:14"]Note h, Gal. 2:14[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (1673, 1, 44, 26, 7, 'a', 'As [scripture id="ac?n=7:7"]chs. 7:7[/scripture]; [scripture id="ac?n=24:14"]24:14[/scripture]; [scripture id="ac?n=27:23"]27:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1674, 1, 44, 26, 14, 'a', 'As [scripture id="ac?n=9:4"]ch. 9:4[/scripture] (accusative).');
INSERT INTO `mse_bible_footnote` VALUES (1675, 1, 44, 26, 16, 'b', 'As [scripture id="1co?n=4:1"]1Cor. 4:1[/scripture].');
