INSERT INTO `mse_bible_footnote` VALUES (1677, 1, 44, 26, 25, 'd', 'As ''discretion.'' [scripture id="1ti?n=2:9"]1Tim. 2:9[/scripture],15.');
INSERT INTO `mse_bible_footnote` VALUES (1678, 1, 44, 27, 12, 'a', 'Lit. ''looking down the south-west (wind) and down the north-west.'' Down the wind is the point to which it blows.');
