INSERT INTO `mse_bible_footnote` VALUES (1705, 1, 30, 1, 5, 'e', 'Meaning, ''House of delight.''');
INSERT INTO `mse_bible_footnote` VALUES (1706, 1, 30, 1, 6, 'f', 'Lit. ''revoke it:'' see [scripture id="am?n=1:3"]ver. 3[/scripture].');
