INSERT INTO `mse_bible_footnote` VALUES (1712, 1, 30, 2, 13, 'd', 'Lit. ''in your place.'' Others, ''I am pressed under you (i.e. ''your sins'') as the cart is loaded with sheaves.''');
INSERT INTO `mse_bible_footnote` VALUES (1713, 1, 30, 2, 14, 'e', 'Or ''refuge,'' as [scripture id="jer?n=25:35"]Jer. 25:35[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1714, 1, 30, 3, 6, 'f', 'Or ''not have acted.''');
INSERT INTO `mse_bible_footnote` VALUES (1715, 1, 30, 3, 9, 'g', 'Or ''upon.''');
INSERT INTO `mse_bible_footnote` VALUES (1716, 1, 30, 3, 12, 'a', 'The best place of the divan in the houses of the rich.');
INSERT INTO `mse_bible_footnote` VALUES (1717, 1, 30, 3, 13, 'b', 'Or ''against.''');
INSERT INTO `mse_bible_footnote` VALUES (1718, 1, 30, 4, 3, 'c', 'Some translate ''to the mountains,'' others, ''to the <I>enemy''s</I> fortress,'' or ''to the palace,'' as in [scripture id="am?n=1:4"]ch. 1:4[/scripture], &c.: the meaning is not ascertained.');
INSERT INTO `mse_bible_footnote` VALUES (1719, 1, 30, 4, 4, 'd', 'Perhaps ''years;'' ''days'' in Hebrew is used for ''years,'' but here probably it is in irony.');
INSERT INTO `mse_bible_footnote` VALUES (1720, 1, 30, 4, 5, 'e', '''Burn as incense:'' see [scripture id="le?n=1:9"]Lev. 1:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1721, 1, 30, 4, 9, 'f', 'See [scripture id="joe?n=1:4"]Joel 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1722, 1, 30, 5, 3, 'a', 'i.e. that contributed a thousand men for war: cf. [scripture id="jud?n=11:3"]Judg. 11:3[/scripture] and [footnote id="n_jud?n=11:3"]Note[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (1723, 1, 30, 5, 5, 'b', 'The Hebrew for ''nought'' is here <I>Aven</I>, ''vanity,'' ''iniquity:'' see [scripture id="ho?n=4:15"]Hos. 4:15[/scripture].');
