INSERT INTO `mse_bible_footnote` VALUES (1726, 1, 30, 5, 22, 'a', '[scripture id="le?n=14:20"]Lev. 14:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1727, 1, 30, 5, 26, 'b', 'i.e. Milcom. Others render it ''your King.''');
INSERT INTO `mse_bible_footnote` VALUES (1728, 1, 30, 5, 26, 'c', 'Some understand <I>Chiun</I> as the ''stand of the idol.'' The LXX reads, ''star of your god Remphan:'' see [scripture id="ac?n=7:43"]Acts 7:43[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1729, 1, 30, 6, 1, 'd', 'Or ''careless,'' as [scripture id="isa?n=32:9"]Isa. 32:9[/scripture].');
