INSERT INTO `mse_bible_footnote` VALUES (1731, 1, 30, 6, 6, 'f', 'Or ''destruction:'' see [scripture id="la?n=3:48"]Lam. 3:48[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (1732, 1, 30, 6, 7, 'g', 'Lit. ''at the head of the captives''');
