INSERT INTO `mse_bible_footnote` VALUES (2147, 1, 5, 33, 10, 'e', 'Or ''judgments,'' the Hebrew word (<I>Mishpat</I>) having the two senses: see [scripture id="de?n=33:21"]ver. 21[/scripture] and [scripture id="de?n=17:9"]ch. 17:9[/scripture].');
