INSERT INTO `mse_bible_footnote` VALUES (2173, 1, 21, 2, 21, 'd', 'Or ''equity,'' ''success,'' [scripture id="ec?n=4:4"]ch. 4:4[/scripture], ''profit,'' [scripture id="ec?n=5:11"]ch. 5:11[/scripture].');
