INSERT INTO `mse_bible_footnote` VALUES (2222, 1, 21, 10, 11, 'g', 'Or ''he who hath a tongue is no better;'' the word for ''charmer here is lit. ''master of the tongue.''');
INSERT INTO `mse_bible_footnote` VALUES (2223, 1, 21, 10, 13, 'h', 'Lit. ''mouth.''');
