INSERT INTO `mse_bible_footnote` VALUES (2530, 1, 2, 28, 42, 'e', 'Lit. ''shall they be.''');
INSERT INTO `mse_bible_footnote` VALUES (2531, 1, 2, 29, 1, 'f', 'Lit. ''young bullock, son of the herd.''');
INSERT INTO `mse_bible_footnote` VALUES (2532, 1, 2, 29, 2, 'g', 'See [scripture id="le?n=2:4"]Lev. 2:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2533, 1, 2, 29, 9, 'a', 'Lit. ''fill their hand:'' so [scripture id="ex?n=29:33"]vers. 33[/scripture], [scripture id="ex?n=29:35"]35[/scripture]; [scripture id="ex?n=32:29"]ch. 32:29[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2534, 1, 2, 29, 10, 'b', '''Lean with:'' so [scripture id="ex?n=29:15"]vers. 15[/scripture], [scripture id="ex?n=29:19"]19[/scripture]; [scripture id="le?n=1:4"]Lev. 1:4[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2535, 1, 2, 29, 13, 'c', 'A term used for ''burning incense:'' so [scripture id="ex?n=29:18"]vers. 18[/scripture], [scripture id="ex?n=29:25"]25[/scripture]; [scripture id="ex?n=30:7~a=1"]chs. 30:7, 8[/scripture], [scripture id="ex?n=30:20"]20[/scripture]; [scripture id="ex?n=40:27"]40:27[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2536, 1, 2, 29, 14, 'd', '''Burn'' in [scripture id="ex?n=29:14"]ver. 14[/scripture] is ''to consume:'' see [scripture id="ex?n=29:34"]ver. 34[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2537, 1, 2, 29, 21, 'e', 'As [scripture id="le?n=4:6"]Lev. 4:6[/scripture], [scripture id="le?n=4:17"]17[/scripture]; [scripture id="le?n=16:14"]16:14[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2538, 1, 2, 29, 23, 'f', 'Lit. ''one cake of bread, round.;');
INSERT INTO `mse_bible_footnote` VALUES (2539, 1, 2, 29, 24, 'g', 'Or ''balance,'' here and elsewhere.');
INSERT INTO `mse_bible_footnote` VALUES (2540, 1, 2, 29, 34, 'a', '''To consume,'' as [scripture id="ex?n=29:14"]ver. 14[/scripture], not employed for the burnt-offering: see [scripture id="le?n=4:12"]Lev. 4:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2541, 1, 2, 29, 36, 'b', 'Or ''purge from defilement:'' see [scripture id="le?n=14:49"]Lev. 14:49[/scripture], [scripture id="le?n=14:52"]52[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2542, 1, 2, 29, 37, 'c', 'Lit. ''holiness of holinesses:'' so [scripture id="ex?n=30:36"]ch. 30:36[/scripture]: see [scripture id="ex?n=26:33"]ch. 26:33[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2543, 1, 2, 29, 40, 'd', 'See [scripture id="ex?n=27:20"]ch. 27:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2544, 1, 2, 29, 41, 'e', '<I>Minchah</I>, see [footnote id="n_le?n=2:1"]Note f to Lev. 2:1[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2545, 1, 2, 30, 3, 'a', 'Lit. ''roof:'' so [scripture id="ex?n=37:26"]ch. 37:26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2546, 1, 2, 30, 3, 'b', 'Lit. ''walls:'' so [scripture id="ex?n=37:26"]ch. 37:26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2547, 1, 2, 30, 4, 'c', 'Lit. ''ribs.''');
INSERT INTO `mse_bible_footnote` VALUES (2548, 1, 2, 30, 7, 'd', 'Lit. ''incense of fragrant drugs.''');
INSERT INTO `mse_bible_footnote` VALUES (2549, 1, 2, 30, 8, 'e', 'See [scripture id="ex?n=25:37"]ch. 25:37[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2550, 1, 2, 30, 10, 'f', 'Or ''upon;'' the Hebrew word indicates the object in view of which atonement is made: so [scripture id="ex?n=29:36"]ch. 29:36[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2551, 1, 2, 30, 23, 'a', 'As ''calamus'' in [scripture id="so?n=4:14"]Cant. 4:14[/scripture]; [scripture id="eze?n=27:19"]Ezek. 27:19[/scripture]; and ''cane'' as [scripture id="isa?n=43:24"]Isa. 43:24[/scripture]; [scripture id="jer?n=6:20"]Jer. 6:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2552, 1, 2, 30, 33, 'b', 'Stranger, i.e. a stranger to the priesthood -- one not a priest: see [scripture id="le?n=22:10"]Lev. 22:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2553, 1, 2, 32, 1, 'a', 'Or ''gods:'' Heb. <I>elohim</I>: similarly [scripture id="ex?n=32:4"]vers. 4[/scripture], [scripture id="ex?n=32:8"]8[/scripture], [scripture id="ex?n=32:23"]23[/scripture], [scripture id="ex?n=32:31"]31[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2554, 1, 2, 32, 8, 'b', 'Or ''have quickly forsaken.''');
INSERT INTO `mse_bible_footnote` VALUES (2555, 1, 2, 32, 18, 'a', 'Strictly ''answer of power ... answer of weakness.''');
INSERT INTO `mse_bible_footnote` VALUES (2556, 1, 2, 32, 24, 'b', 'i.e. from themselves, as [scripture id="ex?n=32:3"]ver. 3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2557, 1, 2, 32, 25, 'c', 'Or ''unbridled;'' translated ''uncover,'' [scripture id="le?n=13:45"]Lev. 13:45[/scripture]; [scripture id="nu?n=5:18"]Num. 5:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2558, 1, 2, 32, 29, 'a', 'Others, ''against.''');
INSERT INTO `mse_bible_footnote` VALUES (2559, 1, 2, 33, 6, 'b', 'Or ''from;'' i.e. from that time.');
INSERT INTO `mse_bible_footnote` VALUES (2560, 1, 2, 33, 7, 'c', 'The tent which would be in question.');
INSERT INTO `mse_bible_footnote` VALUES (2561, 1, 2, 33, 13, 'a', 'Lit. ''ways.''');
INSERT INTO `mse_bible_footnote` VALUES (2562, 1, 2, 34, 10, 'a', 'Lit. ''created.''');
INSERT INTO `mse_bible_footnote` VALUES (2563, 1, 2, 34, 13, 'b', 'Images of the goddess Asherah: possibly the same as Ashtoreth (Astarte): see [scripture id="ge?n=38:21"]Gen. 38:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2564, 1, 2, 34, 15, 'c', 'Or ''beware lest.''');
INSERT INTO `mse_bible_footnote` VALUES (2565, 1, 2, 34, 15, 'd', 'Lit. ''his.''');
INSERT INTO `mse_bible_footnote` VALUES (2566, 1, 2, 34, 18, 'e', 'See [scripture id="ex?n=13:4"]ch. 13:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2567, 1, 2, 34, 20, 'f', 'Or ''kid.''');
INSERT INTO `mse_bible_footnote` VALUES (2568, 1, 2, 34, 25, 'g', '<I>Shachat</I>, ''kill,'' ''slaughter.''');
INSERT INTO `mse_bible_footnote` VALUES (2569, 1, 2, 34, 29, 'a', 'Or ''while he talked.''');
INSERT INTO `mse_bible_footnote` VALUES (2570, 1, 2, 34, 33, 'b', 'The force would be: ''having (or, when he had) put a covering over his face.'' [scripture id="ex?n=34:35"]Ver. 35[/scripture] would, I think, imply that as long as he was getting these divine communications he kept the veil over his face except when he went into the sanctuary. [scripture id="2co?n=3"]2Cor. 3[/scripture], which has been alleged for the opposite view, proves this, for it alludes to the fact in saying of the veil on Israel''s heart, that when they turn to the Lord it will be taken off.');
INSERT INTO `mse_bible_footnote` VALUES (2571, 1, 2, 35, 2, 'c', 'Lit. ''holiness.'' <I>Kodesh</I>: see [footnote id="n_ex?n=25:8"]Note at ch. 25:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2572, 1, 2, 35, 8, 'd', 'As [scripture id="ex?n=35:28"]ver. 28[/scripture], and [scripture id="ex?n=25:6"]chs. 25:6[/scripture]; [scripture id="ex?n=39:37"]39:37[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2573, 1, 2, 35, 12, 'e', 'Strictly, ''veil of the cover,'' or ''covering veil:'' so [scripture id="ex?n=39:34"]chs. 39:34[/scripture]; [scripture id="ex?n=40:21"]40:21[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2574, 1, 2, 35, 24, 'a', 'Lit. ''heaved,'' as [scripture id="le?n=22:15"]Lev. 22:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2575, 1, 2, 36, 22, 'a', 'Or ''answering one to the other.''');
INSERT INTO `mse_bible_footnote` VALUES (2576, 1, 2, 36, 29, 'a', 'Or ''to.''');
INSERT INTO `mse_bible_footnote` VALUES (2577, 1, 2, 37, 3, 'b', 'See [scripture id="ex?n=25:12"]ch. 25:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2578, 1, 2, 37, 16, 'a', 'Or ''for.''');
INSERT INTO `mse_bible_footnote` VALUES (2579, 1, 2, 37, 17, 'b', 'Or ''lamp-stand,'' and so in [scripture id="ex?n=37:18~a=2"]vers. 18, 19, 20[/scripture], and [scripture id="ex?n=25:34"]ch. 25:34[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2580, 1, 2, 37, 19, 'c', 'Or ''almond-flowers,'' and so elsewhere.');
INSERT INTO `mse_bible_footnote` VALUES (2581, 1, 2, 38, 24, 'a', 'A talent equals 3,000 shekels.');
INSERT INTO `mse_bible_footnote` VALUES (2582, 1, 2, 39, 6, 'a', 'Or ''beryl.''');
INSERT INTO `mse_bible_footnote` VALUES (2583, 1, 2, 39, 6, 'b', 'Or ''surrounded by.''');
INSERT INTO `mse_bible_footnote` VALUES (2584, 1, 2, 39, 16, 'c', 'Or ''enclosures,'' as [scripture id="ex?n=39:6"]vers. 6[/scripture], [scripture id="ex?n=39:13"]13[/scripture]: see [scripture id="ex?n=28:11"]ch. 28:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2585, 1, 2, 39, 28, 'a', 'See [footnote id="n_ge?n=41:42"]Note, Gen. 41:42[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2586, 1, 2, 39, 37, 'b', 'Or ''lamp-stand:'' so [scripture id="ex?n=40:4"]ch. 40:4[/scripture], [scripture id="ex?n=40:24"]24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2587, 1, 2, 40, 4, 'a', 'Lit. ''its order.''');
INSERT INTO `mse_bible_footnote` VALUES (2588, 1, 2, 40, 4, 'b', 'Or ''lamp-stand:'' see [scripture id="ex?n=37:17"]ch. 37:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2589, 1, 2, 40, 4, 'c', 'See [scripture id="ex?n=25:37"]ch. 25:37[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2590, 1, 2, 40, 21, 'd', 'See [footnote id="n_ex?n=35:12"]Note, ch. 35:12[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2591, 1, 2, 40, 23, 'e', 'Lit. ''set in order upon it the order of bread.''');
INSERT INTO `mse_bible_footnote` VALUES (2592, 1, 26, 1, 1, 'c', '(The title of this book, ''Ezekiel''), Meaning, ''Strength of God.''');
INSERT INTO `mse_bible_footnote` VALUES (2593, 1, 26, 1, 1, 'd', 'Lit. ''captivity.''');
INSERT INTO `mse_bible_footnote` VALUES (2594, 1, 26, 1, 2, 'e', 'Or ''Joiachin''s;'' elsewhere called also ''Jeconias,'' and ''Coniah.''');
INSERT INTO `mse_bible_footnote` VALUES (2595, 1, 26, 1, 4, 'f', 'Or ''mingled,'' as [scripture id="ex?n=9:24"]Ex. 9:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2596, 1, 26, 1, 4, 'g', 'An unknown substance; some think a mixture of gold and silver: so in [scripture id="eze?n=1:27"]ver. 27[/scripture]; and [scripture id="eze?n=8:2"]ch. 8:2[/scripture]; see [scripture id="re?n=1:15"]Rev. 1:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2597, 1, 26, 1, 11, 'a', 'Or ''So were their faces; and.''');
INSERT INTO `mse_bible_footnote` VALUES (2598, 1, 26, 1, 13, 'b', 'Or ''flames.''');
INSERT INTO `mse_bible_footnote` VALUES (2599, 1, 26, 1, 17, 'c', 'Or ''toward.''');
INSERT INTO `mse_bible_footnote` VALUES (2600, 1, 26, 1, 20, 'd', 'Or ''over against,'' or ''beside,'' as [scripture id="eze?n=3:13"]ch. 3:13[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2601, 1, 26, 1, 20, 'e', 'Some read ''spirit of life:'' so in [scripture id="eze?n=1:21"]ver. 21[/scripture] and [scripture id="eze?n=10:17"]ch. 10:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2602, 1, 26, 1, 24, 'f', 'Lit. ''voice:'' so [scripture id="eze?n=3:12"]ch. 3:12[/scripture], ''sound.''');
INSERT INTO `mse_bible_footnote` VALUES (2603, 1, 26, 1, 24, 'g', 'Or ''camp.''');
INSERT INTO `mse_bible_footnote` VALUES (2604, 1, 26, 1, 27, 'h', 'See [footnote id="n_eze?n=1:4a"]Note g to ver. 4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2605, 1, 26, 2, 4, 'a', 'Lit. ''stiff of face:'' see [scripture id="eze?n=3:7"]ch. 3:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2606, 1, 26, 2, 4, 'b', '<I>Adonai Jehovah</I>, characteristic of this prophet, used about 200 times. <I>Adonai</I>, a plural form of <I>Adon</I>, ''lord,'' ''master,'' is a name of God, not merely a title.');
INSERT INTO `mse_bible_footnote` VALUES (2607, 1, 26, 2, 6, 'c', 'Or ''at them,'' as [scripture id="jer?n=1:17"]Jer. 1:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2608, 1, 26, 3, 5, 'd', 'Lit. ''deep of lip, and heavy of tongue.''');
INSERT INTO `mse_bible_footnote` VALUES (2609, 1, 26, 3, 13, 'a', 'Or ''over against:'' so [scripture id="eze?n=10:19"]chs. 10:19[/scripture]; [scripture id="eze?n=11:22"]11:22[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2610, 1, 26, 3, 17, 'b', 'Lit. ''given:'' see [scripture id="jer?n=1:5"]Jer. 1:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2611, 1, 26, 3, 22, 'c', 'Or ''plain:'' see [footnote id="n_jos?n=11:8"]Note, Josh. 11:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2612, 1, 26, 4, 2, 'a', 'Or ''siege-towers:'' see [scripture id="eze?n=17:17"]chs. 17:17[/scripture]; [scripture id="eze?n=21:22"]21:22[/scripture]; [scripture id="eze?n=26:8"]26:8[/scripture]; [scripture id="2ki?n=25:1"]2Kings 25:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2613, 1, 26, 4, 13, 'b', 'Or ''defiled,'' as [scripture id="eze?n=4:14"]ver. 14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2614, 1, 26, 4, 14, 'c', 'Or ''my person.''');
INSERT INTO `mse_bible_footnote` VALUES (2615, 1, 26, 4, 14, 'd', 'Or ''unclean,'' as [scripture id="le?n=7:18"]Lev. 7:18[/scripture]; [scripture id="le?n=19:7"]19:7[/scripture]; [scripture id="isa?n=65:4"]Isa. 65:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2616, 1, 26, 4, 17, 'e', 'Or ''through.''');
INSERT INTO `mse_bible_footnote` VALUES (2617, 1, 26, 5, 1, 'f', 'Elsewhere ''sword.''');
INSERT INTO `mse_bible_footnote` VALUES (2618, 1, 26, 5, 2, 'a', 'Lit. ''light,'' and so in [scripture id="isa?n=31:9"]Isa. 31:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2619, 1, 26, 5, 4, 'b', 'i.e. still from the third part last mentioned: see [scripture id="zec?n=13:8~a=1"]Zech. 13:8, 9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2620, 1, 26, 5, 6, 'c', 'Or ''judgments;'' so [scripture id="eze?n=11:12"]ch. 11:12[/scripture]; ''manner'' in [scripture id="2ki?n=17:26"]2Kings 17:26[/scripture], [scripture id="2ki?n=17:33"]33[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2621, 1, 26, 5, 6, 'd', '<I>choq</I>, as [scripture id="ex?n=12:24"]Ex. 12:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2622, 1, 26, 5, 11, 'e', 'Lit. ''if ... not.'' A strong form of solemn declaration: see [scripture id="nu?n=14:23"]Num. 14:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2623, 1, 26, 5, 15, 'f', 'Or ''desolation,'' as [scripture id="eze?n=33:28~a=1"]ch. 33:28, 29[/scripture], where both words are used.');
INSERT INTO `mse_bible_footnote` VALUES (2624, 1, 26, 6, 3, 'a', 'Or ''ravines.''');
INSERT INTO `mse_bible_footnote` VALUES (2625, 1, 26, 6, 4, 'b', 'A term of contempt, used 38 times in Ezekiel. See [scripture id="le?n=26:30"]Lev. 26:30[/scripture]; [scripture id="de?n=29:17"]Deut. 29:17[/scripture]; [scripture id="1ki?n=21:26"]1Kings 21:26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2626, 1, 26, 6, 9, 'c', 'Or ''because I am broken with their. ...''');
INSERT INTO `mse_bible_footnote` VALUES (2627, 1, 26, 6, 10, 'd', 'Or ''that I, Jehovah, have not.''');
INSERT INTO `mse_bible_footnote` VALUES (2628, 1, 26, 7, 7, 'a', 'Or ''destiny:'' lit. ''turn,'' or ''cycle.''');
INSERT INTO `mse_bible_footnote` VALUES (2629, 1, 26, 7, 11, 'b', 'As [scripture id="eze?n=7:23"]ver. 23[/scripture]; [scripture id="zep?n=1:9"]Zeph. 1:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2630, 1, 26, 7, 11, 'c', 'A play on words here.');
INSERT INTO `mse_bible_footnote` VALUES (2631, 1, 26, 7, 11, 'd', 'Or ''neither shall there be wailing for them.''');
INSERT INTO `mse_bible_footnote` VALUES (2632, 1, 26, 7, 13, 'e', 'Lit. ''they.'' Cf. [scripture id="le?n=2:5"]Lev. 2:5[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2633, 1, 26, 7, 13, 'f', 'Or ''for the vision touching her noisy multitude shall not be revoked.''');
INSERT INTO `mse_bible_footnote` VALUES (2634, 1, 26, 7, 13, 'g', 'Or ''strengthen.''');
INSERT INTO `mse_bible_footnote` VALUES (2635, 1, 26, 7, 19, 'h', 'Or ''bowels.''');
INSERT INTO `mse_bible_footnote` VALUES (2636, 1, 26, 7, 20, 'a', 'Or ''of his beautiful ornament, he (i.e. the people) hath made his pride; and they have made therefrom.''');
INSERT INTO `mse_bible_footnote` VALUES (2637, 1, 26, 7, 22, 'b', 'Or ''robbers.''');
INSERT INTO `mse_bible_footnote` VALUES (2638, 1, 26, 7, 24, 'c', 'Or ''they shall inherit their sanctuaries.''');
INSERT INTO `mse_bible_footnote` VALUES (2639, 1, 26, 8, 2, 'd', 'See [footnote id="n_eze?n=1:4a"]Note, ch. 1:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2640, 1, 26, 8, 4, 'e', 'Or ''plain,'' as [scripture id="eze?n=3:22"]ch. 3:22[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2641, 1, 26, 8, 10, 'f', 'Or ''pattern,'' as [scripture id="de?n=4:17"]Deut. 4:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2642, 1, 26, 8, 12, 'a', 'Or ''earth.''');
INSERT INTO `mse_bible_footnote` VALUES (2643, 1, 26, 8, 14, 'b', 'A Syrian and Phoenician idol identified with Adonis.');
INSERT INTO `mse_bible_footnote` VALUES (2644, 1, 26, 9, 1, 'c', 'Or ''Cause to draw near the chastisements of,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (2645, 1, 26, 9, 2, 'd', 'Lit. ''dashing-in-pieces;'' [scripture id="jer?n=51:20"]Jer. 51:20[/scripture], ''maul.''');
INSERT INTO `mse_bible_footnote` VALUES (2646, 1, 26, 9, 2, 'e', 'Lit. ''on his loins.''');
INSERT INTO `mse_bible_footnote` VALUES (2647, 1, 26, 9, 4, 'f', 'The word is <I>Tau</I>, the name of the last letter of the Hebrew alphabet.');
INSERT INTO `mse_bible_footnote` VALUES (2648, 1, 26, 9, 9, 'a', 'Or ''wresting <I>of judgment</I>.''');
INSERT INTO `mse_bible_footnote` VALUES (2649, 1, 26, 9, 10, 'b', 'Lit. ''put,'' as also [scripture id="eze?n=22:31"]ch. 22:31[/scripture] and [scripture id="1ki?n=8:32"]1Kings 8:32[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2650, 1, 26, 10, 5, 'c', '<I>El-Shaddai</I>: see [scripture id="ge?n=17:1"]Gen. 17:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2651, 1, 26, 10, 12, 'd', 'See [footnote id="n_re?n=4:8a"]Note b, Rev. 4:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (2652, 1, 26, 10, 13, 'e', '''Wheel,'' or ''Whirlwind.''');
INSERT INTO `mse_bible_footnote` VALUES (2653, 1, 26, 10, 19, 'a', 'Lit. ''he,'' or ''it.''');
INSERT INTO `mse_bible_footnote` VALUES (2654, 1, 26, 11, 3, 'b', 'Lit. ''not near.''');
INSERT INTO `mse_bible_footnote` VALUES (2655, 1, 26, 11, 3, 'c', 'See [scripture id="jer?n=29:5"]Jer. 29:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2656, 1, 26, 11, 3, 'd', 'i.e. the city: so [scripture id="eze?n=11:7"]vers. 7[/scripture], [scripture id="eze?n=11:11"]11[/scripture]: see [scripture id="eze?n=24:6"]ch. 24:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2657, 1, 26, 11, 10, 'e', 'Or ''in the territory of.''');
INSERT INTO `mse_bible_footnote` VALUES (2658, 1, 26, 11, 13, 'a', '[scripture id="ne?n=9:31"]Neh. 9:31[/scripture]; [scripture id="jer?n=4:27"]Jer. 4:27[/scripture]; [scripture id="jer?n=5:10"]5:10[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (2659, 1, 26, 11, 15, 'b', 'See [scripture id="le?n=25:25"]Lev. 25:25[/scripture], [scripture id="le?n=25:48"]48[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (2660, 1, 26, 11, 20, 'c', 'Lit. ''for a people for me, and I will be to them for God.''');
