INSERT INTO `mse_bible_footnote` VALUES (3726, 1, 28, 3, 2, 'o', '* Half-homer is <I>lethec</I> = 15 <I>seahs</I>. A <I>seah</I> is about a peck: see [scripture id="ex?n=16:36"]Ex. 16:36[/scripture]; [scripture id="1ki?n=18:32"]1Kings 18:32[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (3727, 1, 28, 3, 4, 'a', 'Or ''pillar,'' ''column,'' as [scripture id="1ki?n=14:23"]1Kings 14:23[/scripture]; [scripture id="isa?n=19:19"]Isa. 19:19[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (3728, 1, 28, 4, 1, 'b', 'Or ''loving-kindness,'' see [footnote id="n_ho?n=2:19a"]Note l, ch. 2:19[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (3729, 1, 28, 4, 6, 'c', '* Lit. ''the knowledge,'' see [scripture id="ho?n=4:1"]ver. 1[/scripture].');
