INSERT INTO `mse_bible_footnote` VALUES (3731, 1, 28, 4, 14, 'e', 'Or ''shall fall,'' as [scripture id="pr?n=10:8"]Prov. 10:8[/scripture], [scripture id="pr?n=10:10"]10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (3732, 1, 28, 4, 15, 'f', 'Meaning, ''House of idols,'' or ''of iniquity'' -- irony for <I>Beth-El</I>, ''House of God:'' see [scripture id="am?n=5:5"]Amos 5:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (3733, 1, 28, 4, 18, 'g', 'Or ''their orgies are vile.''');
