INSERT INTO `mse_bible_footnote` VALUES (3743, 1, 28, 5, 14, 'k', 'Or ''fierce lion,'' as [scripture id="ho?n=13:7"]ch. 13:7[/scripture]; [scripture id="job?n=4:10"]Job 4:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (3744, 1, 28, 5, 15, 'l', 'Or ''guilt,'' <I>asham</I>: see [footnote id="n_le?n=5:1"]Note b, Lev. 5:1[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (3745, 1, 28, 5, 15, 'm', 'Or ''earnestly.''');
