INSERT INTO `mse_bible_footnote` VALUES (3780, 1, 28, 11, 6, 'e', 'Or ''branches.''');
INSERT INTO `mse_bible_footnote` VALUES (3781, 1, 28, 11, 8, 'f', 'Or ''What shall I do with thee?''');
INSERT INTO `mse_bible_footnote` VALUES (3782, 1, 28, 11, 9, 'g', 'Some translate ''come into the city.''');
