INSERT INTO `mse_bible_footnote` VALUES (3791, 1, 28, 12, 11, 'e', 'Or ''Is Gilead iniquity? They are altogether vanity.''');
INSERT INTO `mse_bible_footnote` VALUES (3792, 1, 28, 12, 12, 'f', 'Or ''fields,'' or ''open country'' of Aram:'' see [scripture id="ge?n=28:2"]Gen. 28:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (3793, 1, 28, 12, 13, 'g', 'Or ''kept,'' as [scripture id="ho?n=12:12"]ver. 12[/scripture].');
