INSERT INTO `mse_bible_footnote` VALUES (4014, 1, 23, 26, 15, 'h', 'Or ''Thou hast extended all the boundaries of the land.''');
INSERT INTO `mse_bible_footnote` VALUES (4015, 1, 23, 26, 16, 'i', 'Or ''lisping,'' or ''secret speech.''');
INSERT INTO `mse_bible_footnote` VALUES (4016, 1, 23, 26, 19, 'a', 'According to others ''<I>with</I> my dead body, they.''');
