INSERT INTO `mse_bible_footnote` VALUES (4031, 1, 23, 28, 5, 'b', 'Or ''adornment,'' as [scripture id="isa?n=28:1"]vers. 1[/scripture], [scripture id="isa?n=28:4"]4[/scripture], [scripture id="ps?n=96:6"]Ps. 96:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (4032, 1, 23, 28, 7, 'c', 'Lit. ''are swallowed up,'' as [scripture id="isa?n=3:12"]ch. 3:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (4033, 1, 23, 28, 16, 'd', 'Lit. ''a foundation well-founded.''');
