INSERT INTO `mse_bible_footnote` VALUES (4050, 1, 23, 30, 4, 'l', 'i.e. Judah''s.');
INSERT INTO `mse_bible_footnote` VALUES (4051, 1, 23, 30, 4, 'a', 'Tahpanhes: see [scripture id="jer?n=2:16"]Jer. 2:16[/scripture]; [scripture id="jer?n=43:7"]43:7[/scripture]; [scripture id="eze?n=30:18"]Ezek. 30:18[/scripture]: Heracleopolis, in Central Egypt.');
INSERT INTO `mse_bible_footnote` VALUES (4052, 1, 23, 30, 7, 'b', 'Heb. <I>Rahab</I>, a name of Egypt.');
INSERT INTO `mse_bible_footnote` VALUES (4053, 1, 23, 30, 8, 'c', 'Lit. ''engrave.''');
INSERT INTO `mse_bible_footnote` VALUES (4054, 1, 23, 30, 8, 'd', 'Instead of ''as a witness for ever,'' many take it as ''for ever and ever;'' but cf. [scripture id="de?n=31:26"]Deut. 31:26[/scripture].');
