INSERT INTO `mse_bible_footnote` VALUES (4056, 1, 23, 30, 13, 'f', 'Or ''unexpectedly,'' as [scripture id="nu?n=6:9"]Num. 6:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (4057, 1, 23, 30, 18, 'g', 'Or ''will he withdraw himself until he have mercy.''');
