INSERT INTO `mse_bible_footnote` VALUES (4077, 1, 23, 32, 14, 'l', 'Or, as a proper name, ''Ophel.''');
INSERT INTO `mse_bible_footnote` VALUES (4078, 1, 23, 32, 19, 'a', 'Or ''laid low.''');
INSERT INTO `mse_bible_footnote` VALUES (4079, 1, 23, 33, 4, 'b', 'Lit. ''the devourer:'' see [scripture id="ps?n=78:46"]Ps. 78:46[/scripture]; [scripture id="joe?n=1:4"]Joel 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (4080, 1, 23, 33, 6, 'c', 'Or ''wisdom and knowledge shall be the stability of thy times, the riches of salvation.''');
