INSERT INTO `mse_bible_footnote` VALUES (4091, 1, 23, 34, 11, 'e', '''Waste ... emptiness:'' see [scripture id="isa?n=45:18"]ch. 45:18[/scripture]; [scripture id="ge?n=1:2"]Gen. 1:2[/scripture]; [scripture id="jer?n=4:23"]Jer. 4:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (4092, 1, 23, 34, 12, 'f', 'Or ''whom they call to.''');
INSERT INTO `mse_bible_footnote` VALUES (4093, 1, 23, 34, 14, 'g', 'Or ''night-spectre.''');
INSERT INTO `mse_bible_footnote` VALUES (4094, 1, 23, 34, 17, 'a', 'i.e. the land of Edom.');
