INSERT INTO `mse_bible_footnote` VALUES (4105, 1, 23, 37, 22, 'c', 'See [footnote id="n_2ki?n=19:21"]Note, 2Kings 19:21[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (4106, 1, 23, 37, 24, 'a', 'Or ''forest of his Carmel.''');
INSERT INTO `mse_bible_footnote` VALUES (4107, 1, 23, 37, 25, 'b', 'Egypt: see [scripture id="isa?n=19:6"]ch. 19:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (4108, 1, 23, 37, 26, 'c', 'Or ''from afar.''');
