INSERT INTO `mse_bible_footnote` VALUES (4119, 1, 23, 38, 17, 'i', 'Or ''for <I>my</I> peace.''');
INSERT INTO `mse_bible_footnote` VALUES (4120, 1, 23, 39, 2, 'a', 'Or ''precious ointment,'' as [scripture id="ec?n=7:1"]Eccles. 7:1[/scripture].');
