INSERT INTO `mse_bible_footnote` VALUES (5212, 1, 43, 19, 29, 'b', 'Probably means, ''binding it to hyssop.''');
INSERT INTO `mse_bible_footnote` VALUES (5213, 1, 43, 19, 35, 'c', 'Both verbs, ''saw'' and ''bears,'' are in the perfect tense, expressing what was present in the mind of the writer.');
