INSERT INTO `mse_bible_footnote` VALUES (5249, 1, 6, 3, 16, 'b', 'The <I>Arabah</I>: see [scripture id="jos?n=11:2"]chs. 11:2[/scripture], [scripture id="jos?n=11:16"]16[/scripture]; [scripture id="jos?n=12:1"]12:1[/scripture], [scripture id="jos?n=12:3"]3[/scripture]; [scripture id="jos?n=18:18"]18:18[/scripture]; [scripture id="de?n=1:1"]Deut. 1:1[/scripture]. Meaning strictly ''dry-<I>land</I>,'' but applied to the narrow strip of land in the Jordan valley on both sides of the river below the lake of Gennesaret. In [scripture id="jos?n=4:13"]ch. 4:13[/scripture] it seems to mean the parts of the <I>Arabah</I> about Jericho.');
INSERT INTO `mse_bible_footnote` VALUES (5250, 1, 6, 4, 6, 'c', 'Lit. ''to-morrow;'' and so [scripture id="jos?n=4:21"]ver. 21[/scripture].');
