INSERT INTO `mse_bible_footnote` VALUES (5404, 1, 7, 5, 9, 'a', 'As ''lawgiver,'' [scripture id="ge?n=49:10"]Gen. 49:10[/scripture]; [scripture id="ps?n=60:7"]Ps. 60:7[/scripture], &c.');
