INSERT INTO `mse_bible_footnote` VALUES (5432, 1, 7, 7, 19, 'b', 'i.e. midnight: see [scripture id="la?n=2:19"]Lam. 2:19[/scripture] and [scripture id="1sa?n=11:11"]1Sam. 11:11[/scripture].');
