INSERT INTO `mse_bible_footnote` VALUES (5664, 1, 3, 13, 52, 'a', '''Consume wholly:'' see [scripture id="le?n=4:12"]ch. 4:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (5665, 1, 3, 13, 55, 'b', 'Or ''colour;'' lit. ''eye.''');
INSERT INTO `mse_bible_footnote` VALUES (5666, 1, 3, 13, 55, 'c', 'The words are the same as in [scripture id="le?n=13:42"]vers. 42[/scripture] and 43, applied to the head, and here alone used thus.');
INSERT INTO `mse_bible_footnote` VALUES (5667, 1, 3, 13, 59, 'd', 'Or ''to pronounce it clean.''');
INSERT INTO `mse_bible_footnote` VALUES (5668, 1, 3, 14, 4, 'e', 'Little birds. As ''sparrow,'' [scripture id="ps?n=84:3"]Ps. 84:3[/scripture]; [scripture id="ps?n=102:7"]102:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (5669, 1, 3, 14, 5, 'f', 'Lit.''living.''');
