INSERT INTO `mse_bible_footnote` VALUES (5671, 1, 3, 14, 8, 'h', '''Bathe'' here and in all these passages is ''bathe the whole body.''');
INSERT INTO `mse_bible_footnote` VALUES (5672, 1, 3, 14, 10, 'i', 'A twelfth part of a hin.');
INSERT INTO `mse_bible_footnote` VALUES (5673, 1, 3, 14, 20, 'a', '<I>Olah</I>, ''offer up'' or ''cause to ascend'' on the altar itself: see [footnote id="n_le?n=2:12a"]Note c, ch. 2:12[/footnote].');
