INSERT INTO `mse_bible_footnote` VALUES (5689, 1, 3, 17, 3, 'c', '''Kill as sacrifice,'' as [scripture id="le?n=1:5"]chs. 1:5[/scripture] and [scripture id="le?n=3:2"]3:2[/scripture]; [scripture id="ex?n=34:25"]Ex. 34:25[/scripture].');
