INSERT INTO `mse_bible_footnote` VALUES (5692, 1, 3, 17, 7, 'b', 'Lit. ''hairy.'' hence ''a buck-goat.'' Sacrifices were offered to imaginary beings thus designated: see [scripture id="isa?n=13:21"]Isa. 13:21[/scripture]; [scripture id="isa?n=34:14"]34:14[/scripture].');
