INSERT INTO `mse_bible_footnote` VALUES (5694, 1, 3, 17, 9, 'd', 'Lit. ''to perform it.''');
INSERT INTO `mse_bible_footnote` VALUES (5695, 1, 3, 17, 11, 'e', 'Or ''life;'' ''life'' and ''soul'' are the same word everywhere here.');
INSERT INTO `mse_bible_footnote` VALUES (5696, 1, 3, 17, 11, 'f', 'Lit. ''in souls:'' see [footnote id="n_le?n=6:30"]Note, ch. 6:30[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (5697, 1, 3, 17, 11, 'g', 'Or ''that maketh atonement by the soul (or life).''');
INSERT INTO `mse_bible_footnote` VALUES (5698, 1, 3, 17, 14, 'h', 'Lit. ''its blood is for its life,'' the preposition here denoting essence.');
INSERT INTO `mse_bible_footnote` VALUES (5699, 1, 3, 18, 4, 'i', 'Or ''judgments,'' as [scripture id="le?n=18:5"]ver. 5[/scripture] and elsewhere.');
INSERT INTO `mse_bible_footnote` VALUES (5700, 1, 3, 18, 6, 'a', 'Lit. ''own flesh of his flesh.''');
