INSERT INTO `mse_bible_footnote` VALUES (6934, 1, 57, 1, 7, 'a', '<I>Epi</I>: ''by occasion of:'' see [scripture id="ro?n=10:19"]Rom. 10:19[/scripture]. It was the condition of his joy.');
INSERT INTO `mse_bible_footnote` VALUES (6935, 1, 57, 1, 9, 'b', 'Or ''Christ Jesus,'' according to some authorities.');
INSERT INTO `mse_bible_footnote` VALUES (6936, 1, 57, 1, 13, 'c', '<I>Huper</I>: ''instead of thee.''');
