INSERT INTO `mse_bible_footnote` VALUES (7230, 1, 20, 22, 8, 'd', 'Or ''vanity,'' ''mischief.''');
INSERT INTO `mse_bible_footnote` VALUES (7231, 1, 20, 22, 12, 'e', 'Or ''treacherous <I>man</I>.'' as [scripture id="pr?n=21:18"]ch. 21:18[/scripture]: see [scripture id="pr?n=2:22"]chs. 2:22[/scripture] and [scripture id="pr?n=25:19"]25:19[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7232, 1, 20, 22, 16, 'f', 'As [scripture id="pr?n=10:15"]ch. 10:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7233, 1, 20, 22, 16, 'g', 'Or ''He that oppresseth the poor to make increase for himself <I>and</I> he that giveth to the rich shall certainly <I>come</I> to want.''');
INSERT INTO `mse_bible_footnote` VALUES (7234, 1, 20, 22, 25, 'h', 'As ''ways,'' [scripture id="ps?n=25:4"]Ps. 25:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7235, 1, 20, 23, 4, 'a', 'See [footnote id="n_pr?n=3:5"]Note, ch. 3:5[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7236, 1, 20, 23, 12, 'b', 'Or ''correction:'' see [scripture id="pr?n=23:13"]ver. 13[/scripture] and [scripture id="pr?n=1:2"]ch. 1:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7237, 1, 20, 23, 17, 'c', 'Or ''but <I>let it covet</I> the fear.''');
INSERT INTO `mse_bible_footnote` VALUES (7238, 1, 20, 23, 18, 'd', 'Or ''end;'' ''future,'' as [scripture id="pr?n=24:14"]ch. 24:14[/scripture], [scripture id="pr?n=24:20"]20[/scripture]: [scripture id="ps?n=37:37"]Ps. 37:37[/scripture]: see [footnote id="n_pr?n=23:32"]Note k, ver. 32[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7239, 1, 20, 23, 23, 'e', 'Or ''Get:'' see [scripture id="pr?n=1:5"]chs. 1:5[/scripture]; [scripture id="pr?n=4:5"]4:5[/scripture], [scripture id="pr?n=4:7"]7[/scripture]; [scripture id="pr?n=18:15"]18:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7240, 1, 20, 23, 26, 'f', 'Or ''delight in.''');
INSERT INTO `mse_bible_footnote` VALUES (7241, 1, 20, 23, 29, 'g', 'Lit. ''Whose is, ''''Oh!'''' Whose is, ''''woe is me!''''?''');
INSERT INTO `mse_bible_footnote` VALUES (7242, 1, 20, 23, 29, 'h', 'Or ''darkening.''');
INSERT INTO `mse_bible_footnote` VALUES (7243, 1, 20, 23, 30, 'i', 'Or ''late.''');
INSERT INTO `mse_bible_footnote` VALUES (7244, 1, 20, 23, 32, 'k', 'Or ''in result;'' lit. ''in its end:'' as [scripture id="pr?n=23:18"]ver. 18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7245, 1, 20, 24, 5, 'a', '<I>Geber:</I> see [scripture id="job?n=3:3"]Job 3:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7246, 1, 20, 24, 6, 'b', 'Or ''wise counsels,'' as [scripture id="pr?n=1:5"]chs. 1:5[/scripture]; [scripture id="pr?n=11:14"]11:14[/scripture]; [scripture id="pr?n=20:18"]20:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7247, 1, 20, 24, 6, 'c', 'Or ''victory,'' as [scripture id="pr?n=11:14"]ch. 11:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7248, 1, 20, 24, 7, 'd', 'Or ''Jewels (lit. ''high-priced things'') for a fool are wisdom.''');
INSERT INTO `mse_bible_footnote` VALUES (7249, 1, 20, 24, 10, 'e', 'There is a play on the words ''trouble'' and ''small.''');
INSERT INTO `mse_bible_footnote` VALUES (7250, 1, 20, 24, 12, 'f', 'As [scripture id="1sa?n=2:3"]1Sam. 2:3[/scripture]; ''meted,'' [scripture id="isa?n=40:12"]Isa. 40:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7251, 1, 20, 24, 14, 'g', 'Or ''know.''');
INSERT INTO `mse_bible_footnote` VALUES (7252, 1, 20, 24, 16, 'h', 'Lit. ''evil,'' as [scripture id="pr?n=17:20"]chs. 17:20[/scripture]; [scripture id="pr?n=28:14"]28:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7253, 1, 20, 24, 20, 'i', 'Or ''reward.''');
INSERT INTO `mse_bible_footnote` VALUES (7254, 1, 20, 24, 24, 'k', '<I>Leummim</I>: see [scripture id="ps?n=2:1"]Ps. 2:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7255, 1, 20, 24, 34, 'a', 'Or ''a tramp,'' as [scripture id="pr?n=6:11"]ch. 6:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7256, 1, 20, 25, 6, 'b', 'Or ''Make not thyself grand.''');
INSERT INTO `mse_bible_footnote` VALUES (7257, 1, 20, 25, 7, 'c', 'Or ''noble.''');
INSERT INTO `mse_bible_footnote` VALUES (7258, 1, 20, 25, 11, 'd', 'Others, ''baskets.''');
INSERT INTO `mse_bible_footnote` VALUES (7259, 1, 20, 25, 11, 'e', 'Or ''fitly.''');
INSERT INTO `mse_bible_footnote` VALUES (7260, 1, 20, 25, 14, 'f', 'Or ''falsely of giving.''');
INSERT INTO `mse_bible_footnote` VALUES (7261, 1, 20, 25, 23, 'a', 'Or ''So doth a backbiting (lit. ''secret'') tongue, an angry countenance.''');
INSERT INTO `mse_bible_footnote` VALUES (7262, 1, 20, 25, 26, 'b', 'Lit. ''tottereth.''');
INSERT INTO `mse_bible_footnote` VALUES (7263, 1, 20, 25, 27, 'c', 'Or ''is glory,'' the word having both meanings. Or ''so <I>for men</I> to seek their own glory is <I>not</I> glory.''');
INSERT INTO `mse_bible_footnote` VALUES (7264, 1, 20, 26, 1, 'd', '<I>Kesil</I>: so in all this passage, [scripture id="pr?n=26:1~a=11"]vers. 1 to 12[/scripture]: see [scripture id="pr?n=1:7"]ch. 1:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7265, 1, 20, 26, 6, 'e', 'Lit. ''violence'' or ''wrong.''');
INSERT INTO `mse_bible_footnote` VALUES (7266, 1, 20, 26, 8, 'f', 'Or ''As the binding of a stone in a sling.''');
INSERT INTO `mse_bible_footnote` VALUES (7267, 1, 20, 26, 10, 'g', 'Or ''as an archer that woundeth all, so is he that hireth,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (7268, 1, 20, 26, 18, 'h', 'Or ''jester.''');
INSERT INTO `mse_bible_footnote` VALUES (7269, 1, 20, 26, 22, 'i', 'See [scripture id="pr?n=18:8"]ch. 18:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7270, 1, 20, 27, 2, 'a', 'Or ''a stranger,'' as [scripture id="pr?n=5:10"]ch. 5:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7271, 1, 20, 27, 6, 'b', 'As [scripture id="pr?n=18:24"]ch. 18:24[/scripture]: strictly, ''one who loves.''');
INSERT INTO `mse_bible_footnote` VALUES (7272, 1, 20, 27, 18, 'c', 'Or ''heedeth,'' as [scripture id="pr?n=8:34"]ch. 8:34[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7273, 1, 20, 27, 20, 'd', 'As [scripture id="pr?n=15:11"]ch. 15:11[/scripture]; [scripture id="job?n=26:6"]Job 26:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7274, 1, 20, 27, 23, 'e', 'Lit. ''set thine heart on the.''');
INSERT INTO `mse_bible_footnote` VALUES (7275, 1, 20, 28, 3, 'a', '<I>Geber</I>: so [scripture id="pr?n=28:21"]ver 21[/scripture]: see [scripture id="job?n=3:3"]Job 3:3[/scripture]. ''Poor,'' in [scripture id="pr?n=28:3"]vers. 3[/scripture], [scripture id="pr?n=28:6"]6[/scripture], [scripture id="pr?n=28:27"]27[/scripture], as [scripture id="pr?n=22:2"]ch. 22:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7276, 1, 20, 28, 3, 'b', '<I>Dal</I>, ''poor,'' plural, as [scripture id="pr?n=10:15"]chs. 10:15[/scripture]; [scripture id="pr?n=22:16"]22:16[/scripture]; [scripture id="pr?n=29:7"]29:7[/scripture], [scripture id="pr?n=29:14"]14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7277, 1, 20, 28, 14, 'c', 'Or ''disaster:'' see [scripture id="pr?n=24:16"]ch. 24:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7278, 1, 20, 28, 18, 'd', 'Or ''at once.''');
INSERT INTO `mse_bible_footnote` VALUES (7279, 1, 20, 28, 22, 'e', 'Or ''he that hasteth after wealth hath an evil eye.''');
INSERT INTO `mse_bible_footnote` VALUES (7280, 1, 20, 28, 26, 'a', '<I>Kesil:</I> see [footnote id="n_pr?n=1:7"]Note o, ch. 1:7[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7281, 1, 20, 29, 1, 'b', 'Lit. [scripture id="pr?n=29:1"]ver. 1[/scripture] ''A man of reproofs;'' [scripture id="pr?n=29:4"]ver. 4[/scripture]: ''a man of gifts.''');
INSERT INTO `mse_bible_footnote` VALUES (7282, 1, 20, 29, 5, 'c', '<I>Geber</I>: so [scripture id="pr?n=30:1"]ch. 30:1[/scripture], [scripture id="pr?n=30:19"]19[/scripture]: see [scripture id="job?n=3:3"]Job 3:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7283, 1, 20, 29, 6, 'd', 'Or ''shout,'' as [scripture id="isa?n=24:14"]Isa. 24:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7284, 1, 20, 29, 9, 'e', 'Lit. ''a man, a fool'' (<I>Ish eveel</I>): see [scripture id="pr?n=1:7"]ch. 1:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7285, 1, 20, 29, 10, 'f', 'Others, ''and <I>as for</I> the upright, they seek his life.''');
INSERT INTO `mse_bible_footnote` VALUES (7286, 1, 20, 29, 11, 'g', 'Or ''passion.''');
INSERT INTO `mse_bible_footnote` VALUES (7287, 1, 20, 29, 11, 'h', 'Lit. ''keepeth back and calmeth.''');
INSERT INTO `mse_bible_footnote` VALUES (7288, 1, 20, 29, 13, 'i', 'As [scripture id="pr?n=22:2"]ch. 22:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7289, 1, 20, 29, 13, 'k', 'Or ''brighteneth.''');
INSERT INTO `mse_bible_footnote` VALUES (7290, 1, 20, 29, 21, 'l', 'Strictly, ''son of the house;'' i.e. he gets into possession of his master''s goods.');
INSERT INTO `mse_bible_footnote` VALUES (7291, 1, 20, 29, 24, 'a', 'See [scripture id="le?n=5:1"]Lev. 5:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7292, 1, 20, 29, 25, 'b', 'Or ''shall be set on high:'' see [scripture id="ps?n=20:1"]Ps. 20:1[/scripture]; [scripture id="ps?n=69:29"]69:29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7293, 1, 20, 30, 1, 'c', 'Lit. ''burden'' or ''oracle;'' as ''music,'' [scripture id="1ch?n=15:22"]1Chron. 15:22[/scripture]. The word ''uttered,'' here is as [scripture id="ps?n=36:1"]Ps. 36:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7294, 1, 20, 30, 3, 'd', 'See [footnote id="n_pr?n=9:10a"]Note i, ch. 9:10[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7295, 1, 20, 30, 5, 'e', '<I>Imrah:</I> see [scripture id="ps?n=119:11"]Ps. 119:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7296, 1, 20, 30, 5, 'f', 'Or ''tried,'' as [scripture id="ps?n=18:30"]Ps. 18:30[/scripture]: see [scripture id="ps?n=119:140"]Ps. 119:140[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7297, 1, 20, 30, 9, 'g', 'Strictly, ''lay hands on:'' for this use of the word, see [scripture id="pr?n=30:28"]ver. 28[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7298, 1, 20, 30, 10, 'h', 'Or ''Accuse not.''');
INSERT INTO `mse_bible_footnote` VALUES (7299, 1, 20, 30, 22, 'a', 'Or ''fool,'' ''vile person,'' <I>Nabal</I>: see [scripture id="pr?n=1:7"]ch. 1:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7300, 1, 20, 30, 26, 'b', 'See [scripture id="le?n=11:5"]Lev. 11:5[/scripture]; [scripture id="ps?n=104:18"]Ps. 104:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7301, 1, 20, 30, 30, 'c', 'Strictly, ''an old lion:'' see [scripture id="job?n=4:11"]Job 4:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7302, 1, 20, 30, 31, 'd', 'Possibly, ''a war-horse.''');
INSERT INTO `mse_bible_footnote` VALUES (7303, 1, 20, 31, 1, 'e', 'As [scripture id="pr?n=30:1"]ch. 30:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7304, 1, 20, 31, 4, 'f', 'As [scripture id="pr?n=8:15"]ch. 8:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7305, 1, 20, 31, 4, 'g', 'Some read ''to desire strong drink.''');
INSERT INTO `mse_bible_footnote` VALUES (7306, 1, 20, 31, 8, 'h', 'Lit. ''sons of decease'' (i.e. of those deceased).');
INSERT INTO `mse_bible_footnote` VALUES (7307, 1, 20, 31, 10, 'i', 'From [scripture id="pr?n=31:10"]ver. 10[/scripture] to the end, the initial Hebrew letter of each verse follows the alphabetical order.');
INSERT INTO `mse_bible_footnote` VALUES (7308, 1, 20, 31, 10, 'k', 'As [scripture id="pr?n=3:15"]ch. 3:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7309, 1, 20, 31, 13, 'l', 'Lit. ''palms'' here and in [scripture id="nu?n=5:18"]Num. 5:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7310, 1, 20, 31, 15, 'm', 'Or ''daily provision:'' see [footnote id="n_ex?n=12:24"]Note d, Ex. 12:24[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7311, 1, 20, 31, 19, 'a', 'Not the same word as that marked b in [scripture id="pr?n=31:19~a=1"]vers. 19 and 20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7312, 1, 20, 31, 19, 'b', 'Lit. ''palms.'' see [footnote id="n_pr?n=31:13"]Note l, v. 13[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7313, 1, 20, 31, 22, 'c', 'See [scripture id="pr?n=7:16"]ch. 7:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7314, 1, 20, 31, 22, 'd', 'Or ''linen.''');
INSERT INTO `mse_bible_footnote` VALUES (7315, 1, 19, 1, 1, 'a', 'Or ''in the company.''');
INSERT INTO `mse_bible_footnote` VALUES (7316, 1, 19, 1, 3, 'b', 'Or ''will be.''');
INSERT INTO `mse_bible_footnote` VALUES (7317, 1, 19, 2, 1, 'c', '<I>Leummim</I>, a general word for ''the races of mankind.''');
INSERT INTO `mse_bible_footnote` VALUES (7318, 1, 19, 2, 2, 'd', 'Or ''stand up.''');
INSERT INTO `mse_bible_footnote` VALUES (7319, 1, 19, 2, 4, 'e', '<I>Adonai</I>.');
INSERT INTO `mse_bible_footnote` VALUES (7320, 1, 19, 2, 6, 'f', 'Or ''installed:'' see [scripture id="pr?n=8:23"]Prov. 8:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7321, 1, 19, 2, 8, 'g', '<I>Erets</I>, ''land,'' but the plural ''ends'' seems to involve the meaning ''earth:'' see [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture], [scripture id="1sa?n=2:10"]10[/scripture]; [scripture id="ps?n=72:8"]Ps. 72:8[/scripture]; [scripture id="isa?n=49:6"]Isa. 49:6[/scripture]; [scripture id="ac?n=13:47"]Acts 13:47[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7322, 1, 19, 2, 9, 'h', 'Or ''rod:'' see [scripture id="ge?n=49:10"]Gen. 49:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7323, 1, 19, 2, 10, 'i', 'Or ''consider well.''');
INSERT INTO `mse_bible_footnote` VALUES (7324, 1, 19, 2, 12, 'k', 'Or ''for soon his anger burneth.''');
INSERT INTO `mse_bible_footnote` VALUES (7325, 1, 19, 2, 12, 'l', '''Take refuge,'' as [scripture id="ps?n=61:4"]Ps. 61:4[/scripture]: same root word as ''refuge,'' in [scripture id="ps?n=14:6"]Ps. 14:6[/scripture], &c., and ''trust'' in [scripture id="2sa?n=22:3"]2Sam. 22:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7326, 1, 19, 3, 1, 'm', 'See [scripture id="ps?n=8:2"]Ps. 8:2[/scripture], ''adversaries'' and [footnote id="n_ps?n=8:2"]Note o[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7327, 1, 19, 3, 2, 'a', '''Selah'' is said to mean a pause. Some take it as a direction to the singers to raise their voices. Selah occurs 74 times in the O.T.: 71 in the Psalms, 17 in first book, 30 in second, 20 in third, 4 in fifth; and 3 in Habakkuk.');
INSERT INTO `mse_bible_footnote` VALUES (7328, 1, 19, 4, 1, 'b', 'It is abstract, ''in my calling:'' so [scripture id="ps?n=4:3"]ver. 3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7329, 1, 19, 4, 3, 'c', '<I>Chasid</I>, an adjective used of man and of God; it is translated ''holy,'' ''pious,'' ''saint,'' ''gracious.'' <I>Chesed</I> is the noun meaning ''piety'' and recognition of God on man''s part, and of ''grace'' and ''loving-kindness'' on God''s part: see [scripture id="2ch?n=6:42"]2Chron. 6:42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7330, 1, 19, 4, 4, 'd', 'Or ''tremble.''');
INSERT INTO `mse_bible_footnote` VALUES (7331, 1, 19, 4, 5, 'e', 'As [scripture id="ex?n=23:18"]Ex. 23:18[/scripture]; [scripture id="ps?n=50:14"]Ps. 50:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7332, 1, 19, 4, 5, 'f', '* Implying ''security without anxiety,'' and so elsewhere.');
INSERT INTO `mse_bible_footnote` VALUES (7333, 1, 19, 4, 8, 'g', 'Or ''for thou, Jehovah, makest me <I>although</I> alone <I>yet</I> to dwell,'' &c.: see [scripture id="de?n=33:28"]Deut. 33:28[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7334, 1, 19, 5, 1, 'h', 'Perhaps ''wind-instruments.''');
INSERT INTO `mse_bible_footnote` VALUES (7335, 1, 19, 5, 3, 'i', 'With the sense of ''waiting,'' ''expecting.''');
INSERT INTO `mse_bible_footnote` VALUES (7336, 1, 19, 5, 4, 'k', 'Or ''the evil <I>man</I>.''');
INSERT INTO `mse_bible_footnote` VALUES (7337, 1, 19, 5, 5, 'l', 'Or ''the boastful,'' ''the arrogant,'' as [scripture id="ps?n=73:3"]Ps. 73:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7338, 1, 19, 5, 5, 'm', 'The Hebrew word is used for ''mighty deeds,'' ''achievements,'' and is common in the Psalms: see [scripture id="job?n=31:3"]Job 31:3[/scripture]; [scripture id="job?n=34:8"]34:8[/scripture], [scripture id="job?n=34:22"]22[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7339, 1, 19, 5, 7, 'n', 'Or ''through.''');
INSERT INTO `mse_bible_footnote` VALUES (7340, 1, 19, 5, 7, 'a', '<I>Chesed</I>: see [scripture id="ps?n=4:3"]Ps. 4:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7341, 1, 19, 5, 7, 'b', 'Or ''palace.''');
INSERT INTO `mse_bible_footnote` VALUES (7342, 1, 19, 5, 8, 'c', 'Lit. ''straight.''');
INSERT INTO `mse_bible_footnote` VALUES (7343, 1, 19, 5, 9, 'd', '''Corrupt, depraved desires,'' ''wickedness;'' ''mischievous things,'' [scripture id="ps?n=38:12"]Ps. 38:12[/scripture]: the word is plural.');
INSERT INTO `mse_bible_footnote` VALUES (7344, 1, 19, 5, 9, 'e', 'Lit. ''make smooth.''');
INSERT INTO `mse_bible_footnote` VALUES (7345, 1, 19, 5, 12, 'f', 'Here the larger shield, one which screened the whole body: ''buckler,'' [scripture id="ps?n=35:2"]Ps. 35:2[/scripture]; [scripture id="ps?n=91:4"]91:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7346, 1, 19, 6, 1, 'g', 'Perhaps ''upon the octave,'' or ''with eight strings:'' see [scripture id="ps?n=12"]Ps. 12[/scripture]; [scripture id="1ch?n=15:21"]1Chron. 15:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7347, 1, 19, 6, 5, 'h', 'i.e. ''Hades;'' it vaguely expresses the place or state of the soul separated from the body.');
INSERT INTO `mse_bible_footnote` VALUES (7348, 1, 19, 6, 5, 'i', 'Or ''celebrate thy praises.''');
INSERT INTO `mse_bible_footnote` VALUES (7349, 1, 19, 6, 7, 'k', '<I>Tzar</I>, see [scripture id="ps?n=8:2"]Ps. 8:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7350, 1, 19, 7, 1, 'l', 'According to some, it means ''elegy.''');
INSERT INTO `mse_bible_footnote` VALUES (7351, 1, 19, 7, 4, 'm', 'Or ''or have despoiled him that,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (7352, 1, 19, 7, 5, 'a', 'Or ''cause mine honour to dwell.''');
INSERT INTO `mse_bible_footnote` VALUES (7353, 1, 19, 7, 7, 'b', 'Or ''over them,'' or ''above it,'' i.e. ''the assembly.''');
INSERT INTO `mse_bible_footnote` VALUES (7354, 1, 19, 7, 8, 'c', '<I>Ammim</I>, the peoples in connection with Israel, brought into relationship with Israel.');
INSERT INTO `mse_bible_footnote` VALUES (7355, 1, 19, 7, 8, 'd', 'Or ''be it unto me.''');
INSERT INTO `mse_bible_footnote` VALUES (7356, 1, 19, 7, 11, 'e', 'Or ''God judgeth the righteous.''');
INSERT INTO `mse_bible_footnote` VALUES (7357, 1, 19, 7, 11, 'f', 'Or ''and ~God is angry in every day.''');
INSERT INTO `mse_bible_footnote` VALUES (7358, 1, 19, 7, 12, 'g', 'Or, ''if he,'' i.e. ''the wicked one.''');
INSERT INTO `mse_bible_footnote` VALUES (7359, 1, 19, 7, 12, 'h', 'Or ''adjusted it.''');
INSERT INTO `mse_bible_footnote` VALUES (7360, 1, 19, 7, 13, 'i', 'Or ''pointed at him.''');
INSERT INTO `mse_bible_footnote` VALUES (7361, 1, 19, 7, 17, 'k', 'Or ''thank;'' and so [scripture id="ps?n=9:1"]Ps. 9:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7362, 1, 19, 8, 1, 'l', '* See also [scripture id="ps?n=81"]Ps. 81[/scripture] and [scripture id="ps?n=84"]84[/scripture]. A musical instrument. The word is feminine.');
INSERT INTO `mse_bible_footnote` VALUES (7363, 1, 19, 8, 1, 'm', 'Possibly imperative, it would then read, ''... earth! because of which set thou,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (7364, 1, 19, 8, 2, 'n', 'Or ''founded strength;'' same word as in [scripture id="ps?n=68:34"]Ps. 68:34[/scripture]; [scripture id="ps?n=96:6~a=1"]96:6, 7[/scripture]; [scripture id="ps?n=132:8"]132:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7365, 1, 19, 8, 2, 'o', '<I>Tzar</I>, the ''adversary'' of Christ, or ''oppressor'' of the remnant, from within, as [scripture id="ps?n=3:1"]Ps. 3:1[/scripture]; [scripture id="ps?n=27:2"]27:2[/scripture], [scripture id="ps?n=27:12"]12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7366, 1, 19, 8, 2, 'p', '<I>Oyeb</I>, external ''enemy,'' as [scripture id="ex?n=15:9"]Ex. 15:9[/scripture]; [scripture id="ex?n=23:22"]23:22[/scripture]; [scripture id="nu?n=10:35"]Num. 10:35[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7367, 1, 19, 8, 4, 'q', '<I>Enosh</I>, ''feeble,'' ''mortal man,'' man looked at as a race, in contrast with distinguished individuals: see [scripture id="ge?n=4:26"]Gen. 4:26[/scripture]; [scripture id="ps?n=144:3"]Ps. 144:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7368, 1, 19, 8, 4, 'r', '<I>Adam</I>.');
INSERT INTO `mse_bible_footnote` VALUES (7369, 1, 19, 8, 4, 's', 'Or ''regardest.''');
INSERT INTO `mse_bible_footnote` VALUES (7370, 1, 19, 8, 5, 't', 'Or ''some little.''');
INSERT INTO `mse_bible_footnote` VALUES (7371, 1, 19, 8, 5, 'u', '<I>Elohim</I>: see [scripture id="ps?n=97:7"]Ps. 97:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7372, 1, 19, 9, 1, 'a', 'Perhaps ''the death of <I>his</I> son.''');
INSERT INTO `mse_bible_footnote` VALUES (7373, 1, 19, 9, 5, 'b', 'The word is in the singular, and as such, characteristic; so in [scripture id="ps?n=9:16"]ver. 16[/scripture]: it is plural in [scripture id="ps?n=9:17"]ver. 17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7374, 1, 19, 9, 6, 'c', 'Or ''their memorial hath perished <I>along with</I> them,'' or ''even theirs.''');
INSERT INTO `mse_bible_footnote` VALUES (7375, 1, 19, 9, 8, 'd', '<I>Tebel</I>, the habitable earth: see [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture]; [scripture id="ps?n=19:4"]Ps. 19:4[/scripture]; [scripture id="ps?n=24:1"]24:1[/scripture]; [scripture id="ps?n=33:8"]33:8[/scripture]; [scripture id="ps?n=98:7"]98:7[/scripture], [scripture id="ps?n=98:9"]9[/scripture], &c.; [scripture id="pr?n=8:31"]Prov. 8:31[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7376, 1, 19, 9, 9, 'e', 'Strictly, ''high fortress;'' ''high tower,'' [scripture id="ps?n=18:2"]Ps. 18:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7377, 1, 19, 9, 12, 'f', 'Or ''For he that requireth blood.''');
INSERT INTO `mse_bible_footnote` VALUES (7378, 1, 19, 9, 13, 'g', 'Or ''thou that liftest.''');
INSERT INTO `mse_bible_footnote` VALUES (7379, 1, 19, 9, 16, 'h', 'This may mean, ''to be played on the harp;'' if not ''meditate.'' Same word as [scripture id="ps?n=19:14"]Ps. 19:14[/scripture]; [scripture id="ps?n=92:3"]92:3[/scripture]; [scripture id="la?n=3:62"]Lam. 3:62[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7380, 1, 19, 9, 18, 'i', 'Plural, or ''the afflicted ones;'' ''meek'' and ''afflicted'' are connected in Hebrew.');
INSERT INTO `mse_bible_footnote` VALUES (7381, 1, 19, 9, 19, 'k', '<I>Enosh</I>: see [scripture id="ps?n=8:4"]Ps. 8:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7382, 1, 19, 10, 2, 'a', 'Or ''Through the pride of the wicked <I>man</I>, the afflicted is sore pressed.''');
INSERT INTO `mse_bible_footnote` VALUES (7383, 1, 19, 10, 4, 'b', 'Or ''The wicked in his haughtiness doth not seek <I>God</I>;'' or ''troubleth himself for nothing.''');
INSERT INTO `mse_bible_footnote` VALUES (7384, 1, 19, 10, 5, 'c', 'Lit. ''are rigid.''');
INSERT INTO `mse_bible_footnote` VALUES (7385, 1, 19, 10, 10, 'd', 'An allusion probably to ''fangs <I>of a lion</I>.''');
INSERT INTO `mse_bible_footnote` VALUES (7386, 1, 19, 10, 12, 'e', 'Others, ''the meek,'' as also in [scripture id="ps?n=9:12"]Ps. 9:12[/scripture]: see [scripture id="ps?n=9:18"]9:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7387, 1, 19, 10, 14, 'f', 'Or ''mischief,'' as [scripture id="ps?n=10:7"]ver. 7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7388, 1, 19, 10, 14, 'g', 'Or ''to put it into.''');
INSERT INTO `mse_bible_footnote` VALUES (7389, 1, 19, 10, 14, 'h', 'Or ''it.''');
INSERT INTO `mse_bible_footnote` VALUES (7390, 1, 19, 10, 18, 'i', '<I>Enosh</I>: [scripture id="ps?n=8:4"]Ps. 8:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7391, 1, 19, 11, 1, 'k', 'Or ''Flee, ye birds.''');
INSERT INTO `mse_bible_footnote` VALUES (7392, 1, 19, 11, 4, 'l', 'Or ''palace;'' and so [scripture id="ps?n=18:6"]Ps. 18:6[/scripture]; [scripture id="ps?n=45:8"]45:8[/scripture], [scripture id="ps?n=45:15"]15[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (7393, 1, 19, 11, 5, 'a', 'See [footnote id="n_ps?n=9:5"]Note, Ps. 9:5[/footnote]: here the singular.');
INSERT INTO `mse_bible_footnote` VALUES (7394, 1, 19, 11, 6, 'b', 'Or ''snares: fire and brimstone and a scorching wind shall be.''');
INSERT INTO `mse_bible_footnote` VALUES (7395, 1, 19, 11, 7, 'c', 'Lit. ''righteousnesses:'' see [footnote id="n_re?n=19:8"]Note, Rev. 19:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7396, 1, 19, 11, 7, 'd', 'Or ''the upright shall see his face.''');
INSERT INTO `mse_bible_footnote` VALUES (7397, 1, 19, 12, 1, 'e', 'See [scripture id="ps?n=6~a=9"]Ps. 6[/scripture], title.');
INSERT INTO `mse_bible_footnote` VALUES (7398, 1, 19, 12, 1, 'f', 'Or ''ceaseth.''');
INSERT INTO `mse_bible_footnote` VALUES (7399, 1, 19, 12, 2, 'g', 'Or ''vanity.''');
INSERT INTO `mse_bible_footnote` VALUES (7400, 1, 19, 12, 2, 'h', 'Lit. ''a heart and a heart.''');
INSERT INTO `mse_bible_footnote` VALUES (7401, 1, 19, 12, 3, 'i', 'Lit. ''great.''');
INSERT INTO `mse_bible_footnote` VALUES (7402, 1, 19, 12, 5, 'k', 'Lit. ''one puffeth:'' see [scripture id="ps?n=10:5"]Ps. 10:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7403, 1, 19, 12, 6, 'l', '<I>Imrah</I>: see [scripture id="ps?n=119:11"]Ps. 119:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7404, 1, 19, 12, 7, 'm', 'Or ''him:'' see end of [scripture id="ps?n=12:5"]ver. 5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7405, 1, 19, 13, 5, 'n', '<I>Chesed</I>: see [scripture id="ps?n=4:3"]Ps. 4:3[/scripture] and [scripture id="2ch?n=6:42"]2Chron. 6:42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7406, 1, 19, 14, 1, 'o', 'With this Psalm compare [scripture id="ps?n=53"]Ps. 53[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7407, 1, 19, 14, 1, 'p', '<I>Nabal</I>: see [scripture id="ps?n=53:1"]Ps. 53:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7408, 1, 19, 14, 7, 'a', 'See [scripture id="ps?n=126:1"]Ps. 126:1[/scripture] and [footnote id="n_ps?n=126:1"]Note[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7409, 1, 19, 15, 5, 'b', 'Or ''a bribe.''');
INSERT INTO `mse_bible_footnote` VALUES (7410, 1, 19, 16, 1, 'c', 'Meaning doubtful. According to some, ''a golden jewel.'' See [scripture id="ps?n=56:1~a=67"]Ps. 56-60[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7411, 1, 19, 16, 3, 'd', 'The preposition is the same as before ''Jehovah'' in [scripture id="ps?n=16:2"]ver. 2[/scripture]: but ''to'' before ''thee,'' at the end of [scripture id="ps?n=16:2"]ver. 2[/scripture], is different.');
INSERT INTO `mse_bible_footnote` VALUES (7412, 1, 19, 16, 3, 'e', '<I>Kadoshim</I>: plural of <I>Kadosh</I>, which is used of God in [scripture id="ps?n=22:3"]Ps. 22:3[/scripture]; [scripture id="ps?n=71:22"]71:22[/scripture]; [scripture id="ps?n=78:41"]78:41[/scripture]; [scripture id="ps?n=89:18"]89:18[/scripture]; [scripture id="ps?n=99:3"]99:3[/scripture], [scripture id="ps?n=99:5"]5[/scripture]; [scripture id="hab?n=1:12"]Hab. 1:12[/scripture], &c., and is translated ''holy'' -- ''holy one.'' It is used of ''Aaron,'' [scripture id="ps?n=106:16"]Ps. 106:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7413, 1, 19, 16, 5, 'f', 'Or ''mine assigned portion.''');
INSERT INTO `mse_bible_footnote` VALUES (7414, 1, 19, 16, 6, 'g', 'Or ''portions:'' see [scripture id="jos?n=17:5"]Josh. 17:5[/scripture], [scripture id="jos?n=17:14"]14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7415, 1, 19, 16, 6, 'h', 'See [scripture id="mic?n=2:5"]Mic. 2:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7416, 1, 19, 16, 9, 'i', 'Or ''honour.'' In [scripture id="ac?n=2:26"]Acts 2:26[/scripture], ''tongue,'' from the LXX. But see [scripture id="ge?n=49:6"]Gen. 49:6[/scripture]. It may be taken as a synonym of ''soul:'' see [scripture id="ps?n=7:5"]Ps. 7:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7417, 1, 19, 16, 9, 'k', 'Or ''shall rest in safety,'' as [scripture id="ps?n=4:8"]Ps. 4:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7418, 1, 19, 16, 10, 'l', 'See [footnote id="n_ps?n=6:5"]Notes to Ps. 6:5[/footnote] and [footnote id="n_mt?n=11:23"]Matt. 11:23[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7419, 1, 19, 16, 10, 'm', 'Or ''gracious one,'' <I>chasid</I>: see [footnote id="n_ps?n=4:3"]Notes to Ps. 4:3[/footnote]; [footnote id="n_ps?n=30:4"]30:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7420, 1, 19, 17, 1, 'a', 'Strictly, ''righteousness''');
INSERT INTO `mse_bible_footnote` VALUES (7421, 1, 19, 17, 2, 'b', 'Or ''right.''');
INSERT INTO `mse_bible_footnote` VALUES (7422, 1, 19, 17, 4, 'c', 'Or ''I have paid attention to.''');
INSERT INTO `mse_bible_footnote` VALUES (7423, 1, 19, 17, 5, 'd', 'Or ''Hold my goings in thy paths, that my ...''');
INSERT INTO `mse_bible_footnote` VALUES (7424, 1, 19, 17, 6, 'e', '<I>Imrah</I>: see [scripture id="ps?n=119:11"]Ps. 119:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7425, 1, 19, 17, 7, 'f', '<I>Chesed</I>: see [scripture id="2ch?n=6:42"]2Chron. 6:42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7426, 1, 19, 17, 8, 'g', 'See [scripture id="de?n=32:10"]Deut. 32:10[/scripture]; [scripture id="pr?n=7:2"]Prov. 7:2[/scripture], [scripture id="pr?n=7:9"]9[/scripture]; [scripture id="pr?n=20:20"]20:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7427, 1, 19, 17, 11, 'h', 'Or ''me.''');
INSERT INTO `mse_bible_footnote` VALUES (7428, 1, 19, 17, 11, 'i', 'Or ''they set their eyes to cast <I>us</I> down to the earth.''');
INSERT INTO `mse_bible_footnote` VALUES (7429, 1, 19, 17, 13, 'k', 'Or ''<I>by</I> thy sword.''');
INSERT INTO `mse_bible_footnote` VALUES (7430, 1, 19, 17, 14, 'l', 'Or ''<I>by</I>.''');
INSERT INTO `mse_bible_footnote` VALUES (7431, 1, 19, 17, 14, 'm', '<I>Cheled</I> or ''world:'' so [scripture id="ps?n=49:1"]Ps. 49:1[/scripture]; ''life,'' [scripture id="ps?n=89:47"]Ps. 89:47[/scripture] and [scripture id="job?n=11:17"]Job 11:17[/scripture]; also ''lifetime,'' [scripture id="ps?n=39:5"]Ps. 39:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7432, 1, 19, 17, 14, 'n', 'Or <I>their</I> sons are surfeited.''');
INSERT INTO `mse_bible_footnote` VALUES (7433, 1, 19, 17, 15, 'o', 'Or ''as.''');
INSERT INTO `mse_bible_footnote` VALUES (7434, 1, 19, 18, 1, 'p', 'As [scripture id="ps?n=8:2"]8:2[/scripture]; so [scripture id="ps?n=18:3"]vers. 3[/scripture], [scripture id="ps?n=18:17"]17[/scripture], [scripture id="ps?n=18:37"]37[/scripture], [scripture id="ps?n=18:40"]40[/scripture], [scripture id="ps?n=18:48"]48[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7435, 1, 19, 18, 1, 'q', 'Compare [scripture id="2sa?n=22"]2Sam. 22[/scripture] throughout, and Notes there.');
INSERT INTO `mse_bible_footnote` VALUES (7436, 1, 19, 18, 2, 'r', '<I>Sela</I>, ''a high rock,'' see [scripture id="isa?n=16:1"]Isa. 16:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7437, 1, 19, 18, 14, 'a', 'Lit. ''and scattered them.''');
INSERT INTO `mse_bible_footnote` VALUES (7438, 1, 19, 18, 14, 'b', 'Or ''and many lightnings.''');
INSERT INTO `mse_bible_footnote` VALUES (7439, 1, 19, 18, 22, 'c', 'Or ''judgments.''');
INSERT INTO `mse_bible_footnote` VALUES (7440, 1, 19, 18, 23, 'd', 'Or ''perfect:'' as [scripture id="ge?n=6:9"]Gen. 6:9[/scripture]; [scripture id="job?n=1:1"]Job 1:1[/scripture]; [scripture id="job?n=4:6"]4:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7441, 1, 19, 18, 25, 'e', '<I>Chasid</I>: see [footnote id="n_2sa?n=22:26"]Notes, 2Sam. 22:26[/footnote]; [footnote id="n_2ch?n=6:42"]2Chron. 6:42[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7442, 1, 19, 18, 25, 'f', '<I>Chesed</I>: see [scripture id="2ch?n=6:42"]2Chron. 6:42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7443, 1, 19, 18, 27, 'g', 'Or ''wilt save.''');
INSERT INTO `mse_bible_footnote` VALUES (7444, 1, 19, 18, 30, 'a', '<I>Imrah</I>: see [scripture id="ps?n=119:11"]Ps. 119:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7445, 1, 19, 18, 30, 'b', 'Or ''refined:'' see [scripture id="ps?n=12:6"]Ps. 12:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7446, 1, 19, 18, 34, 'c', 'Others read ''break.''');
INSERT INTO `mse_bible_footnote` VALUES (7447, 1, 19, 18, 45, 'd', 'Or ''fortified:'' see [scripture id="mic?n=7:17"]Mic. 7:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7448, 1, 19, 18, 49, 'e', 'Or ''celebrate thy Praises.''');
INSERT INTO `mse_bible_footnote` VALUES (7449, 1, 19, 18, 50, 'f', '<I>Chesed</I>: see [scripture id="2ch?n=6:42"]2Chron. 6:42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7450, 1, 19, 19, 2, 'g', 'Lit. ''causeth to boil forth,'' ''gush,'' as of a stream, as [scripture id="ps?n=145:7"]Ps. 145:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7451, 1, 19, 19, 4, 'h', 'That is, the ''extent'' of their testimony.');
INSERT INTO `mse_bible_footnote` VALUES (7452, 1, 19, 19, 6, 'a', 'Or ''starting.''');
INSERT INTO `mse_bible_footnote` VALUES (7453, 1, 19, 19, 11, 'b', 'Or ''warned.''');
INSERT INTO `mse_bible_footnote` VALUES (7454, 1, 19, 19, 12, 'c', 'Or ''Clear.''');
INSERT INTO `mse_bible_footnote` VALUES (7455, 1, 19, 19, 13, 'd', 'Or ''from the presumptuous.''');
INSERT INTO `mse_bible_footnote` VALUES (7456, 1, 19, 19, 13, 'e', 'Or ''from the great transgression.''');
INSERT INTO `mse_bible_footnote` VALUES (7457, 1, 19, 20, 1, 'f', 'Or ''set thee up on high.''');
INSERT INTO `mse_bible_footnote` VALUES (7458, 1, 19, 20, 3, 'g', 'Or ''turn to ashes;'' or ''let thy burnt-offering be fat to him.''');
INSERT INTO `mse_bible_footnote` VALUES (7459, 1, 19, 20, 9, 'h', 'Or ''Let Jehovah save the king. Let him answer,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (7460, 1, 19, 21, 11, 'a', 'Lit. ''spread out,'' as ''extend,'' [scripture id="isa?n=66:12"]Isa. 66:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7461, 1, 19, 22, 1, 'b', 'That is, ''According to the hind of the morning.'' <I>Aijeleth</I> is feminine.');
INSERT INTO `mse_bible_footnote` VALUES (7462, 1, 19, 22, 8, 'c', 'Lit. ''Roll it <I>or ''himself''</I> on.''');
INSERT INTO `mse_bible_footnote` VALUES (7463, 1, 19, 22, 12, 'd', 'Or ''Great.''');
INSERT INTO `mse_bible_footnote` VALUES (7464, 1, 19, 22, 17, 'e', 'Or ''I count.''');
INSERT INTO `mse_bible_footnote` VALUES (7465, 1, 19, 22, 20, 'a', 'Lit. ''the hand.''');
INSERT INTO `mse_bible_footnote` VALUES (7466, 1, 19, 22, 21, 'b', 'Or ''Answer me.''');
INSERT INTO `mse_bible_footnote` VALUES (7467, 1, 19, 22, 30, 'c', 'Or ''it shall be told concerning the Lord to the <I>coming</I> generation:'' but see [scripture id="ps?n=87:6"]Ps. 87:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7468, 1, 19, 23, 3, 'd', 'Or ''reviveth.''');
INSERT INTO `mse_bible_footnote` VALUES (7469, 1, 19, 23, 5, 'e', 'Or ''furnishest.''');
INSERT INTO `mse_bible_footnote` VALUES (7470, 1, 19, 23, 6, 'f', 'Or ''Only.''');
INSERT INTO `mse_bible_footnote` VALUES (7471, 1, 19, 24, 1, 'g', '<I>Tebel</I>, ''the habitable earth:'' see [scripture id="ps?n=9:8"]Ps. 9:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7472, 1, 19, 24, 4, 'a', 'Or ''innocent.''');
INSERT INTO `mse_bible_footnote` VALUES (7473, 1, 19, 24, 6, 'b', 'Or ''thy face <I>in</I> Jacob.''');
INSERT INTO `mse_bible_footnote` VALUES (7474, 1, 19, 25, 1, 'c', 'An acrostic Psalm; the verses begin with the Hebrew letters in alphabetical succession, as [scripture id="ps?n=34"]Ps. 34[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7475, 1, 19, 25, 5, 'd', 'Or ''every day.''');
INSERT INTO `mse_bible_footnote` VALUES (7476, 1, 19, 25, 7, 'e', 'See [footnote id="n_job?n=7:21"]Note to Job 7:21[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7477, 1, 19, 25, 12, 'f', 'Or ''that he <I>Jehovah</I> chooseth.''');
INSERT INTO `mse_bible_footnote` VALUES (7478, 1, 19, 25, 13, 'g', 'Or ''possess the land,'' as [scripture id="ps?n=37:9"]Ps. 37:9[/scripture], [scripture id="ps?n=37:29"]29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7479, 1, 19, 25, 14, 'h', 'i.e. ''the intimate (or private) communications.''');
INSERT INTO `mse_bible_footnote` VALUES (7480, 1, 19, 25, 14, 'i', 'Or ''and his covenant, -- to make them know <I>it</I>.''');
INSERT INTO `mse_bible_footnote` VALUES (7481, 1, 19, 25, 17, 'k', 'Some read ''Enlarge my heart in its troubles, and bring ...'' Others read ''The troubles of my heart relieve thou, and bring ...''');
INSERT INTO `mse_bible_footnote` VALUES (7482, 1, 19, 25, 21, 'a', 'Or ''Integrity (perfection) and uprightness shall.''');
INSERT INTO `mse_bible_footnote` VALUES (7483, 1, 19, 26, 1, 'b', 'Or ''totter,'' ''waver.''');
INSERT INTO `mse_bible_footnote` VALUES (7484, 1, 19, 26, 4, 'c', 'Or ''false.''');
INSERT INTO `mse_bible_footnote` VALUES (7485, 1, 19, 26, 5, 'd', 'Or ''I will not sit.''');
INSERT INTO `mse_bible_footnote` VALUES (7486, 1, 19, 27, 1, 'e', '''Fear'' and ''be afraid'' are nearly the same, but it seems that the latter is more ''terror,'' even used of Jehovah: see [scripture id="1sa?n=11:7"]1Sam. 11:7[/scripture]; [scripture id="ps?n=36:1"]Ps. 36:1[/scripture]; ''fear'' more in a godly sense, as [scripture id="pr?n=1:7"]Prov. 1:7[/scripture], yet [scripture id="ps?n=27:3"]ver. 3[/scripture] here does mean simple ''fear.''');
INSERT INTO `mse_bible_footnote` VALUES (7487, 1, 19, 27, 2, 'f', 'See Notes, [scripture id="ps?n=8:2"]Ps. 8:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7488, 1, 19, 27, 4, 'g', 'Or ''graciousness,'' and so in [scripture id="ps?n=90:17"]Ps. 90:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7489, 1, 19, 27, 10, 'a', 'Or ''For my father and my mother have forsaken me, but Jehovah hath gathered me.''');
INSERT INTO `mse_bible_footnote` VALUES (7490, 1, 19, 27, 11, 'b', 'Or ''those that lie in wait for me.''');
INSERT INTO `mse_bible_footnote` VALUES (7491, 1, 19, 27, 14, 'c', 'Or ''he will strengthen thy heart.''');
INSERT INTO `mse_bible_footnote` VALUES (7492, 1, 19, 28, 5, 'd', 'Or ''Because they regard not ... his hands, he will destroy,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (7493, 1, 19, 28, 8, 'e', 'Lit. ''salvations,'' ''deliverances:'' see [scripture id="ps?n=18:50"]Ps. 18:50[/scripture]; [scripture id="ps?n=42:5"]42:5[/scripture], [scripture id="ps?n=42:11"]11[/scripture]; [scripture id="ps?n=74:12"]74:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7494, 1, 19, 28, 9, 'f', 'Or ''bear:'' see [scripture id="de?n=1:31"]Deut. 1:31[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7495, 1, 19, 29, 1, 'g', '''Give'' (3 times), same as ''Ascribe,'' [scripture id="de?n=32:3"]Deut. 32:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7496, 1, 19, 29, 6, 'h', 'Hermon in [scripture id="de?n=3:9"]Deut. 3:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7497, 1, 19, 29, 9, 'a', 'Or perhaps, ''everything saith;'' the Hebrew is the same as ''every one'' in [scripture id="isa?n=1:23"]Isa. 1:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7498, 1, 19, 29, 10, 'b', 'The word used in [scripture id="ge?n=6:17"]Gen. 6:17[/scripture] of the deluge.');
INSERT INTO `mse_bible_footnote` VALUES (7499, 1, 19, 30, 1, 'c', 'Strictly, ''drawn me up,'' as out of a well.');
INSERT INTO `mse_bible_footnote` VALUES (7500, 1, 19, 30, 4, 'd', '<I>Chasid</I>: those characterized by piety, grace, goodness; see [scripture id="ps?n=16:10"]Ps. 16:10[/scripture], and [footnote id="n_2ch?n=6:42"]Note on 2Chron. 6:42[/footnote]. ''Saints,'' [scripture id="ps?n=16:3"]Ps. 16:3[/scripture], is <I>Kadoshim</I>, ''holy ones.''');
INSERT INTO `mse_bible_footnote` VALUES (7501, 1, 19, 30, 4, 'e', 'Or ''celebrate his holy (<I>Kodesh</I>) memorial,'' i.e. his holy name: see [scripture id="ex?n=3:15"]Ex. 3:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7502, 1, 19, 30, 8, 'f', 'Many read ''Jehovah.''');
INSERT INTO `mse_bible_footnote` VALUES (7503, 1, 19, 30, 9, 'g', 'Or ''give thanks to;'' and so [scripture id="ps?n=30:12"]ver. 12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7504, 1, 19, 30, 12, 'h', 'See [scripture id="ps?n=7:5"]Ps. 7:5[/scripture]; [scripture id="ps?n=16:9"]16:9[/scripture], and [footnote id="n_ps?n=16:9"]Note[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7505, 1, 19, 31, 3, 'i', '<I>Sela</I>, as in [scripture id="ps?n=18:2"]Ps. 18:2[/scripture], first time.');
