INSERT INTO `mse_bible_footnote` VALUES (7550, 1, 19, 37, 23, 'c', '<I>Geber</I>, as [scripture id="ps?n=34:8"]Ps. 34:8[/scripture]: see [scripture id="job?n=3:3"]Job 3:3[/scripture], and cf. [scripture id="job?n=4:17"]Job 4:17[/scripture].');
