INSERT INTO `mse_bible_footnote` VALUES (7556, 1, 19, 38, 1, 'b', '''Hot displeasure,'' (''fury,'' [scripture id="ps?n=88:7"]Ps. 88:7[/scripture]), would be rather stronger. Both words ([footnote id="n_ps?n=38:1"]Notes a[/footnote] and b) are of discipline.');
INSERT INTO `mse_bible_footnote` VALUES (7557, 1, 19, 38, 2, 'c', 'Lit. ''have descended into me.''');
