INSERT INTO `mse_bible_footnote` VALUES (7559, 1, 19, 38, 4, 'e', 'i.e. as waters.');
INSERT INTO `mse_bible_footnote` VALUES (7560, 1, 19, 38, 5, 'f', 'Or ''they run.''');
INSERT INTO `mse_bible_footnote` VALUES (7561, 1, 19, 38, 7, 'g', 'Or ''a loathsome <I>plague</I>.''');
