INSERT INTO `mse_bible_footnote` VALUES (7762, 1, 19, 71, 6, 'd', 'Or ''art my benefactor.''');
INSERT INTO `mse_bible_footnote` VALUES (7763, 1, 19, 71, 8, 'e', 'Or ''Let my mouth.''');
INSERT INTO `mse_bible_footnote` VALUES (7764, 1, 19, 71, 13, 'f', 'Heb. <I>Satan</I>: see [scripture id="ps?n=38:20"]Ps. 38:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7765, 1, 19, 71, 16, 'g', 'Or ''make mention of.''');
INSERT INTO `mse_bible_footnote` VALUES (7766, 1, 19, 71, 19, 'h', 'Or ''is very high.''');
INSERT INTO `mse_bible_footnote` VALUES (7767, 1, 19, 71, 20, 'a', 'Or ''me.''');
INSERT INTO `mse_bible_footnote` VALUES (7768, 1, 19, 71, 21, 'b', 'Or ''and thou wilt turn and comfort me.''');
INSERT INTO `mse_bible_footnote` VALUES (7769, 1, 19, 71, 22, 'c', 'Or ''give thee thanks.''');
INSERT INTO `mse_bible_footnote` VALUES (7770, 1, 19, 71, 22, 'd', 'Lit. ''an instrument, a lute.''');
INSERT INTO `mse_bible_footnote` VALUES (7771, 1, 19, 71, 23, 'e', 'Or ''exult; for I will.''');
INSERT INTO `mse_bible_footnote` VALUES (7772, 1, 19, 72, 1, 'f', '''Of,'' or ''Concerning.''');
INSERT INTO `mse_bible_footnote` VALUES (7773, 1, 19, 72, 2, 'g', 'Or ''rectitude.''');
INSERT INTO `mse_bible_footnote` VALUES (7774, 1, 19, 72, 5, 'h', 'Lit. ''with the sun and before the moon:'' see ''as long as the sun,'' lit. ''before the sun,'' [scripture id="ps?n=72:17"]ver. 17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7775, 1, 19, 72, 5, 'i', 'Lit. ''generation of generations.''');
INSERT INTO `mse_bible_footnote` VALUES (7776, 1, 19, 72, 6, 'k', 'Or ''land,'' see [scripture id="ps?n=2:8"]Ps. 2:8[/scripture]; [scripture id="ps?n=58:2"]58:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7777, 1, 19, 72, 10, 'l', 'Or ''shall present gifts.''');
INSERT INTO `mse_bible_footnote` VALUES (7778, 1, 19, 72, 12, 'm', 'Or ''and him that hath.''');
INSERT INTO `mse_bible_footnote` VALUES (7779, 1, 19, 72, 16, 'n', 'Or ''handfuls.''');
INSERT INTO `mse_bible_footnote` VALUES (7780, 1, 19, 73, 4, 'a', 'Some read ''until.''');
INSERT INTO `mse_bible_footnote` VALUES (7781, 1, 19, 73, 4, 'b', 'Or ''strength.''');
INSERT INTO `mse_bible_footnote` VALUES (7782, 1, 19, 73, 5, 'c', '<I>Enosh</I>: [scripture id="ps?n=8:4"]Ps. 8:4[/scripture]');
INSERT INTO `mse_bible_footnote` VALUES (7783, 1, 19, 73, 10, 'd', 'Or ''Therefore he turns his people.''');
INSERT INTO `mse_bible_footnote` VALUES (7784, 1, 19, 73, 12, 'e', 'Or ''and they are ever at rest'' (i.e. careless): see [scripture id="eze?n=23:42"]Ezek. 23:42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7785, 1, 19, 73, 16, 'f', '''Labour,'' ''trouble:'' ''hardships,'' [scripture id="ps?n=73:5"]ver. 5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7786, 1, 19, 73, 17, 'g', 'Or ''<I>and</I> I had considered.''');
INSERT INTO `mse_bible_footnote` VALUES (7787, 1, 19, 73, 20, 'h', 'Or ''in wrath.''');
INSERT INTO `mse_bible_footnote` VALUES (7788, 1, 19, 73, 23, 'i', 'Properly ''seized.''');
INSERT INTO `mse_bible_footnote` VALUES (7789, 1, 19, 73, 28, 'a', '''Things made,'' ''wrought,'' as [scripture id="ge?n=2:2"]Gen. 2:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7790, 1, 19, 74, 2, 'b', 'As [scripture id="ps?n=69:18"]Ps. 69:18[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (7791, 1, 19, 74, 2, 'c', 'Lit. ''staff <I>for measuring</I>;'' or ''tribe.''');
INSERT INTO `mse_bible_footnote` VALUES (7792, 1, 19, 74, 4, 'd', '<I>Tzar</I>, [scripture id="ps?n=8:2"]Ps. 8:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7793, 1, 19, 74, 4, 'e', 'See [scripture id="ex?n=4:17"]Ex. 4:17[/scripture]; [scripture id="ex?n=10:2"]10:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7794, 1, 19, 74, 7, 'f', '<I>Mishkan</I>, used for ''tabernacle,'' [scripture id="ex?n=25:9"]Ex. 25:9[/scripture], &c.; [scripture id="2ch?n=29:6"]2Chron. 29:6[/scripture], [scripture id="ps?n=78:60"]Ps. 78:60[/scripture]. Also ''dwelleth,'' [scripture id="ps?n=26:8"]Ps. 26:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7795, 1, 19, 74, 8, 'g', 'Or ''oppress.''');
INSERT INTO `mse_bible_footnote` VALUES (7796, 1, 19, 74, 14, 'h', 'As [scripture id="job?n=3:8"]Job 3:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7797, 1, 19, 74, 14, 'i', 'i.e. the wild beasts.');
INSERT INTO `mse_bible_footnote` VALUES (7798, 1, 19, 74, 16, 'k', 'Lit. ''light-bearer,'' i.e. what gives light, as [scripture id="ge?n=1:14~a=2"]Gen. 1:14-16[/scripture]; [scripture id="ex?n=25:6"]Ex. 25:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7799, 1, 19, 74, 18, 'l', 'Or ''vile,'' ''impious:'' see [scripture id="ps?n=74:22"]ver. 22[/scripture] and [scripture id="ps?n=53:1"]Ps. 53:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7800, 1, 19, 74, 19, 'm', 'Or ''Give not up thy turtle-dove unto the greedy troop'' (or ''herd'').');
INSERT INTO `mse_bible_footnote` VALUES (7801, 1, 19, 74, 19, 'n', 'Others, ''life,'' as ''flock,'' [scripture id="ps?n=68:10"]Ps. 68:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7802, 1, 19, 75, 2, 'a', 'Or ''When I shall reach (seize) the set time:'' see [scripture id="le?n=23:2"]Lev. 23:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7803, 1, 19, 75, 3, 'b', 'Or ''land,'' as [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture]: so [scripture id="ps?n=76:8"]ver. 8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7804, 1, 19, 75, 6, 'c', 'Lit. ''the wilderness,'' which was in the south.');
INSERT INTO `mse_bible_footnote` VALUES (7805, 1, 19, 75, 8, 'd', 'Or ''it is red.''');
INSERT INTO `mse_bible_footnote` VALUES (7806, 1, 19, 76, 2, 'e', 'Lit. ''booth.''');
INSERT INTO `mse_bible_footnote` VALUES (7807, 1, 19, 76, 10, 'f', 'Or ''restrain.''');
INSERT INTO `mse_bible_footnote` VALUES (7808, 1, 19, 77, 1, 'a', 'As [scripture id="ps?n=39"]Ps. 39[/scripture] and [scripture id="ps?n=62"]62[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7809, 1, 19, 77, 2, 'b', 'Or ''was poured out,'' or, ''my sore ran.''');
INSERT INTO `mse_bible_footnote` VALUES (7810, 1, 19, 77, 8, 'c', '<I>Omer</I> see [scripture id="ps?n=119:11"]Ps. 119:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7811, 1, 19, 77, 11, 'd', 'Strictly, ''doings,'' ''exploits,'' [scripture id="pr?n=20:11"]Prov. 20:11[/scripture]; [scripture id="mic?n=2:7"]Mic. 2:7[/scripture]; ''doings'' in [scripture id="ps?n=77:12"]ver. 12[/scripture] and [scripture id="ps?n=78:11"]Ps. 78:11[/scripture] is rather ''actions:'' ''work'' in [scripture id="ps?n=77:12"]ver. 12[/scripture] is a ''thing done,'' as [scripture id="isa?n=45:9"]Isa. 45:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7812, 1, 19, 77, 20, 'e', 'See [scripture id="mic?n=6:4"]Mic. 6:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7813, 1, 19, 78, 2, 'f', 'As [scripture id="ps?n=19:2"]Ps. 19:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7814, 1, 19, 78, 7, 'a', 'As [scripture id="ps?n=77:11"]Ps. 77:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7815, 1, 19, 78, 12, 'b', 'i.e. Tanis, a town in Lower Egypt.');
INSERT INTO `mse_bible_footnote` VALUES (7816, 1, 19, 78, 16, 'c', '<I>Sela</I>, as [scripture id="ps?n=31:3"]Ps. 31:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7817, 1, 19, 78, 17, 'd', 'Or ''rebelling against;'' and so [scripture id="ps?n=78:40"]ver. 40[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7818, 1, 19, 78, 19, 'e', 'Or ''furnish,'' as [scripture id="pr?n=9:2"]Prov. 9:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7819, 1, 19, 78, 32, 'f', 'Or ''by.''');
INSERT INTO `mse_bible_footnote` VALUES (7820, 1, 19, 78, 35, 'a', 'As [scripture id="ps?n=69:18"]Ps. 69:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7821, 1, 19, 78, 36, 'b', 'The word means, ''make pretence,'' elsewhere ''entice,'' ''deceive.''');
INSERT INTO `mse_bible_footnote` VALUES (7822, 1, 19, 78, 40, 'c', 'Or ''rebel against.''');
INSERT INTO `mse_bible_footnote` VALUES (7823, 1, 19, 78, 41, 'd', 'Or ''limited.''');
INSERT INTO `mse_bible_footnote` VALUES (7824, 1, 19, 78, 42, 'e', 'Lit. ''redeemed.''');
INSERT INTO `mse_bible_footnote` VALUES (7825, 1, 19, 78, 45, 'f', 'Or ''vermin;'' but it is as in [scripture id="ex?n=8:21"]Ex. 8:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7826, 1, 19, 78, 46, 'g', 'Lit. ''the devourer,'' a species of locust: so [scripture id="1ki?n=8:37"]1Kings 8:37[/scripture]; [scripture id="2ch?n=6:28"]2Chron. 6:28[/scripture]: see [scripture id="joe?n=1:4"]Joel 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7827, 1, 19, 78, 61, 'h', 'Lit. ''beauty.''');
INSERT INTO `mse_bible_footnote` VALUES (7828, 1, 19, 78, 63, 'a', 'Or ''chosen,'' as [scripture id="ps?n=78:31"]ver. 31[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7829, 1, 19, 78, 64, 'b', 'Or ''wept not.''');
INSERT INTO `mse_bible_footnote` VALUES (7830, 1, 19, 79, 2, 'c', 'As [scripture id="ps?n=30:4"]Ps. 30:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7831, 1, 19, 79, 11, 'd', 'Lit. ''the sons of death.''');
INSERT INTO `mse_bible_footnote` VALUES (7832, 1, 19, 80, 1, 'a', 'Or ''On lilies. A testimony of Asaph: a Psalm.''');
INSERT INTO `mse_bible_footnote` VALUES (7833, 1, 19, 80, 1, 'b', 'Or ''dwellest above the cherubim,'' so [scripture id="ps?n=99:1"]Ps. 99:1[/scripture]; but there the article is omitted before ''cherubim.''');
INSERT INTO `mse_bible_footnote` VALUES (7834, 1, 19, 80, 4, 'c', 'Lit. ''wilt thou.''');
INSERT INTO `mse_bible_footnote` VALUES (7835, 1, 19, 80, 10, 'd', 'Or ''great cedars:'' see [scripture id="ps?n=36:6"]Ps. 36:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7836, 1, 19, 80, 12, 'e', 'Or ''enclosure-walls:'' see [scripture id="ec?n=10:8"]Eccles. 10:8[/scripture]; [scripture id="eze?n=22:30"]Ezek. 22:30[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7837, 1, 19, 80, 13, 'f', 'Or ''roaming creature,'' as [scripture id="ps?n=50:11"]Ps. 50:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7838, 1, 19, 80, 15, 'g', 'Others, ''And protect that which.''');
INSERT INTO `mse_bible_footnote` VALUES (7839, 1, 19, 80, 15, 'h', 'Lit. ''the son.''');
INSERT INTO `mse_bible_footnote` VALUES (7840, 1, 19, 80, 17, 'i', 'Or ''Adam''s son,'' as [scripture id="ps?n=8:4"]Ps. 8:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7841, 1, 19, 81, 1, 'k', 'See [scripture id="ps?n=8"]Ps. 8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7842, 1, 19, 81, 3, 'l', 'Others, ''at the full moon,'' as [scripture id="pr?n=7:20"]Prov. 7:20[/scripture]; but it more probably refers to special appointment or ''reckoning,'' according to the word in the original.');
INSERT INTO `mse_bible_footnote` VALUES (7843, 1, 19, 81, 16, 'a', 'Lit. ''the fat of wheat,'' and so [scripture id="ps?n=147:14"]Ps. 147:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7844, 1, 19, 82, 1, 'b', '<I>Elohim</I>, ''the judges:'' see [scripture id="ex?n=21:6"]Ex. 21:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7845, 1, 19, 82, 3, 'c', 'That is, as often, ''Judge in favour of.''');
INSERT INTO `mse_bible_footnote` VALUES (7846, 1, 19, 82, 5, 'd', '<I>Erets</I>, [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7847, 1, 19, 82, 7, 'e', '<I>Adam</I>.');
INSERT INTO `mse_bible_footnote` VALUES (7848, 1, 19, 83, 3, 'f', 'See [scripture id="ps?n=31:20"]Ps. 31:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7849, 1, 19, 83, 8, 'g', 'Assyria.');
INSERT INTO `mse_bible_footnote` VALUES (7850, 1, 19, 83, 11, 'a', 'Lit. ''anointed ones:'' see [scripture id="eze?n=32:30"]Ezek. 32:30[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7851, 1, 19, 83, 18, 'b', 'Or ''who alone hast the name Jehovah.''');
INSERT INTO `mse_bible_footnote` VALUES (7852, 1, 19, 83, 18, 'c', 'Heb. <I>Elyon</I>: see [scripture id="ge?n=14:19"]Gen. 14:19[/scripture], and note the omission here of ''possessor of heavens,'' cf. [scripture id="de?n=32:8"]Deut. 32:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7853, 1, 19, 84, 1, 'd', 'See [scripture id="ps?n=8"]Ps. 8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7854, 1, 19, 84, 6, 'e', 'Or ''of weeping.''');
INSERT INTO `mse_bible_footnote` VALUES (7855, 1, 19, 84, 6, 'f', 'See [scripture id="de?n=11:14"]Deut. 11:14[/scripture]. The autumn rain: the <I>first</I> with reference to the time of sowing.');
INSERT INTO `mse_bible_footnote` VALUES (7856, 1, 19, 85, 8, 'a', 'Or ''saints'' (<I>chasid</I>): see [footnote id="n_ps?n=86:2"]Note c[/footnote] and [scripture id="2ch?n=6:41~a=1"]2Chron. 6:41, 42[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7857, 1, 19, 85, 13, 'b', 'Or ''shall set <I>itself</I> in the way of his steps.''');
INSERT INTO `mse_bible_footnote` VALUES (7858, 1, 19, 86, 2, 'c', 'Or ''pious,'' ''holy,'' <I>chasid</I>, one who is the object of God''s loving-kindness (<I>chesed</I>): see [scripture id="ps?n=86:13"]ver. 13[/scripture] and [scripture id="ps?n=85:7"]Ps. 85:7[/scripture], [scripture id="ps?n=85:10"]10[/scripture], and [footnote id="n_ps?n=4:3"]Note to Ps. 4:3[/footnote]; and, by way of contrast, [scripture id="ps?n=43:1"]Ps. 43:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7859, 1, 19, 86, 5, 'd', 'Lit. ''forgiving.''');
INSERT INTO `mse_bible_footnote` VALUES (7860, 1, 19, 86, 12, 'e', 'Or ''thank;'' and so [scripture id="ps?n=88:10"]Ps. 88:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7861, 1, 19, 86, 15, 'f', 'Or ''loving-kindness,'' <I>chesed</I>, as in [scripture id="ps?n=86:5"]vers. 5[/scripture], [scripture id="ps?n=86:13"]13[/scripture], and [scripture id="ps?n=103:8"]Ps. 103:8[/scripture]: see [scripture id="ex?n=34:6"]Ex. 34:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7862, 1, 19, 87, 1, 'a', 'i.e. ''What he has founded.''');
INSERT INTO `mse_bible_footnote` VALUES (7863, 1, 19, 87, 2, 'b', 'Or ''tabernacles.''');
INSERT INTO `mse_bible_footnote` VALUES (7864, 1, 19, 87, 4, 'c', 'i.e. Egypt.');
INSERT INTO `mse_bible_footnote` VALUES (7865, 1, 19, 87, 7, 'd', 'Or ''pipers,'' ''players on instruments.''');
INSERT INTO `mse_bible_footnote` VALUES (7866, 1, 19, 88, 1, 'e', 'The meaning of <I>Mahalath</I> is uncertain, see [scripture id="ps?n=53"]Ps. 53[/scripture]. <I>Leannoth</I> may mean ''for subdued singing.''');
INSERT INTO `mse_bible_footnote` VALUES (7867, 1, 19, 88, 3, 'f', 'See [scripture id="ps?n=6:5"]Ps. 6:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7868, 1, 19, 88, 5, 'g', 'Or ''Cast away.''');
INSERT INTO `mse_bible_footnote` VALUES (7869, 1, 19, 88, 5, 'h', 'i.e. ''from thy leading and helping hand.''');
INSERT INTO `mse_bible_footnote` VALUES (7870, 1, 19, 88, 6, 'i', 'Or ''in darkness,'' lit. ''darknesses.''');
INSERT INTO `mse_bible_footnote` VALUES (7871, 1, 19, 88, 7, 'k', 'Strictly, ''breakers,'' as [scripture id="ps?n=42:7"]Ps. 42:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7872, 1, 19, 88, 10, 'l', 'Lit. ''wonder.''');
INSERT INTO `mse_bible_footnote` VALUES (7873, 1, 19, 88, 10, 'm', 'Or ''the dead,'' as [scripture id="job?n=26:5"]Job 26:5[/scripture]; [scripture id="pr?n=2:18"]Prov. 2:18[/scripture]; [scripture id="isa?n=14:9"]Isa. 14:9[/scripture]; not the same as ''dead'' in this verse.');
INSERT INTO `mse_bible_footnote` VALUES (7874, 1, 19, 88, 11, 'n', 'Heb. <I>Abaddon</I>.');
INSERT INTO `mse_bible_footnote` VALUES (7875, 1, 19, 88, 13, 'o', 'Lit. ''cometh to meet thee.''');
INSERT INTO `mse_bible_footnote` VALUES (7876, 1, 19, 88, 16, 'a', 'The Hebrew word is plural.');
INSERT INTO `mse_bible_footnote` VALUES (7877, 1, 19, 89, 1, 'b', 'Lit. ''loving-kindnesses,'' ''mercies,'' <I>chesed</I>, as [scripture id="ps?n=89:49"]ver. 49[/scripture]. The word is the same, but in the singular, in [scripture id="ps?n=89:2"]vers. 2[/scripture], [scripture id="ps?n=89:14"]14[/scripture], [scripture id="ps?n=89:24"]24[/scripture], [scripture id="ps?n=89:28"]28[/scripture], [scripture id="ps?n=89:33"]33[/scripture]. See [footnote id="n_ps?n=4:3"]Notes, Ps. 4:3[/footnote]; [footnote id="n_ps?n=43:1a"]43:1[/footnote]; [footnote id="n_ps?n=86:2"]86:2[/footnote]; and [footnote id="n_ac?n=13:34a"]Acts 13:34[/footnote]');
INSERT INTO `mse_bible_footnote` VALUES (7878, 1, 19, 89, 5, 'c', 'Elsewhere, ''praise,'' ''confess.''');
INSERT INTO `mse_bible_footnote` VALUES (7879, 1, 19, 89, 5, 'd', 'Lit. ''wonder.''');
INSERT INTO `mse_bible_footnote` VALUES (7880, 1, 19, 89, 5, 'e', 'As [scripture id="ex?n=12:3"]Ex. 12:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7881, 1, 19, 89, 5, 'f', '<I>Kadosh</I>, as [scripture id="ps?n=89:18"]ver. 18[/scripture], ''Holy One,'' [scripture id="ps?n=89:20"]ver. 20[/scripture], ''holy <I>oil</I>,'' and [scripture id="ps?n=89:35"]ver. 35[/scripture], ''holiness.''');
INSERT INTO `mse_bible_footnote` VALUES (7882, 1, 19, 89, 6, 'g', 'Or ''in the sky.''');
INSERT INTO `mse_bible_footnote` VALUES (7883, 1, 19, 89, 6, 'h', '<I>Elim</I>, plural of <I>El</I>: see [footnote id="n_ge?n=1:1"]Note, Gen. 1:1[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7884, 1, 19, 89, 7, 'i', 'Or ''above.''');
INSERT INTO `mse_bible_footnote` VALUES (7885, 1, 19, 89, 9, 'k', 'Or ''fury.''');
INSERT INTO `mse_bible_footnote` VALUES (7886, 1, 19, 89, 10, 'l', 'Egypt.');
INSERT INTO `mse_bible_footnote` VALUES (7887, 1, 19, 89, 18, 'm', 'Or ''For of (or from) Jehovah ... and of (or from) the Holy One,'' and see [footnote id="n_ps?n=89:5c"]Note f, ver. 5[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7888, 1, 19, 89, 19, 'n', '<I>Chasid</I>: see [scripture id="ps?n=16:10"]Ps. 16:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7889, 1, 19, 89, 19, 'o', 'Or a ''young man;'' as ''excellent,'' [scripture id="so?n=5:15"]Cant. 5:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7890, 1, 19, 89, 21, 'b', 'Or ''him.''');
INSERT INTO `mse_bible_footnote` VALUES (7891, 1, 19, 89, 27, 'c', '<I>Elyon</I>: see [scripture id="ps?n=83:18"]Ps. 83:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7892, 1, 19, 89, 32, 'd', 'Or ''plagues,'' see [scripture id="2sa?n=7:14"]2Sam. 7:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7893, 1, 19, 89, 35, 'a', 'See [footnote id="n_ps?n=89:5c"]Ps. 89:5, Note f[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7894, 1, 19, 89, 37, 'e', 'Or ''steadfast.''');
INSERT INTO `mse_bible_footnote` VALUES (7895, 1, 19, 89, 39, 'f', 'Elsewhere also ''diadem:'' see [footnote id="n_nu?n=6:2"]Note c, Num. 6:2[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7896, 1, 19, 89, 48, 'g', 'Lit. ''hand.''');
INSERT INTO `mse_bible_footnote` VALUES (7897, 1, 19, 89, 50, 'a', 'Lit. ''many,'' i.e. ''numerous.''');
INSERT INTO `mse_bible_footnote` VALUES (7898, 1, 19, 90, 2, 'b', 'Or ''given birth to,'' same in Hebrew as ''begot'' in [scripture id="de?n=32:18"]Deut. 32:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7899, 1, 19, 90, 2, 'c', 'Or ''brought forth,'' as [scripture id="de?n=32:18"]Deut. 32:18[/scripture]; [scripture id="pr?n=8:24~a=1"]Prov. 8:24, 25[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7900, 1, 19, 90, 2, 'd', 'See [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7901, 1, 19, 90, 3, 'e', 'Lit. ''to crumbling.''');
INSERT INTO `mse_bible_footnote` VALUES (7902, 1, 19, 90, 9, 'f', 'Or ''sigh.''');
INSERT INTO `mse_bible_footnote` VALUES (7903, 1, 19, 90, 10, 'g', 'These words are rendered ''mischief and iniquity,'' when presented actively: see [scripture id="job?n=4:8"]Job 4:8[/scripture]; [scripture id="ps?n=55:10"]Ps. 55:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7904, 1, 19, 90, 13, 'h', 'Or ''and be merciful to.''');
INSERT INTO `mse_bible_footnote` VALUES (7905, 1, 19, 90, 14, 'i', 'Or ''in the morning.''');
INSERT INTO `mse_bible_footnote` VALUES (7906, 1, 19, 90, 16, 'k', 'Or ''upon.''');
INSERT INTO `mse_bible_footnote` VALUES (7907, 1, 19, 90, 17, 'l', 'See [scripture id="ps?n=27:4"]Ps. 27:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7908, 1, 19, 91, 1, 'm', 'See [scripture id="ge?n=14:19"]Gen. 14:19[/scripture]; [scripture id="ps?n=83:18"]Ps. 83:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7909, 1, 19, 91, 1, 'n', '<I>Shaddai</I>: see [scripture id="ge?n=17:1"]Gen. 17:1[/scripture]; [scripture id="ex?n=6:3"]Ex. 6:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7910, 1, 19, 91, 2, 'o', 'Or ''to.''');
INSERT INTO `mse_bible_footnote` VALUES (7911, 1, 19, 91, 3, 'a', 'Or ''For.''');
INSERT INTO `mse_bible_footnote` VALUES (7912, 1, 19, 91, 12, 'b', 'Or ''lest thy foot stumble.''');
INSERT INTO `mse_bible_footnote` VALUES (7913, 1, 19, 91, 13, 'c', 'Strictly, ''a fierce lion,'' [scripture id="job?n=4:10"]Job 4:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7914, 1, 19, 91, 13, 'd', 'Or ''sea-monster,'' or ''serpent:'' see [footnote id="n_ge?n=1:21"]Note, Gen. 1:21[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7915, 1, 19, 91, 14, 'e', 'See [scripture id="ps?n=20:1"]Ps. 20:1[/scripture]; [scripture id="ps?n=69:29"]69:29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7916, 1, 19, 92, 3, 'f', 'Perhaps an instrument of music, or the softened tones of a harp (see [scripture id="ps?n=9:16"]Ps. 9:16[/scripture]); or ''meditation,'' as [scripture id="ps?n=19:14"]Ps. 19:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7917, 1, 19, 92, 4, 'g', '<I>Poal</I>, as [scripture id="isa?n=45:9"]Isa. 45:9[/scripture]; ''works'' <I>masseh</I> (next line) as ''occupation,'' [scripture id="ge?n=46:33"]Gen. 46:33[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7918, 1, 19, 92, 6, 'h', 'See [scripture id="ps?n=49:10"]Ps. 49:10[/scripture]; [scripture id="pr?n=12:1"]Prov. 12:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7919, 1, 19, 92, 6, 'i', '<I>Kesil</I>, as [scripture id="ps?n=49:10"]Ps. 49:10[/scripture]; [scripture id="ps?n=94:8"]94:8[/scripture]; [scripture id="pr?n=1:22"]Prov. 1:22[/scripture]; [scripture id="pr?n=10:23"]10:23[/scripture], &c.: see [footnote id="n_pr?n=1:7"]Note, Prov. 1:7[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7920, 1, 19, 92, 10, 'k', 'Strictly, ''mingled:'' see [footnote id="n_le?n=2:4a"]Note k, Lev. 2:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7921, 1, 19, 92, 14, 'a', 'Or ''shall still bear fruit:'' as ''increase,'' [scripture id="ps?n=62:10"]Ps. 62:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7922, 1, 19, 93, 3, 'b', 'Or ''rivers:'' so [scripture id="ps?n=98:8"]Ps. 98:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7923, 1, 19, 93, 5, 'c', 'Or ''length of days,'' as [scripture id="ps?n=23:6"]Ps. 23:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7924, 1, 19, 94, 7, 'd', 'See [footnote id="n_ps?n=68:4a"]Note c, Ps. 68:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7925, 1, 19, 94, 10, 'e', 'Or ''chasteneth.''');
INSERT INTO `mse_bible_footnote` VALUES (7926, 1, 19, 94, 11, 'f', 'Or ''a breath,'' as [scripture id="job?n=7:16"]Job 7:16[/scripture]; [scripture id="ps?n=39:5~a=1"]Ps. 39:5, 6[/scripture], [scripture id="ps?n=39:11"]11[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (7927, 1, 19, 94, 12, 'g', '<I>Geber</I>: see [scripture id="job?n=3:3"]Job 3:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7928, 1, 19, 94, 15, 'h', 'That is, ''judgment.''');
INSERT INTO `mse_bible_footnote` VALUES (7929, 1, 19, 94, 17, 'i', 'Or ''soon.''');
INSERT INTO `mse_bible_footnote` VALUES (7930, 1, 19, 94, 20, 'a', 'See [scripture id="ps?n=55:11"]Ps. 55:11[/scripture], ''perversities:'' see [footnote id="n_ps?n=5:9"]Note d, Ps. 5:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7931, 1, 19, 94, 20, 'b', 'Or ''iniquity.''');
INSERT INTO `mse_bible_footnote` VALUES (7932, 1, 19, 95, 4, 'c', 'Or ''treasures,'' as [scripture id="job?n=22:25"]Job 22:25[/scripture], ''heaped up.''');
INSERT INTO `mse_bible_footnote` VALUES (7933, 1, 19, 95, 8, 'd', 'Strife.');
INSERT INTO `mse_bible_footnote` VALUES (7934, 1, 19, 95, 8, 'e', 'Temptation.');
INSERT INTO `mse_bible_footnote` VALUES (7935, 1, 19, 95, 10, 'f', 'The word implies loathing.');
INSERT INTO `mse_bible_footnote` VALUES (7936, 1, 19, 95, 11, 'g', 'Or ''Unto whom I swore.''');
INSERT INTO `mse_bible_footnote` VALUES (7937, 1, 19, 96, 1, 'h', 'With [scripture id="ps?n=96"]Ps. 96[/scripture] compare [scripture id="1ch?n=16:23~a=10"]1Chron. 16:23-33[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7938, 1, 19, 96, 5, 'i', 'Or ''nonentities;'' and so [scripture id="ps?n=97:7"]Ps. 97:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7939, 1, 19, 96, 10, 'k', '<I>Tebel</I>, ''habitable earth:'' see [footnote id="n_1sa?n=2:8a"]Note f, 1Sam. 2:8[/footnote]; [scripture id="ps?n=9:8"]Ps. 9:8[/scripture]; [scripture id="ps?n=24:1"]24:1[/scripture]; [scripture id="ps?n=89:11"]89:11[/scripture]; [footnote id="n_ps?n=90:2b"]Note d, 90:2[/footnote]; [scripture id="ps?n=93:1"]93:1[/scripture]; [scripture id="ps?n=97:4"]97:4[/scripture]; [scripture id="ps?n=98:7"]98:7[/scripture], [scripture id="ps?n=98:9"]9[/scripture]; [scripture id="pr?n=8:31"]Prov. 8:31[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7940, 1, 19, 96, 13, 'a', 'See [footnote id="n_ps?n=96:10"]Ps. 96:10, Note k[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7941, 1, 19, 97, 6, 'b', 'Or ''declared ... saw.''');
INSERT INTO `mse_bible_footnote` VALUES (7942, 1, 19, 97, 7, 'c', 'See [footnote id="n_ps?n=96:5"]Note, Ps. 96:5[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7943, 1, 19, 97, 7, 'd', 'Or ''angels,'' <I>elohim,</I> as [scripture id="ps?n=8:5"]Ps. 8:5[/scripture]: see [scripture id="heb?n=1:6"]Heb. 1:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7944, 1, 19, 97, 10, 'e', 'Or ''godly ones,'' <I>chasid</I>: see [footnote id="n_ps?n=86:2"]Note, Ps. 86:2[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7945, 1, 19, 97, 11, 'f', 'The Hebrew word is singular.');
INSERT INTO `mse_bible_footnote` VALUES (7946, 1, 19, 97, 12, 'g', 'See [scripture id="ex?n=3:15"]Ex. 3:15[/scripture]. Holiness here is <I>Kodesh</I>, as [scripture id="ps?n=30:4"]Ps. 30:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7947, 1, 19, 98, 6, 'h', 'Or ''shout aloud,'' as [scripture id="ps?n=98:4"]ver. 4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7948, 1, 19, 99, 1, 'a', 'See [footnote id="n_ps?n=80:1a"]Note, Ps. 80:1[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7949, 1, 19, 99, 3, 'b', 'Or ''thank.''');
INSERT INTO `mse_bible_footnote` VALUES (7950, 1, 19, 99, 3, 'c', '<I>Kadosh</I>: see [scripture id="ps?n=16:3"]Ps. 16:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7951, 1, 19, 99, 4, 'd', 'Lit. ''judgment.''');
INSERT INTO `mse_bible_footnote` VALUES (7952, 1, 19, 100, 1, 'e', 'Or ''Make a joyful noise:'' see [scripture id="ps?n=89:15"]Ps. 89:15[/scripture]; [scripture id="ps?n=98:4"]98:4[/scripture], [scripture id="ps?n=98:6"]6[/scripture] -- ''earth;'' in this verse (1) may read ''land.''');
INSERT INTO `mse_bible_footnote` VALUES (7953, 1, 19, 100, 3, 'f', 'Others, ''and we are his.''');
INSERT INTO `mse_bible_footnote` VALUES (7954, 1, 19, 101, 3, 'g', 'Elsewhere also ''apostates,'' see [scripture id="ho?n=5:2"]Hos. 5:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7955, 1, 19, 101, 4, 'h', 'Or ''an evil person.''');
INSERT INTO `mse_bible_footnote` VALUES (7956, 1, 19, 101, 8, 'i', 'Or ''earth.''');
INSERT INTO `mse_bible_footnote` VALUES (7957, 1, 19, 102, 4, 'a', 'Or ''for.''');
INSERT INTO `mse_bible_footnote` VALUES (7958, 1, 19, 102, 10, 'b', 'See [footnote id="n_ps?n=38:1"]Notes, Ps. 38:1[/footnote] and [footnote id="n_ps?n=38:3"]3[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7959, 1, 19, 102, 12, 'c', 'See [scripture id="ex?n=3:15"]Ex. 3:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7960, 1, 19, 102, 14, 'd', 'Or ''are gracious to,'' as in preceding verse.');
INSERT INTO `mse_bible_footnote` VALUES (7961, 1, 19, 102, 20, 'e', 'Lit. ''the sons of death.''');
INSERT INTO `mse_bible_footnote` VALUES (7962, 1, 19, 102, 24, 'f', 'Lit. ''in generation of generations:'' see [scripture id="ps?n=72:5"]Ps. 72:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7963, 1, 19, 102, 27, 'g', 'Lit. ''Thou He,'' a name of God, ''The existing One, who does not change:'' see [scripture id="de?n=32:39"]Deut. 32:39[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7964, 1, 19, 102, 28, 'h', 'Or ''dwell,'' as [scripture id="ps?n=37:27"]Ps. 37:27[/scripture], [scripture id="ps?n=37:29"]29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7965, 1, 19, 103, 5, 'a', 'Or ''adornment,'' or ''mouth.''');
INSERT INTO `mse_bible_footnote` VALUES (7966, 1, 19, 103, 6, 'b', 'Lit. ''righteousnesses and judgments.''');
INSERT INTO `mse_bible_footnote` VALUES (7967, 1, 19, 103, 7, 'c', 'Or ''doings.'' ''exploits:'' so [scripture id="ps?n=66:5"]Ps. 66:5[/scripture]; [scripture id="ps?n=78:11"]78:11[/scripture]; [scripture id="ps?n=105:1"]105:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7968, 1, 19, 103, 11, 'd', 'Lit. ''so has prevailed.''');
INSERT INTO `mse_bible_footnote` VALUES (7969, 1, 19, 103, 15, 'e', '<I>Enosh</I>, ''mortal man:'' see [scripture id="ps?n=8:4"]Ps. 8:4[/scripture]; [scripture id="ps?n=9:20"]9:20[/scripture]; [scripture id="ps?n=144:3"]144:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7970, 1, 19, 103, 21, 'f', 'Or ''good pleasure,'' as [scripture id="ps?n=51:18"]Ps. 51:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7971, 1, 19, 104, 4, 'g', 'i.e. he created them so.');
INSERT INTO `mse_bible_footnote` VALUES (7972, 1, 19, 104, 13, 'a', '<I>Masseh</I>; as ''occupation,'' [scripture id="ge?n=46:33"]Gen. 46:33[/scripture]: see [scripture id="ps?n=92:4"]Ps. 92:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7973, 1, 19, 104, 15, 'b', '<I>Enosh</I>, [scripture id="ps?n=8:4"]Ps. 8:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7974, 1, 19, 104, 17, 'c', 'Or ''cypresses,'' as [scripture id="1ki?n=5:10"]1Kings 5:10[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (7975, 1, 19, 104, 18, 'd', 'As [scripture id="le?n=11:5"]Lev. 11:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7976, 1, 19, 104, 23, 'e', '<I>Adam</I>.');
INSERT INTO `mse_bible_footnote` VALUES (7977, 1, 19, 104, 26, 'f', 'Probably, in this instance, an animal of the whale tribe: see [scripture id="job?n=41:1"]Job 41:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7978, 1, 19, 104, 30, 'g', 'Or ''ground.''');
INSERT INTO `mse_bible_footnote` VALUES (7979, 1, 19, 104, 34, 'a', 'Or ''My meditation of him shall be pleasant.''');
INSERT INTO `mse_bible_footnote` VALUES (7980, 1, 19, 104, 35, 'b', 'Or ''May sinners be. ...''');
INSERT INTO `mse_bible_footnote` VALUES (7981, 1, 19, 104, 35, 'c', 'i.e. ''Praise ye Jah.''');
INSERT INTO `mse_bible_footnote` VALUES (7982, 1, 19, 105, 1, 'd', 'With [scripture id="ps?n=105:1~a=14"]vers. 1-15[/scripture] compare [scripture id="1ch?n=16:8~a=14"]1Chron. 16:8-22[/scripture]');
INSERT INTO `mse_bible_footnote` VALUES (7983, 1, 19, 105, 2, 'e', 'Or ''talk of.''');
INSERT INTO `mse_bible_footnote` VALUES (7984, 1, 19, 105, 5, 'f', 'Or ''ordinances.''');
INSERT INTO `mse_bible_footnote` VALUES (7985, 1, 19, 105, 12, 'g', 'As [scripture id="de?n=4:27"]Deut. 4:27[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7986, 1, 19, 105, 19, 'h', '''Said'' <I>dabar</I>; ''word'' <I>imrah</I>: see [footnote id="n_ps?n=119:11"]Note, Ps. 119:11[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7987, 1, 19, 105, 34, 'a', 'Lit. ''the feeder,'' a species of locust, most probably in the stage before it arrives at its perfect state, and in which it devours much herbage: see [scripture id="joe?n=1:4"]Joel 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7988, 1, 19, 105, 37, 'b', 'Or ''that stumbled.''');
INSERT INTO `mse_bible_footnote` VALUES (7989, 1, 19, 105, 43, 'c', 'As ''elect,'' [scripture id="ps?n=89:3"]Ps. 89:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7990, 1, 19, 105, 43, 'd', 'Or ''songs of triumph.''');
INSERT INTO `mse_bible_footnote` VALUES (7991, 1, 19, 105, 44, 'e', '<I>Leummim</I>: see [scripture id="ps?n=2:1"]Ps. 2:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7992, 1, 19, 105, 45, 'f', 'See [footnote id="n_ps?n=104:35a"]Note, Ps. 104:35[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (7993, 1, 19, 106, 1, 'g', 'With [scripture id="ps?n=106:1"]vers. 1[/scripture], [scripture id="ps?n=106:1~a=1"]47, 48[/scripture] compare [scripture id="1ch?n=16:34~a=2"]1Chron. 16:34-36[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7994, 1, 19, 106, 1, 'h', 'Or ''it;'' and so in [scripture id="ps?n=107:1"]Ps. 107:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7995, 1, 19, 106, 7, 'i', 'Or ''provoked <I>him</I>,'' as [scripture id="ps?n=106:43"]ver. 43[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7996, 1, 19, 106, 9, 'k', 'Or ''the wilderness;'' but see [scripture id="eze?n=34:25"]Ezek. 34:25[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7997, 1, 19, 106, 16, 'a', 'Or ''holy one'' (<I>kadosh</I>): see [scripture id="ps?n=89:18"]Ps. 89:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7998, 1, 19, 106, 26, 'b', 'i.e. he swore: see [scripture id="ex?n=6:8"]Ex. 6:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (7999, 1, 19, 106, 34, 'c', 'Or ''concerning whom.''');
INSERT INTO `mse_bible_footnote` VALUES (8000, 1, 19, 107, 12, 'a', 'Or ''pain,'' ''anguish.''');
INSERT INTO `mse_bible_footnote` VALUES (8001, 1, 19, 107, 20, 'a', 'Lit. ''pits,'' and so ''pit-falls:'' see [scripture id="job?n=33:18"]Job 33:18[/scripture], [scripture id="job?n=33:22"]22[/scripture]; [scripture id="la?n=4:20"]Lam. 4:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8002, 1, 19, 107, 22, 'b', '<I>Zavach</I>, lit. ''sacrifice,'' as [scripture id="ps?n=4:5"]Ps. 4:5[/scripture]; [scripture id="ps?n=27:6"]27:6[/scripture]; [scripture id="ps?n=50:14"]50:14[/scripture], [scripture id="ps?n=50:23"]23[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (8003, 1, 19, 107, 22, 'c', 'See [scripture id="le?n=7:12"]Lev. 7:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8004, 1, 19, 107, 22, 'd', 'Or ''with rejoicing.''');
INSERT INTO `mse_bible_footnote` VALUES (8005, 1, 19, 107, 43, 'e', 'Or ''Whoso is wise and observeth these things, even <I>they</I> shall.''');
INSERT INTO `mse_bible_footnote` VALUES (8006, 1, 19, 107, 43, 'f', '<I>Chesed</I>, as in [scripture id="ps?n=107:1"]vers. 1[/scripture], [scripture id="ps?n=107:8"]8[/scripture], [scripture id="ps?n=107:15"]15[/scripture], [scripture id="ps?n=107:21"]21[/scripture], [scripture id="ps?n=107:31"]31[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8007, 1, 19, 108, 1, 'g', 'Compare with this, [scripture id="ps?n=57:7~a=4"]Ps. 57:7-11[/scripture]; [scripture id="ps?n=60:5~a=7"]60:5-12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8008, 1, 19, 108, 2, 'h', 'Or ''awake with the dawn.''');
INSERT INTO `mse_bible_footnote` VALUES (8009, 1, 19, 108, 3, 'i', 'As [scripture id="ps?n=7:8"]Ps. 7:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8010, 1, 19, 108, 3, 'k', '<I>Leummim</I>, as [scripture id="ps?n=67:4"]Ps. 67:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8011, 1, 19, 108, 6, 'a', 'Some read ''us.''');
INSERT INTO `mse_bible_footnote` VALUES (8012, 1, 19, 108, 8, 'b', '''defence.''');
INSERT INTO `mse_bible_footnote` VALUES (8013, 1, 19, 108, 13, 'c', '<I>Tzar</I>, or ''oppressors,'' as [scripture id="ps?n=8:2"]Ps. 8:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8014, 1, 19, 109, 2, 'd', 'Or ''to me.''');
INSERT INTO `mse_bible_footnote` VALUES (8015, 1, 19, 109, 4, 'e', '''Satan,'' as [scripture id="1ch?n=21:1"]1Chron. 21:1[/scripture], without the article. The same word, as verb or participle, is in [scripture id="ps?n=109:4"]vers. 4[/scripture], [scripture id="ps?n=109:20"]20[/scripture], [scripture id="ps?n=109:29"]29[/scripture], and [scripture id="ps?n=38:20"]Ps. 38:20[/scripture]; [scripture id="ps?n=71:13"]71:13[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8016, 1, 19, 109, 7, 'f', 'i.e. be imputed as sin.');
INSERT INTO `mse_bible_footnote` VALUES (8017, 1, 19, 109, 10, 'g', 'Or ''ruins:'' so [scripture id="ps?n=102:6"]Ps. 102:6[/scripture]: ''waste,'' [scripture id="le?n=26:31"]Lev. 26:31[/scripture], [scripture id="le?n=26:33"]33[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8018, 1, 19, 109, 12, 'h', 'Or ''continue mercy,'' <I>chesed</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8019, 1, 19, 109, 18, 'i', 'Or ''And let him clothe.''');
INSERT INTO `mse_bible_footnote` VALUES (8020, 1, 19, 109, 20, 'a', 'Lit. ''the <I>work</I> wrought;'' ''wages,'' [scripture id="le?n=19:13"]Lev. 19:13[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8021, 1, 19, 109, 30, 'b', 'Or ''thank:'' so [scripture id="ps?n=111:1"]Ps. 111:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8022, 1, 19, 110, 1, 'c', 'As [scripture id="ge?n=22:16"]Gen. 22:16[/scripture]; [scripture id="ps?n=36:1"]Ps. 36:1[/scripture].');
