INSERT INTO `mse_bible_footnote` VALUES (8065, 1, 19, 119, 25, 'f', 'The word has the double sense of ''making to live,'' [scripture id="job?n=33:4"]Job 33:4[/scripture], and ''keeping alive,'' [scripture id="ge?n=7:3"]Gen. 7:3[/scripture].');
