INSERT INTO `mse_bible_footnote` VALUES (8067, 1, 19, 119, 43, 'a', 'The word means ''to wait with earnest desire and longing'' (see [scripture id="ps?n=69:3"]Ps. 69:3[/scripture]); and so [scripture id="ps?n=119:49"]vers. 49[/scripture], [scripture id="ps?n=119:74"]74[/scripture], [scripture id="ps?n=119:81"]81[/scripture], [scripture id="ps?n=119:114"]114[/scripture], [scripture id="ps?n=119:147"]147[/scripture]; [scripture id="ps?n=147:11"]Ps. 147:11[/scripture], &c.');
