INSERT INTO `mse_bible_footnote` VALUES (8162, 1, 19, 140, 11, 'b', '<I>Erets</I>, ''land:'' see [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8163, 1, 19, 141, 5, 'c', 'Or ''smite me in kindness.''');
INSERT INTO `mse_bible_footnote` VALUES (8164, 1, 19, 141, 5, 'd', 'Or ''it is oil for the head; let not my head refuse it.''');
INSERT INTO `mse_bible_footnote` VALUES (8165, 1, 19, 141, 5, 'e', 'Or ''is against their evil deeds.''');
INSERT INTO `mse_bible_footnote` VALUES (8166, 1, 19, 141, 7, 'f', 'Or ''cleaveth the earth.''');
INSERT INTO `mse_bible_footnote` VALUES (8167, 1, 19, 141, 8, 'g', 'Or ''Let not my soul be laid bare'' or ''poured out.''');
INSERT INTO `mse_bible_footnote` VALUES (8168, 1, 19, 142, 4, 'a', '''A place of escape,'' as [scripture id="ps?n=59:16"]Ps. 59:16[/scripture]; [scripture id="am?n=2:14"]Amos 2:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8169, 1, 19, 142, 5, 'b', 'Or ''shelter;'' as [scripture id="isa?n=25:4"]Isa. 25:4[/scripture], [scripture id="jer?n=17:17"]Jer. 17:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8170, 1, 19, 142, 7, 'c', 'As [scripture id="ps?n=138:2"]Ps. 138:2[/scripture]: see [scripture id="ps?n=109:30"]Ps. 109:30[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8171, 1, 19, 143, 9, 'd', 'As ''covered,'' [scripture id="isa?n=51:16"]Isa. 51:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8172, 1, 19, 143, 10, 'e', 'As [scripture id="ps?n=40:8"]Ps. 40:8[/scripture]; [scripture id="ps?n=103:21"]103:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8173, 1, 19, 143, 10, 'f', 'Or ''thy Spirit is good. Lead me.''');
INSERT INTO `mse_bible_footnote` VALUES (8174, 1, 19, 143, 10, 'g', 'Or ''land of uprightness.''');
INSERT INTO `mse_bible_footnote` VALUES (8175, 1, 19, 143, 11, 'h', 'Or ''Keep me alive,'' as [scripture id="ps?n=119:25"]Ps. 119:25[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8176, 1, 19, 144, 2, 'i', 'See [footnote id="n_ps?n=59:10"]Note, Ps. 59:10[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8177, 1, 19, 144, 3, 'k', '<I>Adam</I> as [scripture id="ge?n=1:26"]Gen. 1:26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8178, 1, 19, 144, 3, 'l', '<I>Enosh</I>. Note here, first ''<I>Adam</I>,'' then ''son of <I>Enosh</I>,'' i.e. just the converse of [scripture id="ps?n=8:4"]Ps. 8:4[/scripture]. Here it is sinful man''s fragility and impotence, there what God does in grace in exalting the ''SON OF MAN'' (<I>Adam</I>).');
INSERT INTO `mse_bible_footnote` VALUES (8179, 1, 19, 144, 4, 'a', '<I>Adam</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8180, 1, 19, 144, 4, 'b', 'Or ''a breath,'' as [scripture id="ps?n=62:9"]Ps. 62:9[/scripture]; [scripture id="ps?n=94:11"]94:11[/scripture]: see [footnote id="n_ge?n=4:2"]Note b, Gen. 4:2[/footnote]: in vers;  [scripture id="ge?n=4:8"]8[/scripture], [scripture id="ge?n=4:11"]11[/scripture] the word is as ''falsehood'' in [footnote id="n_ps?n=12:2"]Ps. 12:2, where see Note g[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8181, 1, 19, 144, 10, 'c', 'Or ''victory.''');
INSERT INTO `mse_bible_footnote` VALUES (8182, 1, 19, 144, 13, 'd', 'Or ''streets.''');
INSERT INTO `mse_bible_footnote` VALUES (8183, 1, 19, 144, 14, 'e', 'Or ''oxen well laden.''');
INSERT INTO `mse_bible_footnote` VALUES (8184, 1, 19, 145, 1, 'f', 'An alphabetical Psalm, as [scripture id="ps?n=25"]Ps. 25[/scripture], the 14th letter being, omitted.');
INSERT INTO `mse_bible_footnote` VALUES (8185, 1, 19, 145, 7, 'g', 'As [scripture id="ps?n=19:2"]Ps. 19:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8186, 1, 19, 145, 10, 'h', 'Or ''godly ones:'' see [scripture id="ps?n=30:4"]Ps. 30:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8187, 1, 19, 145, 12, 'i', 'Or ''the glory of the majesty.''');
INSERT INTO `mse_bible_footnote` VALUES (8188, 1, 19, 145, 13, 'k', 'Lit. ''in every generation and generation,'' as [scripture id="ps?n=45:17"]Ps. 45:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8189, 1, 19, 145, 17, 'a', '<I>Chasid</I>, as ''merciful,'' [scripture id="jer?n=3:12"]Jer. 3:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8190, 1, 19, 145, 21, 'b', 'Or ''all flesh shall bless.''');
INSERT INTO `mse_bible_footnote` VALUES (8191, 1, 19, 146, 3, 'c', '<I>Adam</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8192, 1, 19, 146, 9, 'd', 'Or ''keepeth,'' as [scripture id="ps?n=145:20"]Ps. 145:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8193, 1, 19, 146, 9, 'e', 'Or ''upholdeth.''');
INSERT INTO `mse_bible_footnote` VALUES (8194, 1, 19, 146, 9, 'f', 'Properly ''make tortuous.''');
INSERT INTO `mse_bible_footnote` VALUES (8195, 1, 19, 147, 1, 'g', '''Hallelujah:'' see [scripture id="ps?n=68:4"]Ps. 68:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8196, 1, 19, 147, 1, 'h', 'See [scripture id="ps?n=30:12"]Ps. 30:12[/scripture]; [scripture id="ps?n=47:6"]47:6[/scripture]; [scripture id="ps?n=138:1"]138:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8197, 1, 19, 147, 7, 'i', 'As [scripture id="nu?n=21:17"]Num. 21:17[/scripture]. Lit. ''Respond,'' as [scripture id="ps?n=119:172"]Ps. 119:172[/scripture]; [scripture id="ho?n=2:15"]Hos. 2:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8198, 1, 19, 147, 14, 'a', 'Lit ''the fat of wheat.''');
INSERT INTO `mse_bible_footnote` VALUES (8199, 1, 19, 147, 15, 'b', '<I>Imrah</I>: see [footnote id="n_ps?n=119:11"]Note, Ps. 119:11[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8200, 1, 19, 147, 19, 'c', 'Or ''ordinances.''');
INSERT INTO `mse_bible_footnote` VALUES (8201, 1, 19, 148, 6, 'd', 'Or ''which they do not transgress.''');
INSERT INTO `mse_bible_footnote` VALUES (8202, 1, 19, 148, 11, 'e', '<I>Leummim</I>: see [scripture id="ps?n=2:1"]Ps. 2:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8203, 1, 19, 148, 14, 'f', '<I>Am</I> (singular of <I>Ammim</I>, [scripture id="ps?n=7:8"]Ps. 7:8[/scripture]). It generally signifies Israel.');
INSERT INTO `mse_bible_footnote` VALUES (8204, 1, 19, 148, 14, 'g', 'Or ''godly,'' <I>chasid</I>: see [scripture id="ps?n=30:4"]Ps. 30:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8205, 1, 19, 149, 1, 'h', 'As [scripture id="ex?n=12:6"]Ex. 12:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8206, 1, 19, 149, 2, 'i', 'The Hebrew word for ''Maker'' is plural.');
INSERT INTO `mse_bible_footnote` VALUES (8207, 1, 19, 149, 4, 'a', '<I>Am</I> (singular of <I>Ammim</I>, [scripture id="ps?n=7:8"]Ps. 7:8[/scripture]). It generally signifies Israel.');
INSERT INTO `mse_bible_footnote` VALUES (8208, 1, 19, 149, 5, 'b', '<I>Chasid</I>: see [scripture id="ps?n=30:4"]Ps. 30:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8209, 1, 19, 149, 6, 'c', 'Lit. ''throat,'' as [scripture id="ps?n=5:9"]Ps. 5:9[/scripture]; [scripture id="isa?n=58:1"]Isa. 58:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8210, 1, 19, 149, 7, 'd', '<I>Goim</I>: see [scripture id="isa?n=1:4"]Isa. 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8211, 1, 19, 149, 7, 'e', '<I>Leummim</I>: see [scripture id="ps?n=2:1"]Ps. 2:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8212, 1, 19, 149, 9, 'f', 'Or ''exercise among them.''');
INSERT INTO `mse_bible_footnote` VALUES (8213, 1, 19, 150, 1, 'g', '<I>Kodesh</I>: as [scripture id="ps?n=134:2"]Ps. 134:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8214, 1, 19, 150, 1, 'h', 'Lit. the ''expanse,'' as [scripture id="ge?n=1:6"]Gen. 1:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8215, 1, 66, 1, 1, 'b', 'Aorist, lit. ''have taken place,'' perhaps ''be accomplished.''');
INSERT INTO `mse_bible_footnote` VALUES (8216, 1, 66, 1, 1, 'c', '* Lit. ''made known by signs.''');
INSERT INTO `mse_bible_footnote` VALUES (8217, 1, 66, 1, 3, 'd', '<I>Logos</I>, as throughout this book, save [scripture id="re?n=17:17"]ch. 17:17[/scripture], <I>rhema</I>. See [footnote id="n_joh?n=17:14"]Note at John 17:14[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8218, 1, 66, 1, 4, 'e', 'It is not ''about to come,'' for the <I>act</I> of the Lord''s coming is not immediately before the mind. I will not say there is no allusion to the future awaited exercise of divine power; for in Daniel, not only is the Son of man brought before the Ancient of days, but the Ancient of days comes. In the words ''who is, and was, and is to come,'' there is surely reference to the abiding nature of his being. ''Who is to come'' seems to me thus to render more truly the sense than any other form of words. The Greek has the future sense. See [scripture id="mr?n=10:30"]Mark 10:30[/scripture], [scripture id="lu?n=18:30"]Luke 18:30[/scripture]; and [scripture id="mr?n=11:10"]Mark 11:10[/scripture] partly runs into this sense. ''The coming one'' became a name of the expected Messiah, as in [scripture id="mt?n=11:3"]Matt. 11:3[/scripture], and in [scripture id="heb?n=10:37"]Heb. 10:37[/scripture]. But note, ''essential being'' is put first, ''who is;'' and then ''time past, and to come.'' ''Who is'' asserts the essential nature of his being.');
INSERT INTO `mse_bible_footnote` VALUES (8219, 1, 66, 1, 5, 'f', 'Lit. ''of the dead.''');
INSERT INTO `mse_bible_footnote` VALUES (8220, 1, 66, 1, 6, 'g', 'Lit. ''he has made.'' ''Loves'' and ''has washed'' are participles.');
INSERT INTO `mse_bible_footnote` VALUES (8221, 1, 66, 1, 7, 'h', '<I>Hostis</I>: as [scripture id="mt?n=7:24"]Matt. 7:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8222, 1, 66, 1, 7, 'i', 'Or ''earth.'' See [footnote id="n_mt?n=5:5"]Note at Matt. 5:5[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8223, 1, 66, 1, 8, 'k', 'Alpha and Omega are the first and last letters of the Greek alphabet.');
INSERT INTO `mse_bible_footnote` VALUES (8224, 1, 66, 1, 8, 'l', '''Jehovah Elohim,'' as [scripture id="lu?n=1:32"]Luke 1:32[/scripture]; see [scripture id="re?n=4:8"]chs. 4:8[/scripture]; [scripture id="re?n=15:3"]15:3[/scripture]; [scripture id="re?n=18:8"]18:8[/scripture]; [scripture id="re?n=21:22"]21:22[/scripture]; [scripture id="re?n=22:5~a=1"]22:5, 6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8225, 1, 66, 1, 8, 'm', '''The Almighty'' always has the article in Greek in the Revelation.');
INSERT INTO `mse_bible_footnote` VALUES (8226, 1, 66, 1, 9, 'n', '<I>Hupomone</I>, ''endurance:'' see [scripture id="jas?n=5:7"]Jas. 5:7[/scripture]; so [scripture id="re?n=2:2~a=1"]chs. 2:2, 3[/scripture], [scripture id="re?n=2:19"]19[/scripture]; [scripture id="re?n=13:10"]13:10[/scripture]; [scripture id="re?n=14:12"]14:12[/scripture]. The words ''tribulation,'' ''kingdom,'' ''patience'' are intimately connected, being brought together under one head by one article in the Greek.');
INSERT INTO `mse_bible_footnote` VALUES (8227, 1, 66, 1, 9, 'o', '<I>Ginomai</I>: see [scripture id="joh?n=1:17"]John 1:17[/scripture]. His being there was an event which happened on account of the word of God. So ''became,'' [scripture id="re?n=1:10"]ver. 10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8228, 1, 66, 1, 10, 'p', '''In <I>the</I> Spirit'' is a <I>state</I> into which he entered. See [footnote id="n_mt?n=3:11a"]Note c, Matt. 3:11[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8229, 1, 66, 1, 10, 'a', 'The ''dominical'' or ''lordly'' day -- characterized by belonging to the Lord, as ''Lord''s,'' [scripture id="1co?n=11:20"]1Cor. 11:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8230, 1, 66, 1, 11, 'b', 'Aorist, ''have it written and sent:'' it is to be a complete thing done.');
INSERT INTO `mse_bible_footnote` VALUES (8231, 1, 66, 1, 12, 'c', 'Or ''lampstands'' (<I>luchnia</I>) but simply here, I think, ''lamps'' as compared with [scripture id="heb?n=9:2"]Heb. 9:2[/scripture], where it is clearly the ''candlestick'' or ''lampstand.'' If <I>luchnos</I> and <I>luchnia</I> are distinguished, <I>luchnia</I> is the stand. The words are so distinguished in [scripture id="mt?n=5:15"]Matt. 5:15[/scripture], [scripture id="lu?n=8:16"]Luke 8:16[/scripture]; [scripture id="lu?n=11:33"]11:33[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8232, 1, 66, 1, 13, 'd', 'Or ''a Son of man.'' See [scripture id="da?n=7:13"]Dan. 7:13[/scripture], also without the article. It is not likeness to a person John knew, but to the character known by this title in scripture. To have seen angels in heaven would have been no wonder, but to see one as ''Son of man'' was. This chapter corresponds to [scripture id="da?n=7"]Dan. 7[/scripture]: only now he was seen on earth. It was the title the Lord habitually took. This made it personal; but in Daniel, though surely the same person, it was characteristic. Here, too, it is characteristic. Still the person designated is now known, and it is difficult to say ''a Son of man,'' because of excluding this. ''Son-of-man-like'' is feeble, it might only mean a manner: see [scripture id="re?n=14:14"]ch. 14:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8233, 1, 66, 1, 15, 'e', 'Or ''shining.''');
INSERT INTO `mse_bible_footnote` VALUES (8234, 1, 66, 1, 18, 'f', '<I>Ginomai</I>: see [scripture id="joh?n=1:17"]John 1:17[/scripture]. Became what he was not before.');
INSERT INTO `mse_bible_footnote` VALUES (8235, 1, 66, 1, 18, 'g', 'Hades: see [footnote id="n_mt?n=11:23"]Note, Matt. 11:23[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8236, 1, 66, 1, 19, 'h', 'The first ''are'' is plural; ''are about to'' is singular.');
INSERT INTO `mse_bible_footnote` VALUES (8237, 1, 66, 1, 20, 'i', 'The word has also the sense of ''messengers:'' see [footnote id="n_re?n=2:20"]Note e, ch. 2:20[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8238, 1, 66, 2, 8, 'a', 'See [footnote id="n_re?n=1:18"]Note f, ch. 1:18[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8239, 1, 66, 2, 8, 'b', 'That is, after having died: see [scripture id="re?n=1:17~a=1"]chs. 1:17, 18[/scripture]; [scripture id="re?n=13:14"]13:14[/scripture]; [scripture id="mt?n=9:18"]Matt. 9:18[/scripture]; [scripture id="ro?n=14:9"]Rom. 14:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8240, 1, 66, 2, 11, 'c', 'A strong negative.');
INSERT INTO `mse_bible_footnote` VALUES (8241, 1, 66, 2, 18, 'd', 'Or ''shining.''');
INSERT INTO `mse_bible_footnote` VALUES (8242, 1, 66, 2, 20, 'e', 'Some read ''thy wife,'' alluding doubtless to Jezebel''s connexion with responsible Ahab. And that is the meaning I believe of ''angel;'' the symbolical representative of the assembly seen in those responsible in it, which indeed all really are. Hence ''thee,'' and ''to you,'' ''to you each,'' and ''the rest.''');
INSERT INTO `mse_bible_footnote` VALUES (8243, 1, 66, 2, 20, 'f', 'Or ''bondmen,'' as [scripture id="re?n=1:1"]ch. 1:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8244, 1, 66, 2, 23, 'g', '<I>Ginosko</I>: [scripture id="re?n=2:23~a=1"]chs. 2:23, 24[/scripture]; [scripture id="re?n=3:3"]3:3[/scripture], [scripture id="re?n=3:9"]9[/scripture]; elsewhere <I>Oida</I> in this book: see [scripture id="1co?n=8:1"]1Cor. 8:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8245, 1, 66, 2, 24, 'a', '''Such as.''');
INSERT INTO `mse_bible_footnote` VALUES (8246, 1, 66, 2, 24, 'b', 'See [footnote id="n_re?n=2:23"]Note, ver. 23[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8247, 1, 66, 3, 2, 'c', '<I>Ginomai</I>. Lit. ''become so.''');
INSERT INTO `mse_bible_footnote` VALUES (8248, 1, 66, 3, 2, 'd', 'The imperfect tense. I translate ''are,'' because ''were'' supposes they are no longer ''about to die,'' which is contrary to the passage. Perhaps ''have been'' may be used. It refers to what they were found to be when he strengthened them. The point of departure of thought is not the writer''s when writing, but the strengthener''s when strengthening.');
INSERT INTO `mse_bible_footnote` VALUES (8249, 1, 66, 3, 3, 'e', 'See [footnote id="n_re?n=2:23"]Note, ch. 2:23[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8250, 1, 66, 3, 7, 'f', 'Some authorities read ''opens.''');
INSERT INTO `mse_bible_footnote` VALUES (8251, 1, 66, 3, 8, 'g', 'Lit. ''give'' or ''have given'' ([scripture id="re?n=3:8"]ver. 8[/scripture]) and ''give'' ([scripture id="re?n=3:9"]ver. 9[/scripture]).');
INSERT INTO `mse_bible_footnote` VALUES (8252, 1, 66, 3, 9, 'h', '<I>Proskuneo</I>, see [scripture id="mt?n=4:10"]Matt. 4:10[/scripture]. This is the word translated ''do homage'' or ''worship'' throughout Revelation.');
INSERT INTO `mse_bible_footnote` VALUES (8253, 1, 66, 3, 10, 'i', '<I>Hupomone</I>;, ''endurance:'' see [scripture id="re?n=1:9"]ch. 1:9[/scripture] and [footnote id="n_jas?n=5:7a"]Note f, Jas. 5:7[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8254, 1, 66, 3, 12, 'a', '<I>Naos</I>, the house itself (or the inner shrine): see [scripture id="1co?n=9:13"]1Cor. 9:13[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8255, 1, 66, 3, 18, 'b', 'All aorists, having the force of an accomplished fact, ''mayest have become,'' ''have got into that state.''');
INSERT INTO `mse_bible_footnote` VALUES (8256, 1, 66, 3, 19, 'c', '<I>Phileo</I>: see [footnote id="n_joh?n=21:15"]Note b, John 21:15[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8257, 1, 66, 3, 20, 'd', 'Meaning ''I have placed myself there and am standing:'' see [scripture id="joh?n=1:26"]John 1:26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8258, 1, 66, 4, 3, 'e', 'I use ''round'' for what is connected with anything (not necessarily united to it) as a centre, as the tire of a wheel, but ''around'' is used for detached objects encircling.');
INSERT INTO `mse_bible_footnote` VALUES (8259, 1, 66, 4, 5, 'f', '<I>Lampas</I>: only here and [scripture id="re?n=8:10"]ch. 8:10[/scripture] where the word is translated ''torch.'' Not the lamp as a utensil (<I>luchnia</I>, [scripture id="re?n=1:13"]ch. 1:13[/scripture]), but what furnished the light itself');
INSERT INTO `mse_bible_footnote` VALUES (8260, 1, 66, 4, 8, 'a', 'See [scripture id="isa?n=6:2~a=1"]Isa. 6:2, 3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8261, 1, 66, 4, 8, 'b', 'I judge that [scripture id="eze?n=10:12"]Ezek. 10:12[/scripture] shows that ''round'' belongs to the eyes.');
INSERT INTO `mse_bible_footnote` VALUES (8262, 1, 66, 4, 8, 'c', 'I prefer ''cease not'' to ''have no rest,'' because in English ''having no rest'' means constant fatigue. The Greek here means ''no intermission of action.''');
INSERT INTO `mse_bible_footnote` VALUES (8263, 1, 66, 4, 8, 'd', 'See [footnote id="n_re?n=1:8a"]Note l, ch. 1:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8264, 1, 66, 4, 8, 'e', 'On the throne, ''who was'' comes first. It is in time: see [footnote id="n_re?n=1:4"]Note, ch. 1:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8265, 1, 66, 4, 10, 'f', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8266, 1, 66, 5, 1, 'g', 'Or ''that sits.'' It is the present tense, and is really a title, ''the sitter on the throne.''');
INSERT INTO `mse_bible_footnote` VALUES (8267, 1, 66, 5, 6, 'h', '<I>Arnion</I>: a diminutive, used throughout Revelation: as [scripture id="joh?n=21:15"]John 21:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8268, 1, 66, 5, 8, 'i', '''Having'' refers strictly only to ''elders.''');
INSERT INTO `mse_bible_footnote` VALUES (8269, 1, 66, 5, 9, 'k', 'Or ''bought,'' as [scripture id="re?n=14:3~a=1"]ch. 14:3, 4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8270, 1, 66, 5, 10, 'l', 'Some authorities here read ''a kingdom,'' as in [scripture id="re?n=1:6"]ch. 1:6[/scripture], but it is rather here a title or name.');
INSERT INTO `mse_bible_footnote` VALUES (8271, 1, 66, 5, 11, 'm', 'See [footnote id="n_re?n=4:3"]Note, ch. 4:3[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8272, 1, 66, 5, 14, 'a', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8273, 1, 66, 6, 1, 'b', 'The words ''and see'' here and [scripture id="re?n=6:3"]vers. 3[/scripture], [scripture id="re?n=6:5"]5[/scripture], [scripture id="re?n=6:7"]7[/scripture], are very doubtful and probably added in some MSS.');
INSERT INTO `mse_bible_footnote` VALUES (8274, 1, 66, 6, 2, 'c', 'Or ''had been given to him.'' It is not expressive of a particular time. He had one which was given him. It is the aorist, as ''went forth.''');
INSERT INTO `mse_bible_footnote` VALUES (8275, 1, 66, 6, 4, 'd', 'Aorist tense, see [footnote id="n_re?n=6:2"]Note, ver. 2[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8276, 1, 66, 6, 8, 'e', 'See [footnote id="n_mt?n=11:23"]Note, Matt. 11:23[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8277, 1, 66, 6, 8, 'f', 'See [scripture id="eze?n=14:21"]Ezek. 14:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8278, 1, 66, 6, 10, 'g', 'Lit. ''Until when.''');
INSERT INTO `mse_bible_footnote` VALUES (8279, 1, 66, 6, 10, 'h', '<I>Despotes</I>, translated ''Lord'' in [scripture id="ac?n=4:24"]Acts 4:24[/scripture]. Here, ''Sovereign Ruler.''');
INSERT INTO `mse_bible_footnote` VALUES (8280, 1, 66, 6, 16, 'a', 'The aorist.');
INSERT INTO `mse_bible_footnote` VALUES (8281, 1, 66, 7, 3, 'b', '<I>Doulos</I>: as [scripture id="re?n=2:20"]chs. 2:20[/scripture]; [scripture id="re?n=10:7"]10:7[/scripture]; [scripture id="re?n=11:18"]11:18[/scripture]; [scripture id="re?n=15:3"]15:3[/scripture]; [scripture id="re?n=19:2"]19:2[/scripture], [scripture id="re?n=19:5"]5[/scripture]; [scripture id="re?n=22:3"]22:3[/scripture], [scripture id="re?n=22:6"]6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8282, 1, 66, 7, 11, 'c', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8283, 1, 66, 7, 14, 'd', 'Aorists.');
INSERT INTO `mse_bible_footnote` VALUES (8284, 1, 66, 7, 15, 'e', '<I>Latreuo</I>, ''priestly service,'' as [scripture id="lu?n=2:37"]Luke 2:37[/scripture], [scripture id="heb?n=12:28"]Heb. 12:28[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8285, 1, 66, 7, 16, 'f', 'See [scripture id="isa?n=49:10"]Isa. 49:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8286, 1, 66, 8, 1, 'a', '<I>Ginomai</I>: It took place then.');
INSERT INTO `mse_bible_footnote` VALUES (8287, 1, 66, 8, 4, 'b', 'There is no Greek preposition here for ''with;'' the sense is ''associated with and giving its efficacy to,'' according to [scripture id="re?n=8:3"]ver. 3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8288, 1, 66, 8, 5, 'c', 'Or ''to,'' <I>eis</I>, [scripture id="mt?n=12:20"]Matt. 12:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8289, 1, 66, 8, 7, 'd', 'Or ''it was.''');
INSERT INTO `mse_bible_footnote` VALUES (8290, 1, 66, 8, 10, 'e', '<I>Epi</I>, as [scripture id="re?n=16:2"]ch. 16:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8291, 1, 66, 9, 1, 'f', '<I>Eis</I>, as ''on,'' [scripture id="re?n=8:5"]ch. 8:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8292, 1, 66, 9, 2, 'g', 'i.e. the star.');
INSERT INTO `mse_bible_footnote` VALUES (8293, 1, 66, 9, 4, 'a', 'Such as had this character. <I>Hostis</I>: as [scripture id="mt?n=7:24"]Matt. 7:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8294, 1, 66, 9, 10, 'b', '<I>Exousia</I>, authority, title, to do it: see [scripture id="mt?n=10:1"]Matt. 10:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8295, 1, 66, 9, 11, 'c', 'Destruction: see [scripture id="job?n=26:6"]Job 26:6[/scripture]; [scripture id="job?n=28:22"]28:22[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8296, 1, 66, 9, 11, 'd', 'i.e. Destroyer.');
INSERT INTO `mse_bible_footnote` VALUES (8297, 1, 66, 9, 12, 'e', 'Perhaps ''has passed away.''');
INSERT INTO `mse_bible_footnote` VALUES (8298, 1, 66, 9, 18, 'f', 'Or ''of the men.''');
INSERT INTO `mse_bible_footnote` VALUES (8299, 1, 66, 9, 19, 'g', '<I>Exousia</I>: see [scripture id="re?n=9:10"]ver. 10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8300, 1, 66, 9, 20, 'h', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8301, 1, 66, 10, 3, 'i', 'Lit. ''spoke.''');
INSERT INTO `mse_bible_footnote` VALUES (8302, 1, 66, 10, 7, 'a', 'Or ''when he shall sound.'' I have translated literally; but the sense I believe to be ''when he shall sound, as he is about to do.''');
INSERT INTO `mse_bible_footnote` VALUES (8303, 1, 66, 10, 7, 'b', 'Aorist, ''will have been.'' But in the Revelation the aorist is almost everywhere used where other tenses might be put. Such evident Hebraisms are incessant.');
INSERT INTO `mse_bible_footnote` VALUES (8304, 1, 66, 10, 11, 'c', 'Lit. ''they say:'' see [footnote id="n_lu?n=6:38"]Note b, Luke 6:38[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8305, 1, 66, 11, 1, 'd', 'Or ''rod.''');
INSERT INTO `mse_bible_footnote` VALUES (8306, 1, 66, 11, 1, 'e', '<I>Naos</I>: see [scripture id="re?n=3:12"]ch. 3:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8307, 1, 66, 11, 1, 'f', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8308, 1, 66, 11, 6, 'g', 'As [scripture id="re?n=9:10"]ch. 9:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8309, 1, 66, 11, 8, 'h', '<I>Hostis</I>, as [scripture id="mt?n=7:24"]Matt. 7:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8310, 1, 66, 11, 11, 'a', 'Or ''breath.''');
INSERT INTO `mse_bible_footnote` VALUES (8311, 1, 66, 11, 13, 'b', '<I>Ginomai</I>: aorist. It then took place: ''they became;'' but this has too much the sense of continuance. ''Were'' gives the sense.');
INSERT INTO `mse_bible_footnote` VALUES (8312, 1, 66, 11, 14, 'c', 'See [scripture id="re?n=9:12"]ch. 9:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8313, 1, 66, 11, 15, 'd', '<I>Ginomai</I>: aorist. Has now taken place, begun to be.');
INSERT INTO `mse_bible_footnote` VALUES (8314, 1, 66, 11, 16, 'e', 'Or ''who sat.'' It is characteristic, without reference to time, save as he had seen them ([scripture id="re?n=4:4"]ch. 4:4[/scripture]); ''the sitters on thrones.'' I put only ''sit,'' because the scene is present, though the actions follow one another.');
INSERT INTO `mse_bible_footnote` VALUES (8315, 1, 66, 11, 16, 'f', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8316, 1, 66, 11, 17, 'g', 'Note the omission of ''who is to come'' here, as bearing on the force of that expression: see [scripture id="re?n=1:4"]ch. 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8317, 1, 66, 11, 18, 'h', 'Or ''bondmen,'' as [scripture id="re?n=7:3"]ch. 7:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8318, 1, 66, 11, 18, 'i', 'Or ''corrupt.'' See [scripture id="1co?n=3:17"]1Cor. 3:17[/scripture]. Here it is to ''destroy utterly.''');
INSERT INTO `mse_bible_footnote` VALUES (8319, 1, 66, 11, 19, 'k', 'Or ''and the temple of God was opened in the heaven.''');
INSERT INTO `mse_bible_footnote` VALUES (8320, 1, 66, 12, 7, 'a', 'It took place then, as [scripture id="re?n=11:15"]ch. 11:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8321, 1, 66, 12, 7, 'b', 'Lit. ''to go to war,'' i.e. infinitive.');
INSERT INTO `mse_bible_footnote` VALUES (8322, 1, 66, 12, 7, 'c', 'Same word (not mood) as ''went to war,'' but this is the attack. Hence I have said ''fought.'' They tried to hold their ground, did not flee.');
INSERT INTO `mse_bible_footnote` VALUES (8323, 1, 66, 12, 9, 'd', 'Lit. ''the Satan'' (''adversary''): see [scripture id="job?n=1:6"]Job 1:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8324, 1, 66, 12, 10, 'e', 'Has taken place, is set up, <I>ginomai</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8325, 1, 66, 13, 4, 'f', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8326, 1, 66, 13, 5, 'g', '<I>Poieo</I>: see [scripture id="ro?n=1:32"]Rom. 1:32[/scripture]. Or ''to work,'' or ''act,'' ''<I>practised</I> and prospered'' is said in [scripture id="da?n=8:12"]Dan. 8:12[/scripture], [scripture id="da?n=8:24"]24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8327, 1, 66, 13, 6, 'a', 'Or ''his dwelling, and those who dwell.''');
INSERT INTO `mse_bible_footnote` VALUES (8328, 1, 66, 13, 7, 'b', 'Aorist: it is viewed as a finished act.');
INSERT INTO `mse_bible_footnote` VALUES (8329, 1, 66, 13, 8, 'c', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8330, 1, 66, 13, 8, 'd', '<I>Kosmos</I>: here and [scripture id="re?n=11:15"]chs. 11:15[/scripture]; [scripture id="re?n=17:8"]17:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8331, 1, 66, 14, 6, 'a', 'Lit. ''sitting down,'' as [scripture id="mt?n=4:16"]Matt. 4:16[/scripture], not ''dwelling,'' as [scripture id="re?n=3:10"]ch. 3:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8332, 1, 66, 14, 7, 'b', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8333, 1, 66, 14, 14, 'c', 'As [scripture id="re?n=1:13"]ch. 1:13[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8334, 1, 66, 14, 15, 'd', 'As [scripture id="mt?n=13:6"]Matt. 13:6[/scripture]; [scripture id="jas?n=1:11"]Jas. 1:11[/scripture], ''wither.'' It is more than ''become ripe.'' A different word is used at the end of [scripture id="re?n=14:18"]ver. 18[/scripture], meaning ''in full harvest.''');
INSERT INTO `mse_bible_footnote` VALUES (8335, 1, 66, 15, 1, 'e', 'Aorist.');
INSERT INTO `mse_bible_footnote` VALUES (8336, 1, 66, 15, 3, 'a', 'See [footnote id="n_re?n=1:8a"]Note l, ch. 1:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8337, 1, 66, 15, 4, 'b', '<I>Hosios</I>, not <I>hagios</I>. It is used for mercy, grace, and of Christ, as the One in whom all gracious qualities are concentrated. ([scripture id="ps?n=89:1~a=1"]Ps. 89:1, 2[/scripture], [scripture id="ps?n=89:19"]19[/scripture].) In men it means piety and uprightness. <I>Chesed</I> is so translated in the Old Testament. It is in general the sum of qualities which suit and form the divine character in man, as opposed to the human will: what God gives as consistent with himself, his character, and promise. The ''sure <I>mercies</I>'' of David is expressed by this word in the LXX, [scripture id="isa?n=55:3"]Isa. 55:3[/scripture]; [scripture id="ac?n=13:34"]Acts 13:34[/scripture]. God alone possesses the qualities which entitle him to worship as a pious man would understand it. <I>Hosios</I> is used in this general way for ''holy:'' see [footnote id="n_heb?n=7:26"]Note i, Heb. 7:26[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8338, 1, 66, 15, 4, 'c', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8339, 1, 66, 15, 4, 'd', 'See [footnote id="n_re?n=19:8"]Note, ch. 19:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8340, 1, 66, 15, 5, 'e', '<I>Naos</I>: see [scripture id="re?n=3:12"]ch. 3:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8341, 1, 66, 16, 1, 'f', '<I>Eis</I>. As ''on,'' [scripture id="re?n=8:5"]ch. 8:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8342, 1, 66, 16, 2, 'g', '<I>Epi</I>, refers to actual place.');
INSERT INTO `mse_bible_footnote` VALUES (8343, 1, 66, 16, 2, 'h', 'Or ''did homage to:'' see [scripture id="re?n=3:9"]ch. 3:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8344, 1, 66, 16, 3, 'i', 'Or ''there was blood.'' <I>Ginomai</I>: see [scripture id="joh?n=1:17"]John 1:17[/scripture]. Strictly ''and there was,'' or ''came to be,'' ''blood.'' But I apprehend ''it'' ([scripture id="re?n=16:3"]ver. 3[/scripture]) and ''they'' ([scripture id="re?n=16:4"]ver. 4[/scripture]) are the sense, in spite of accuracy of grammar: blood took place as a consequence in what is spoken of. The sense is more striking as it stands in Greek considered as a vision. What he saw took that character.');
INSERT INTO `mse_bible_footnote` VALUES (8345, 1, 66, 16, 3, 'k', 'Lit. ''every soul of life:'' a Hebraism.');
INSERT INTO `mse_bible_footnote` VALUES (8346, 1, 66, 16, 5, 'l', '<I>Hosios</I>: see [footnote id="n_re?n=15:4"]Note b, ch. 15:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8347, 1, 66, 16, 17, 'a', 'Many authorities omit the words ''of the heaven.''');
INSERT INTO `mse_bible_footnote` VALUES (8348, 1, 66, 16, 17, 'b', 'Or ''it is over, past.'' <I>Ginomai</I>: perfect tense.');
INSERT INTO `mse_bible_footnote` VALUES (8349, 1, 66, 16, 18, 'c', 'Lit. ''began to be.'' <I>Ginomai</I>: aorists.');
INSERT INTO `mse_bible_footnote` VALUES (8350, 1, 66, 17, 4, 'd', 'Lit. ''was made golden with:'' so [scripture id="re?n=18:16"]ch. 18:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8351, 1, 66, 17, 4, 'e', 'Possibly, ''of the fornication of the earth.''');
INSERT INTO `mse_bible_footnote` VALUES (8352, 1, 66, 17, 8, 'a', 'Or ''shall come.''');
INSERT INTO `mse_bible_footnote` VALUES (8353, 1, 66, 17, 9, 'b', '<I>Nous</I>, [scripture id="re?n=17:9"]ver. 9[/scripture] and [scripture id="re?n=13:18"]ch. 13:18[/scripture], <I>understanding</I>: see [footnote id="n_1co?n=1:5"]Note 1Cor. 1:5[/footnote]. <I>Gnome</I> in [scripture id="re?n=17:13"]vers. 13[/scripture], [scripture id="re?n=17:17"]17[/scripture] only; the mind expressed; as ''opinion,'' [scripture id="1co?n=1:10"]1Cor. 1:10[/scripture]; [scripture id="1co?n=7:25"]7:25[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8354, 1, 66, 17, 10, 'c', 'Or ''... sits, and are seven kings.''');
INSERT INTO `mse_bible_footnote` VALUES (8355, 1, 66, 17, 17, 'd', '''Do'' and ''act'' they <I>poieo</I>: see [scripture id="ro?n=1:32"]Rom. 1:32[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8356, 1, 66, 17, 17, 'e', 'See [footnote id="n_re?n=17:9"]Note b, ver. 9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8357, 1, 66, 18, 2, 'f', 'Or ''prison,'' where they are confined.');
INSERT INTO `mse_bible_footnote` VALUES (8358, 1, 66, 18, 5, 'g', 'Lit. ''joined together.''');
INSERT INTO `mse_bible_footnote` VALUES (8359, 1, 66, 18, 6, 'h', 'Some authorities omit ''to her,'' in which case we must say ''and double twofold according to her works.''');
INSERT INTO `mse_bible_footnote` VALUES (8360, 1, 66, 18, 8, 'a', 'See [footnote id="n_re?n=1:8a"]Note l, ch. 1:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8361, 1, 66, 18, 11, 'b', 'Or ''merchandise.''');
INSERT INTO `mse_bible_footnote` VALUES (8362, 1, 66, 18, 13, 'c', 'A spice-plant.');
INSERT INTO `mse_bible_footnote` VALUES (8363, 1, 66, 18, 14, 'd', 'Lit. ''ripe fruit of the lust.''');
INSERT INTO `mse_bible_footnote` VALUES (8364, 1, 66, 18, 17, 'e', 'To any particular place; as we say now, chartered for such or such a place, or sailing to the place.');
INSERT INTO `mse_bible_footnote` VALUES (8365, 1, 66, 19, 1, 'f', 'Or ''crowd'' <I>of people</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8366, 1, 66, 19, 2, 'a', 'Of such a character, see [scripture id="mt?n=7:24"]Matt. 7:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8367, 1, 66, 19, 4, 'b', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8368, 1, 66, 19, 4, 'c', 'See [footnote id="n_re?n=5:1"]Note, ch. 5:7[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8369, 1, 66, 19, 6, 'd', 'Or ''crowd'' <I>of people</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8370, 1, 66, 19, 8, 'e', '<I>Dikaioma</I>: see [scripture id="ro?n=1:32"]Rom. 1:32[/scripture]. In Hebrew the plural of acts expressing a quality is used for the abstract quality itself. This may be the case, by analogy, here. See [scripture id="ps?n=11:7"]Ps. 11:7[/scripture], where, in Hebrew, it is ''righteousnesses,'' but it is actual, not imputed, righteousness.');
INSERT INTO `mse_bible_footnote` VALUES (8371, 1, 66, 19, 13, 'f', '<I>Logos</I>: see [scripture id="joh?n=1:1"]John 1:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8372, 1, 66, 19, 17, 'g', 'Lit. ''one.''');
INSERT INTO `mse_bible_footnote` VALUES (8373, 1, 66, 19, 20, 'a', 'I do not say ''had received'' here, because then the deceiving would come after the receiving, which is not the sense.');
INSERT INTO `mse_bible_footnote` VALUES (8374, 1, 66, 19, 20, 'b', 'Or ''do homage to.'' As [scripture id="re?n=3:9"]ch. 3:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8375, 1, 66, 20, 2, 'c', 'Lit. ''the Satan.'' The adversary: see [scripture id="re?n=12:9"]ch. 12:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8376, 1, 66, 20, 4, 'd', 'In Daniel no one was seen sitting on them.');
INSERT INTO `mse_bible_footnote` VALUES (8377, 1, 66, 20, 4, 'e', '<I>Hostis</I>, as [scripture id="mt?n=7:24"]Matt. 7:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8378, 1, 66, 20, 4, 'f', 'See [footnote id="n_re?n=3:9"]Note h, ch. 3:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8379, 1, 66, 20, 4, 'g', 'Or ''on the forehead, and on their hand.''');
INSERT INTO `mse_bible_footnote` VALUES (8380, 1, 66, 20, 6, 'h', '<I>Exousia</I>, right or title; by adding ''over'' we get the full sense.');
INSERT INTO `mse_bible_footnote` VALUES (8381, 1, 66, 20, 7, 'i', 'See [footnote id="n_re?n=20:2"]Note c, ver. 2[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8382, 1, 66, 20, 8, 'k', 'Or ''to war.''');
INSERT INTO `mse_bible_footnote` VALUES (8383, 1, 66, 20, 13, 'l', 'See [scripture id="re?n=1:18"]ch. 1:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8384, 1, 66, 20, 14, 'a', 'Or ''This second death is the lake of fire.''');
INSERT INTO `mse_bible_footnote` VALUES (8385, 1, 66, 21, 6, 'b', 'Perhaps ''They are fulfilled.''');
INSERT INTO `mse_bible_footnote` VALUES (8386, 1, 66, 21, 11, 'c', '''Lightbearer'' (used for heavenly luminaries), but here it would seem in general ''brightness'' or ''shining.'' ''She was the lightbearer,'' ''she as a lightbearer was.'' Elsewhere used only [scripture id="php?n=2:15"]Phil. 2:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8387, 1, 66, 21, 22, 'a', 'See [footnote id="n_re?n=1:8a"]Note l, ch. 1:8[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8388, 1, 66, 22, 3, 'b', 'The sense is, <I>none</I> shall exist any more.');
INSERT INTO `mse_bible_footnote` VALUES (8389, 1, 66, 22, 3, 'c', 'Or ''bondmen,'' as [scripture id="re?n=7:3"]ch. 7:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8390, 1, 66, 22, 3, 'd', '<I>Latreuo</I>, as [scripture id="re?n=7:15"]ch. 7:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8391, 1, 66, 22, 8, 'e', 'Or ''worship,'' as [scripture id="re?n=3:9"]ch. 3:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8392, 1, 66, 22, 14, 'a', 'See [footnote id="n_ga?n=2:4"]Note c, Gal. 2:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8393, 1, 66, 22, 15, 'b', 'The article is characteristic, I think, here by force of contrast.');
INSERT INTO `mse_bible_footnote` VALUES (8394, 1, 66, 22, 19, 'c', 'Or ''the things which.''');
INSERT INTO `mse_bible_footnote` VALUES (8395, 1, 45, 1, 1, 'f', '''A called apostle,'' i.e. an apostle by (divine) calling. Cf. [scripture id="ro?n=1:7"]ver. 7[/scripture] ''called saints,'' and see [footnote id="n_ro?n=1:7"]note[/footnote] there.');
INSERT INTO `mse_bible_footnote` VALUES (8396, 1, 45, 1, 2, 'g', '''In holy writings:'' there is no article in the original. The statement of the apostle is general, addressing himself, as he does, to Gentiles.');
INSERT INTO `mse_bible_footnote` VALUES (8397, 1, 45, 1, 4, 'h', 'In many cases it is impossible to put a small or a large S rightly to the word Spirit, as the presence and power of the Holy Spirit characterizes the state, and that and the state are both included; so it is here. It is divine, not merely human, perfectness, and by the Holy Spirit, yet it is Christ''s state. But it is not merely a state, but that state which consists in the presence and power of the Holy Spirit, and is the absolute expression of it. On the whole, I put a large S here, but it is the Son manifested on earth who is spoken of, and characteristic of him. Resurrection was the proof, but he who had eyes to see, saw what came in flesh justified in the Spirit even when here, the same Spirit which was quickening power in resurrection, as [scripture id="1pe?n=3:18"]1Pet. 3:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8398, 1, 45, 1, 4, 'i', '<I>Hagiosune</I>, the nature and quality itself, as [scripture id="2co?n=7:1"]2Cor. 7:1[/scripture] and [scripture id="1th?n=3:13"]1Thess. 3:13[/scripture]. Distinct from <I>hagiasmos</I>, the practical effect produced, the character in activity, translated ''holiness'' in [scripture id="ro?n=6:19"]Rom. 6:19[/scripture], [scripture id="ro?n=6:22"]22[/scripture]; [scripture id="1co?n=1:30"]1Cor. 1:30[/scripture]; [scripture id="1ti?n=2:15"]1Tim. 2:15[/scripture]; [scripture id="heb?n=12:14"]Heb. 12:14[/scripture]; and ''sanctification'' in [scripture id="1th?n=4:3"]1Thess. 4:3[/scripture]; [scripture id="1th?n=4:7"]4:7[/scripture]; [scripture id="2th?n=2:13"]2Thess. 2:13[/scripture] and [scripture id="1pe?n=1:2"]1Pet. 1:2[/scripture]. Another word, <I>hagiotes</I>, is used in [scripture id="heb?n=12:10"]Heb. 12:10[/scripture] (the only time in Scripture), for the quality itself.');
INSERT INTO `mse_bible_footnote` VALUES (8399, 1, 45, 1, 4, 'k', 'Plural, ''of dead persons.''');
INSERT INTO `mse_bible_footnote` VALUES (8400, 1, 45, 1, 6, 'a', '''Called ones of Jesus Christ'' is what they are.');
INSERT INTO `mse_bible_footnote` VALUES (8401, 1, 45, 1, 7, 'b', '''Called saints'' is ambiguous in English, but the sense is that they are saints by the calling of God. The Greek implies that they are already saints, not called to be so.');
INSERT INTO `mse_bible_footnote` VALUES (8402, 1, 45, 1, 10, 'c', 'This does not refer to a prosperous journey, but to the hope that God may favour or prosper him so that he may come; he had long wished it, and hoped that at last it might be granted him.');
INSERT INTO `mse_bible_footnote` VALUES (8403, 1, 45, 1, 17, 'd', '''Righteousness of God.'' The absence of the article may arrest the mind here, and in some other places, in this part of the epistle. It is likely to do so, because <I>the</I> righteousness of God is now a known doctrine; not so when the apostle taught. The righteousness of God was a wholly new thought, as was indeed wrath from heaven: wrath on earth was not. The gospel, or ''glad tidings,'' was the power of God to salvation, because righteousness of God (that kind of righteousness) was revealed -- not a righteousness required of man. See [scripture id="ro?n=3:21"]ch. 3:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8404, 1, 45, 1, 17, 'e', '''By faith.'' The word ''by'' is the same in Greek (<I>ek</I>) as that translated ''on the principle of.'' I have left ''by'' as being a quotation from [scripture id="hab?n=2:4"]Hab. 2:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8405, 1, 45, 1, 18, 'f', '''Wrath of God from heaven is revealed:'' see [footnote id="n_ro?n=1:17"]Note d[/footnote]; ''there'' is merely the impersonal form, not an adverb, but necessary, as it is difficult to put the words in another order without injuring the sense.');
INSERT INTO `mse_bible_footnote` VALUES (8406, 1, 45, 1, 19, 'g', 'Or ''may be.'' The Greek word here is used for ''may be known.'' What is spoken of here is ''knowledge acquirable by nature in contrast with revelation,'' it means what is within the capacity of man''s apprehension. But ''is known'' sufficiently represents that and is more exact.');
INSERT INTO `mse_bible_footnote` VALUES (8407, 1, 45, 1, 20, 'h', 'What is characteristic of God; <I>not</I> ''Godhead,'' as in [scripture id="col?n=2:9"]Col. 2:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8408, 1, 45, 1, 20, 'i', 'The Greek expression does not affirm that they are so, but states the consequence of the display of creative glory in what was seen. ''So that they should,'' or ''might be,'' is ambiguous in English, and implies purpose. Hence I have said ''so as to render,'' which gives the sense.');
INSERT INTO `mse_bible_footnote` VALUES (8409, 1, 45, 1, 21, 'a', 'This is the second reason: the first is [scripture id="ro?n=1:19"]ver. 19[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8410, 1, 45, 1, 21, 'b', 'The ''inward reasonings of the mind,'' as [scripture id="lu?n=9:46"]Luke 9:46[/scripture]. The word ''thoughts'' in English conveys this best.');
INSERT INTO `mse_bible_footnote` VALUES (8411, 1, 45, 1, 25, 'c', 'The word implies the character: ''who were such as,'' see [scripture id="mt?n=7:24"]Matt. 7:24[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8412, 1, 45, 1, 28, 'd', 'Not ''did not like.'' The word means ''to assay, test, prove,'' and thence ''to approve'' as [scripture id="php?n=1:10"]Phil. 1:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8413, 1, 45, 1, 28, 'e', 'Or, as some, ''a mind void of moral discernment.''');
INSERT INTO `mse_bible_footnote` VALUES (8414, 1, 45, 1, 30, 'f', 'I doubt that it has the sense of ''God-haters.'' It acquired the sense of ''abominably wicked:'' as ''cursed children'' does not mean that a curse is pronounced, but that they are fit only to be so named. It is the moral state.');
INSERT INTO `mse_bible_footnote` VALUES (8415, 1, 45, 1, 32, 'g', 'As in [scripture id="ro?n=1:25"]v. 25[/scripture], ''who being such as so knew.''');
INSERT INTO `mse_bible_footnote` VALUES (8416, 1, 45, 1, 32, 'h', '<I>Dikaioma</I>: what the righteous will of God requires; hence even an ordinance of his will, or a righteous act meeting his requirement. See [scripture id="ro?n=5:18"]ch. 5:18[/scripture]; [scripture id="lu?n=1:6"]Luke 1:6[/scripture]; and [scripture id="re?n=15:4"]Rev. 15:4[/scripture], where it is judgment itself. In [scripture id="ro?n=2:5"]ch. 2:5[/scripture], ''righteous judgment'' is <I>dikaiokrisia</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8417, 1, 45, 1, 32, 'i', '<I>Prasso</I>: to do an act: see [scripture id="joh?n=5:29"]John 5:29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8418, 1, 45, 1, 32, 'k', '<I>Poieo</I>: practise a course of action: see [scripture id="joh?n=5:29"]John 5:29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8419, 1, 45, 1, 32, 'l', 'It means that not only their own passions carry them away, but, being thoroughly corrupt, they enjoy other people''s sinning, associating themselves in thought and spirit with them, as taking pleasure in their doing so.');
INSERT INTO `mse_bible_footnote` VALUES (8420, 1, 45, 2, 1, 'm', 'A play on words in Greek, strengthening the sense. The words for ''judgment'' and ''condemnest'' are from the same root.');
INSERT INTO `mse_bible_footnote` VALUES (8421, 1, 45, 2, 7, 'a', 'Lit. ''good work,'' singular.');
INSERT INTO `mse_bible_footnote` VALUES (8422, 1, 45, 2, 7, 'b', 'Not ''immortality;'' the resurrection, or change, of the body is looked for; and it is thus a part of Christian truth: see [scripture id="2ti?n=1:10"]2Tim. 1:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8423, 1, 45, 2, 15, 'c', 'As [scripture id="ro?n=1:32"]ch. 1:32[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8424, 1, 45, 2, 15, 'd', 'It is not the law, but the work which is written.');
INSERT INTO `mse_bible_footnote` VALUES (8425, 1, 45, 2, 22, 'e', 'See [scripture id="ac?n=19:37"]Acts 19:37[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8426, 1, 45, 2, 24, 'f', 'See [scripture id="isa?n=52:5"]Isa. 52:5[/scripture]; [scripture id="eze?n=36:20~a=3"]Ezek. 36:20-23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8427, 1, 45, 2, 26, 'g', '<I>Dikaioma</I>, as ''righteous judgment,'' [scripture id="ro?n=1:32"]ch. 1:32[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8428, 1, 45, 2, 26, 'h', 'Often translated ''imputed'' in A.V.');
INSERT INTO `mse_bible_footnote` VALUES (8429, 1, 45, 2, 27, 'i', '<I>Dia</I>. The expression has the sense of ''in a given state or condition,'' as well as ''by means of.'' Their having or possessing the letter of the law and circumcision practically puts the Jews in that condition.');
INSERT INTO `mse_bible_footnote` VALUES (8430, 1, 45, 2, 28, 'k', '''Openly,'' ''manifestedly.''');
INSERT INTO `mse_bible_footnote` VALUES (8431, 1, 45, 2, 29, 'l', '''In what is hidden or secret.''');
INSERT INTO `mse_bible_footnote` VALUES (8432, 1, 45, 3, 3, 'a', 'Or ''faithfulness.''');
INSERT INTO `mse_bible_footnote` VALUES (8433, 1, 45, 3, 4, 'b', 'See [scripture id="ps?n=51:4"]Ps. 51:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8434, 1, 45, 3, 5, 'c', '''Inflicts wrath'' is hardly English, as wrath is a sentiment of the mind; and it is what reaches another which is inflicted, as punishment or pain. But ''executes'' is not the sense, nor is ''takes vengeance.'' The form of expression in Greek, as in the use of ''inflict'' in English, is a figure of the effect for the cause, so I have ventured to use it.');
INSERT INTO `mse_bible_footnote` VALUES (8435, 1, 45, 3, 7, 'd', 'Or ''has more abounded by my lie.'' In the translation in the text the thought is ''he remained true in spite of my failure.''');
INSERT INTO `mse_bible_footnote` VALUES (8436, 1, 45, 3, 12, 'e', '[scripture id="ps?n=14:1~a=2"]Ps. 14:1-3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8437, 1, 45, 3, 13, 'f', '[scripture id="ps?n=5:9"]Ps. 5:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8438, 1, 45, 3, 13, 'g', '[scripture id="ps?n=140:3"]Ps. 140:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8439, 1, 45, 3, 14, 'h', '[scripture id="ps?n=10:7"]Ps. 10:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8440, 1, 45, 3, 17, 'i', '[scripture id="isa?n=59:7~a=1"]Isa. 59:7-8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8441, 1, 45, 3, 18, 'k', '[scripture id="ps?n=36:1"]Ps. 36:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8442, 1, 45, 3, 19, 'l', 'Lit. ''in'' <I>en</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8443, 1, 45, 3, 19, 'm', 'i.e. ''become in that state,'' not future, but the existing consequence.');
INSERT INTO `mse_bible_footnote` VALUES (8444, 1, 45, 3, 21, 'n', '''Apart from:'' see [scripture id="heb?n=9:28"]Heb. 9:28[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8445, 1, 45, 3, 21, 'o', 'See [footnote id="n_ro?n=1:17"]Note, ch. 1:17[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8446, 1, 45, 3, 23, 'p', 'The present fact, ''they come short;'' ''come'' is not dependent on ''have.''');
INSERT INTO `mse_bible_footnote` VALUES (8447, 1, 45, 3, 25, 'q', 'I do not think this word can be used for ''<I>a</I> propitiatory <I>sacrifice</I>'' or ''propitiation;'' it certainly is not the habitual use in the LXX; and we have the two parts of the work of the great day of atonement, here and in [scripture id="ro?n=4:25"]ch. 4:25[/scripture]; ''set forth,'' here, has a reflexive force; see [footnote id="n_heb?n=1:3c"]Note e, Heb. 1:3[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8448, 1, 45, 3, 25, 'a', '''In respect of the passing by the sins.'' I have hesitated as to using <I>dia</I> with the accusative in this sense here; but on the whole I do not doubt it gives the sense. God had passed by, not brought into judgment, the sins of Old Testament believers; and the accomplishment of the atonement showed His righteousness in this. <I>Now</I> the righteousness is itself shown, and to be relied on.');
INSERT INTO `mse_bible_footnote` VALUES (8449, 1, 45, 4, 3, 'b', 'See [scripture id="ge?n=15:6"]Gen. 15:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8450, 1, 45, 4, 3, 'c', 'I am not quite satisfied with ''as;'' but it is the nearest approach to the sense in English. ''For,'' I object to; because then faith is made of positive worth, having the value of righteousness; whereas the sense is that he was holden for righteous in virtue of faith. ''For'' does not go far enough as righteousness; too far as to a positive value of faith. Faith might be reckoned for righteousness, and yet the righteousness come short of what was required; whereas if it be reckoned as righteousness, then the full value of righteousness, as such, is seen: ''the man was held to have righteousness.'' It is a Hebrew form. See [scripture id="ps?n=106:31"]Ps. 106:31[/scripture]. [scripture id="ge?n=15:6"]Gen. 15:6[/scripture], where there is no preposition, makes the force of the expression plain.');
INSERT INTO `mse_bible_footnote` VALUES (8451, 1, 45, 4, 5, 'd', 'See [footnote id="n_ac?n=9:42"]Note, Acts 9:42[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8452, 1, 45, 4, 6, 'e', 'As [scripture id="ro?n=3:21"]ch. 3:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8453, 1, 45, 4, 6, 'f', 'See [scripture id="ps?n=32:1~a=1"]Ps. 32:1-2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8454, 1, 45, 4, 8, 'g', 'That is, ''not account of him as having any.''');
INSERT INTO `mse_bible_footnote` VALUES (8455, 1, 45, 4, 11, 'h', '''In order to his being.'' It is necessary perhaps to say, ''that he might be'' in English; but that is an expression of purpose which goes rather too far. See [footnote id="n_ro?n=1:20a"]Note i, ch. 1:20[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8456, 1, 45, 4, 12, 'a', '''Father of circumcision'' means, he in whom real separation to God was first publicly established. Perhaps ''of the faith, during <I>his</I> uncircumcision, of our father Abraham'' may be clearer.');
INSERT INTO `mse_bible_footnote` VALUES (8457, 1, 45, 4, 15, 'b', 'Or ''For the law.''');
INSERT INTO `mse_bible_footnote` VALUES (8458, 1, 45, 4, 17, 'c', 'See [scripture id="ge?n=17:5"]Gen. 17:5[/scripture]');
INSERT INTO `mse_bible_footnote` VALUES (8459, 1, 45, 4, 18, 'd', 'Or ''with,'' <I>epi</I>. It is the state or condition of his mind in believing: see [scripture id="ro?n=10:19"]ch. 10:19[/scripture] and [scripture id="1co?n=9:10"]1Cor. 9:10[/scripture], and [footnote id="n_2ti?n=1:12"]Note at 2Tim. 1:12[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8460, 1, 45, 4, 18, 'e', '[scripture id="ge?n=15:5"]Gen. 15:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8461, 1, 45, 4, 20, 'f', '''Found strength in faith'' may be rather free, but ''strengthened by faith'' might very easily be applied to his body, whereas it means that he was inwardly strengthened by faith. It is, I apprehend, in opposition to ''hesitating through unbelief.'' Literally, it is ''gifted with strength.'' Abraham was gifted with strength by faith (i.e. inwardly, in contrast with doubt).');
INSERT INTO `mse_bible_footnote` VALUES (8462, 1, 45, 4, 24, 'g', 'See [footnote id="n_ac?n=9:42"]Note, Acts 9:42[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8463, 1, 45, 4, 25, 'h', 'See [footnote id="n_ro?n=5:16b"]Note k, ch. 5:16[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8464, 1, 45, 5, 2, 'i', 'Perfect tense: ''have obtained and possess.''');
INSERT INTO `mse_bible_footnote` VALUES (8465, 1, 45, 5, 6, 'k', 'Or ''died:'' so [scripture id="ro?n=5:8"]ver. 8[/scripture]. The aorist; but ''has died'' is used in English for the fact, which is the force of the aorist. That is, it is so used where the moral import is an abiding one, and time is not in question, even if no abiding effect is meant. ''He has taken a journey.'' The simple preterite, ''died,'' is not an aorist in English; it is historical, and the fact is viewed as past and done with.');
INSERT INTO `mse_bible_footnote` VALUES (8466, 1, 45, 5, 9, 'a', 'Or ''by his blood,'' ''by his life:'' when <I>en</I> is used morally it has the force of ''in the power of,'' ''in the intrinsic character of;'' thus ''in flesh,'' ''in spirit.'' The article and pronoun here give it a somewhat more instrumental character. ''Through'' in [scripture id="ro?n=5:10"]ver. 10[/scripture] is <I>dia</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8467, 1, 45, 5, 12, 'b', 'The epistle divides itself here, as to doctrine, into two distinct parts, which a new paragraph hardly shows. Up to [scripture id="ro?n=5:11"]ch. 5:11[/scripture] ''sins'' had been treated of; from this point ''sin'' is in view.');
INSERT INTO `mse_bible_footnote` VALUES (8468, 1, 45, 5, 13, 'c', 'This is a different word from that translated ''reckoned'' in [scripture id="ro?n=4:22~a=1"]ch. 4:22, 23[/scripture] (both ''imputed'' in A.V.). There, a man is judicially estimated such or such: here, a particular fault is put to a person''s account. The former is found in [scripture id="ga?n=3:6"]Gal. 3:6[/scripture] and [scripture id="2co?n=5:19"]2Cor. 5:19[/scripture]; this only in [scripture id="phm?n=1:18"]Philem. 18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8469, 1, 45, 5, 14, 'd', 'Or ''has reigned,'' aorist.');
INSERT INTO `mse_bible_footnote` VALUES (8470, 1, 45, 5, 14, 'e', 'Or ''according to.'' ''Sinned after'' is not the sense here. It refers to [scripture id="ho?n=6:7"]Hos. 6:7[/scripture], ''They, like Adam, have transgressed the covenant:'' this of Israel. But they who had no law did not. The point here is the form or character in or with which anything happens; that which gives it its character.');
INSERT INTO `mse_bible_footnote` VALUES (8471, 1, 45, 5, 15, 'f', 'Or ''not as the offence <I>is</I> the act of favour.''');
INSERT INTO `mse_bible_footnote` VALUES (8472, 1, 45, 5, 15, 'g', 'Lit. ''the one.''');
INSERT INTO `mse_bible_footnote` VALUES (8473, 1, 45, 5, 16, 'h', 'Or ''And not as by one that sinned <I>is</I> the gift.'' I would add that I have translated the Greek words, <I>dorema</I> as ''gift,'' <I>charisma</I> as ''act of favour,'' and <I>dorea</I> as ''free gift.'' This will distinguish them.');
INSERT INTO `mse_bible_footnote` VALUES (8474, 1, 45, 5, 16, 'i', 'That is, had its foundation in, was grounded on, one single thing or act.');
INSERT INTO `mse_bible_footnote` VALUES (8475, 1, 45, 5, 16, 'k', '<I>Dikaioma</I>; or ''judicial righteousness.'' Here the Greek is more exact than English perhaps allows. It is the state of accomplished subsisting righteousness before God, in which justification places us. The word <I>dikaiosis</I>, ''justification,'' in [scripture id="ro?n=5:18"]ver. 18[/scripture] and [scripture id="ro?n=4:25"]ch. 4:25[/scripture] is the act of justifying. In English we must use justification for both. I cannot say ''righteousness;'' that might be practical. In [scripture id="ro?n=4:25"]ch. 4:25[/scripture] the doing of it was in view, ''for our justifying;'' not, as some read, ''because we are justified,'' which could only be said in connection with faith, whereas ''for our justifying'' is the effect future to the rising again.');
INSERT INTO `mse_bible_footnote` VALUES (8476, 1, 45, 5, 17, 'l', '[scripture id="ro?n=5:13~a=4"]Vers. 13-17[/scripture] are a parenthesis.');
INSERT INTO `mse_bible_footnote` VALUES (8477, 1, 45, 5, 18, 'm', 'Here <I>dikaioma</I>; the same word as translated ''justification,'' [scripture id="ro?n=5:16"]ver. 16[/scripture], but it cannot be so translated here. It is the accomplished subsisting righteousness answering to the one offence.');
INSERT INTO `mse_bible_footnote` VALUES (8478, 1, 45, 5, 20, 'a', 'Came in as an extra thing, or by the by.');
INSERT INTO `mse_bible_footnote` VALUES (8479, 1, 45, 5, 21, 'b', 'i.e. on that principle of righteousness, <I>dikaiosune</I>: i.e. the quality itself, as [scripture id="ro?n=3:21"]ch. 3:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8480, 1, 45, 6, 5, 'c', 'Lit. ''grown up with'' and so thoroughly one: cf. [scripture id="lu?n=8:7"]Luke 8:7[/scripture]. It is not ''planted together with.''');
INSERT INTO `mse_bible_footnote` VALUES (8481, 1, 45, 6, 6, 'd', 'In [scripture id="ro?n=6:6"]ver. 6[/scripture] it is objective knowledge, <I>ginosko</I>. In [scripture id="ro?n=6:9"]vers. 9[/scripture] and [scripture id="ro?n=6:16"]16[/scripture] it is conscious knowledge, <I>oida</I>. See [footnote id="n_1co?n=8:1"]Note to 1Cor. 8:1[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8482, 1, 45, 6, 6, 'e', 'i.e. serve as bondmen.');
INSERT INTO `mse_bible_footnote` VALUES (8483, 1, 45, 6, 7, 'f', '''Free'' is ambiguous. It is justified, cleared, discharged. From sin, note, not sins.');
INSERT INTO `mse_bible_footnote` VALUES (8484, 1, 45, 6, 10, 'g', 'Or ''he died,'' aorist. It is the fact: see [footnote id="n_ro?n=5:6"]Note, ch. 5:6[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8485, 1, 45, 6, 13, 'h', 'There is a distinction between the present tense, which has a continuous present sense, and the aorist, which gives the act in itself. ''Neither yield your members,'' [scripture id="ro?n=6:13"]ver. 13[/scripture] (present tense), means that it is at no time to be done. ''Yield yourselves to God'' and ''yield your members,'' [scripture id="ro?n=6:15"]ver. 15[/scripture] (aorists), mean let it have been done, as a once accomplished act.');
INSERT INTO `mse_bible_footnote` VALUES (8486, 1, 45, 6, 13, 'i', 'Lit. ''as out of dead alive.'' The order of the words gives a contrasted force: not merely that they came thence. But ''out of dead alive'' is hardly English.');
INSERT INTO `mse_bible_footnote` VALUES (8487, 1, 45, 6, 15, 'k', 'Or ''are we to sin?'' the subjunctive aorist.');
INSERT INTO `mse_bible_footnote` VALUES (8488, 1, 45, 6, 19, 'a', 'The word translated ''in bondage'' is an adjective, expressing the state of slavery, ''enslaved.''');
INSERT INTO `mse_bible_footnote` VALUES (8489, 1, 45, 6, 19, 'b', 'See [footnote id="n_ro?n=6:13"]Note h, ver. 13[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8490, 1, 45, 6, 19, 'c', '<I>Hagiasmos</I>. See [footnote id="n_ro?n=1:4a"]Note i at ch. 1:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8491, 1, 45, 7, 1, 'd', 'Or ''the law.''');
INSERT INTO `mse_bible_footnote` VALUES (8492, 1, 45, 7, 1, 'e', 'As ''have dominion,'' [scripture id="ro?n=6:9"]ch. 6:9[/scripture], [scripture id="ro?n=6:14"]14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8493, 1, 45, 7, 6, 'f', 'See [footnote id="n_ga?n=5:4"]Note, Gal. 5:4[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8494, 1, 45, 7, 6, 'g', 'i.e. serve as bondmen.');
INSERT INTO `mse_bible_footnote` VALUES (8495, 1, 45, 7, 7, 'h', 'Or ''the, law <I>is</I> sin.''');
INSERT INTO `mse_bible_footnote` VALUES (8496, 1, 45, 7, 7, 'i', '[scripture id="ex?n=20:17"]Ex. 20:17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8497, 1, 45, 7, 11, 'k', 'A strong form of the word, ''to deceive wholly:'' see [scripture id="ro?n=16:18"]ch. 16:18[/scripture] and [scripture id="1ti?n=2:14"]1Tim. 2:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8498, 1, 45, 7, 13, 'l', 'Or ''working out.''');
INSERT INTO `mse_bible_footnote` VALUES (8499, 1, 45, 7, 14, 'm', '<I>Oida</I>: as [scripture id="ro?n=6:16"]ch. 6:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8500, 1, 45, 7, 14, 'n', 'I have put ''I'' in italic when the personal pronoun <I>ego</I> is emphatically introduced in Greek and the emphasis is not otherwise apparent.');
INSERT INTO `mse_bible_footnote` VALUES (8501, 1, 45, 7, 14, 'o', '<I>Sarkinos</I>: see [footnote id="n_1co?n=3:1"]Note, 1Cor. 3:1[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8502, 1, 45, 7, 15, 'p', '''Work out to a result:'' same word as ''wrought'' in [scripture id="ro?n=7:8"]ver. 8[/scripture] and ''working'' in [scripture id="ro?n=7:13"]ver. 13[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8503, 1, 45, 7, 17, 'a', 'See [footnote id="n_ro?n=7:15"]Note, ver. 15[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8504, 1, 45, 7, 18, 'b', '<I>Oida</I>: as [scripture id="ro?n=6:16"]ch. 6:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8505, 1, 45, 7, 25, 'c', 'i.e. serve as bondman.');
INSERT INTO `mse_bible_footnote` VALUES (8506, 1, 45, 8, 3, 'd', 'i.e. as a sacrifice for sin. The same expression as in [scripture id="heb?n=10:6"]Heb. 10:6[/scripture], [scripture id="heb?n=10:18"]18[/scripture], [scripture id="heb?n=10:26"]26[/scripture]; [scripture id="heb?n=13:11"]13:11[/scripture]. Used frequently in LXX in Leviticus for a ''sin-offering,'' as [scripture id="le?n=5:11"]Lev. 5:11[/scripture]; [scripture id="le?n=7:37"]7:37[/scripture]; [scripture id="le?n=16:5"]16:5[/scripture]; [scripture id="nu?n=8:8"]Num. 8:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8507, 1, 45, 8, 4, 'e', '''Righteous requirement,'' as [scripture id="ro?n=1:32"]ch. 1:32[/scripture]; not the ''habit of righteousness.''');
INSERT INTO `mse_bible_footnote` VALUES (8508, 1, 45, 8, 9, 'f', 'An instance of the difficulty of putting a large or small ''S.'' It is clearly the state and characteristic of the believer; but it is so by the presence of the Spirit. See [scripture id="ro?n=8:15"]ver. 15[/scripture]; [scripture id="ro?n=1:4"]ch. 1:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8509, 1, 45, 8, 15, 'g', '''A spirit of adoption'' might read ''Spirit of adoption.'' See [footnote id="n_ro?n=8:9"]Notes on ver. 9[/footnote] and [footnote id="n_ro?n=1:4"]ch 1:4[/footnote]. ''Adoption'' is the same word as ''sonship'' in [scripture id="ga?n=4:5"]Gal. 4:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8510, 1, 45, 8, 17, 'a', 'Lit. ''If we co-suffer that we may be co-glorified,'' but this is hardly English.');
INSERT INTO `mse_bible_footnote` VALUES (8511, 1, 45, 8, 18, 'b', 'Or ''the glory about to be revealed:'' but the sense is most nearly given in the text. See [scripture id="ga?n=3:23"]Gal. 3:23[/scripture] and [scripture id="1co?n=3:22"]1Cor. 3:22[/scripture]. The emphasis is on ''coming,'' in contrast with ''this present time.''');
INSERT INTO `mse_bible_footnote` VALUES (8512, 1, 45, 8, 19, 'c', 'Or ''constant,'' as [scripture id="php?n=1:20"]Phil. 1:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8513, 1, 45, 8, 21, 'd', '''Glorious liberty,'' as in A.V., does not give the sense. The creature has no part in the liberty of grace; but it will have in that which glory gives.');
INSERT INTO `mse_bible_footnote` VALUES (8514, 1, 45, 8, 22, 'e', '''Creation'' is the same in Greek as ''creature'' in [scripture id="ro?n=8:19~a=2"]vers. 19-21[/scripture]; but the Greek for ''whole'' gives it a concrete and not an abstract character, i.e. ''creation.''');
INSERT INTO `mse_bible_footnote` VALUES (8515, 1, 45, 8, 23, 'f', 'As [scripture id="mr?n=7:34"]Mark 7:34[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8516, 1, 45, 8, 23, 'g', 'See [footnote id="n_ro?n=8:15"]Note, ver. 15[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8517, 1, 45, 8, 26, 'h', 'It means ''to take up a person''s cause, so as to help him.'' The ''with,'' added in Greek, is rendered ''join.'' Only occurs here and [scripture id="lu?n=10:40"]Luke 10:40[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8518, 1, 45, 8, 26, 'i', 'Or ''infirmity.''');
INSERT INTO `mse_bible_footnote` VALUES (8519, 1, 45, 8, 28, 'k', 'Refers to [scripture id="ro?n=8:26"]ver. 26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8520, 1, 45, 8, 29, 'l', 'See [footnote id="n_ro?n=4:11"]Note h, ch. 4:11[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8521, 1, 45, 8, 34, 'm', 'Aorist. See [footnote id="n_ro?n=5:6"]Note to ch. 5:6[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8522, 1, 45, 8, 36, 'n', 'See [scripture id="ps?n=44:22"]Ps. 44:22[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8523, 1, 45, 8, 37, 'a', 'Aorist. See [footnote id="n_ro?n=5:6"]Note to ch. 5:6[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8524, 1, 45, 9, 3, 'b', 'I apprehend, in the apostle''s mind the phrase ''for my brethren'' is connected with both ''pain in my heart'' and ''a curse from Christ;'' he parenthetically states how far his heart had gone for Israel, and then continues the phrase. This want of strict continuation of grammatical structure is very common with the apostle from the ardour of his style, and only adds force to what he says. He had loved them as much as Moses. ([scripture id="ex?n=32:32"]Ex. 32:32[/scripture].) His pain was continuous: but the wish, ''to be a curse,'' was like that of Moses, a moment''s earnest appeal, as beside himself.');
INSERT INTO `mse_bible_footnote` VALUES (8525, 1, 45, 9, 4, 'c', 'As [scripture id="ro?n=8:23"]ch. 8:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8526, 1, 45, 9, 4, 'd', 'As <I>latreuo</I>. See [footnote id="n_mt?n=4:9"]Note l to Matt. 4:10[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8527, 1, 45, 9, 5, 'e', '''Who is over all'' is emphatic: he exists and subsists as such. It may be translated also ''is'' or ''exists God over all.''');
INSERT INTO `mse_bible_footnote` VALUES (8528, 1, 45, 9, 7, 'f', 'See [scripture id="ge?n=21:12"]Gen. 21:12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8529, 1, 45, 9, 9, 'g', 'See [scripture id="ge?n=18:10"]Gen. 18:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8530, 1, 45, 9, 9, 'h', 'The apostle''s object is, not to state what the word of promise is, but that the word he quotes, on which all depended, is a matter of promise.');
INSERT INTO `mse_bible_footnote` VALUES (8531, 1, 45, 9, 12, 'i', 'See [scripture id="ge?n=25:23"]Gen. 25:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8532, 1, 45, 9, 13, 'k', 'See [scripture id="mal?n=1:2"]Mal. 1:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8533, 1, 45, 9, 15, 'l', 'See [scripture id="ex?n=33:19"]Ex. 33:19[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8534, 1, 45, 9, 17, 'm', 'See [scripture id="ex?n=9:16"]Ex. 9:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8535, 1, 45, 9, 17, 'n', 'I have said ''that I might <I>thus</I>'' because the word translated ''that'' does not signify the ultimate end, but the means or way of doing it. The same word is translated ''so that'' in the next clause.');
INSERT INTO `mse_bible_footnote` VALUES (8536, 1, 45, 9, 25, 'a', 'See [scripture id="ho?n=2:23"]Hos. 2:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8537, 1, 45, 9, 26, 'b', 'See [scripture id="ho?n=1:10"]Hos. 1:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8538, 1, 45, 9, 27, 'c', 'See [scripture id="isa?n=10:22~a=1"]Isa. 10:22, 23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8539, 1, 45, 9, 29, 'd', 'See [scripture id="isa?n=1:9"]Isa. 1:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8540, 1, 45, 9, 30, 'e', 'As [scripture id="ro?n=5:21"]ch. 5:21[/scripture], <I>dikaiosune</I>, and so to [scripture id="ro?n=10:10"]ch. 10:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8541, 1, 45, 9, 32, 'f', '''As of works.'' <I>Ek</I>, here translated ''of,'' is translated ''on the principle of'' in [scripture id="ro?n=1:17"]chs. 1:17[/scripture]; [scripture id="ro?n=5:1"]5:1[/scripture]; [scripture id="ro?n=9:32"]9:32[/scripture]. But this sense is clearer here in having simply ''of'' after the ''as,'' and the sentence less encumbered.');
INSERT INTO `mse_bible_footnote` VALUES (8542, 1, 45, 9, 32, 'g', 'Christ is the stumblingstone, as Paul goes on to explain.');
INSERT INTO `mse_bible_footnote` VALUES (8543, 1, 45, 9, 33, 'h', 'See [scripture id="isa?n=8:14"]Isa. 8:14[/scripture] and [scripture id="isa?n=28:16"]28:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8544, 1, 45, 10, 1, 'i', 'i.e. his good pleasure, the thought that delighted him. The order of the words gives the force of ''own,'' or an emphatic ''<I>my</I>.'' The connection of the beginning of the phrase with ''<I>for</I> salvation'' is not very grammatical; but this abruptness of style is usual with Paul. See [footnote id="n_ro?n=9:3"]Note, ch. 9:3[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8545, 1, 45, 10, 1, 'k', 'Some authorities have ''for Israel,'' but ''for them'' is the more correct reading, and, occupied as the apostle is with his subject, is far more beautiful. ''For salvation'' is perhaps a little obscure; but what he says is, what would satisfy his heart was that; and his prayers tended that way, not to their judgment, evil as they were, and rejecters of Christ. But the judgment was not yet revealed.');
INSERT INTO `mse_bible_footnote` VALUES (8546, 1, 45, 10, 5, 'a', 'See [scripture id="le?n=18:5"]Lev. 18:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8547, 1, 45, 10, 6, 'b', 'See [scripture id="de?n=30:12~a=2"]Deut. 30:12-14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8548, 1, 45, 10, 8, 'c', 'Lit. ''herald,'' as [scripture id="ro?n=10:14~a=1"]vers. 14, 15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8549, 1, 45, 10, 9, 'd', 'Or ''<I>the</I> Lord Jesus.''');
INSERT INTO `mse_bible_footnote` VALUES (8550, 1, 45, 10, 10, 'e', 'Or ''man believes.'' ''Is believed'' is literal, and sufficiently intelligible.');
INSERT INTO `mse_bible_footnote` VALUES (8551, 1, 45, 10, 11, 'f', 'See [scripture id="isa?n=28:16"]Isa. 28:16[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8552, 1, 45, 10, 13, 'g', 'See [scripture id="joe?n=2:32"]Joel 2:32[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8553, 1, 45, 10, 15, 'h', 'See [scripture id="isa?n=52:7"]Isa. 52:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8554, 1, 45, 10, 16, 'i', 'See [scripture id="isa?n=53:1"]Isa. 53:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8555, 1, 45, 10, 16, 'k', 'The Greek word for ''report,'' here and twice in [scripture id="ro?n=10:17"]ver. 17[/scripture], includes both what is heard and the hearing.');
INSERT INTO `mse_bible_footnote` VALUES (8556, 1, 45, 10, 18, 'l', 'See [scripture id="ps?n=19:4"]Ps. 19:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8557, 1, 45, 10, 19, 'm', 'See [scripture id="de?n=32:21"]Deut. 32:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8558, 1, 45, 10, 19, 'n', '<I>Epi</I>, ''through,'' signifies the occasion or condition under which a thing happens, not the means of, as an instrument: ''through'' expresses this more nearly than ''by.'' See [footnote id="n_ro?n=5:14a"]Note, ch. 5:14[/footnote], ''in.''');
INSERT INTO `mse_bible_footnote` VALUES (8559, 1, 45, 10, 20, 'o', 'See [scripture id="isa?n=65:1"]Isa. 65:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8560, 1, 45, 10, 21, 'p', 'See [scripture id="isa?n=65:2"]Isa. 65:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8561, 1, 45, 10, 21, 'q', 'Or ''contradicting.'' ''Disobeying'' or ''not believing'' is <I>apeitheo</I>, as [scripture id="ro?n=2:8"]chs. 2:8[/scripture]; [scripture id="ro?n=11:30~a=1"]11:30, 31[/scripture]; [scripture id="ro?n=15:31"]15:31[/scripture]: see [footnote id="n_joh?n=3:36a"]Note e, John 3:36[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8562, 1, 45, 11, 2, 'r', 'See [scripture id="1ki?n=19:14~a=4"]1Kings 19:14-18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8563, 1, 45, 11, 4, 'a', '''Baal'' has the feminine article. It was adored in Syria in masculine and feminine characters.');
INSERT INTO `mse_bible_footnote` VALUES (8564, 1, 45, 11, 6, 'b', 'See [footnote id="n_mt?n=18:32"]Note, Matt. 18:32[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8565, 1, 45, 11, 7, 'c', 'Or ''hardened,'' ''made obdurate in heart.''');
INSERT INTO `mse_bible_footnote` VALUES (8566, 1, 45, 11, 8, 'd', 'See [scripture id="de?n=29:4"]Deut. 29:4[/scripture]; [scripture id="isa?n=6:9~a=1"]Isa. 6:9, 10[/scripture]; [scripture id="isa?n=29:10"]29:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8567, 1, 45, 11, 9, 'e', 'See [scripture id="ps?n=69:22~a=1"]Ps. 69:22-23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8568, 1, 45, 11, 21, 'f', 'This is another case where the grammatical structure is not complete. It may well be taken, ''fear ... lest he spare not thee;'' the beginning of [scripture id="ro?n=11:21"]ver. 21[/scripture] adding a supplementary thought, of which the apostle''s mind was full; still it is a broken phrase.');
INSERT INTO `mse_bible_footnote` VALUES (8569, 1, 45, 11, 22, 'g', 'The subjunctive, ''if thou shouldest abide,'' or ''abidest.'' There are three degrees of condition in Greek: indicative, if the fact arrives; subjunctive, doubtful if it will; and conditional, of uncertain probability.');
INSERT INTO `mse_bible_footnote` VALUES (8570, 1, 45, 11, 25, 'a', 'Or ''obdurateness,'' as [scripture id="ro?n=11:7"]ver. 7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8571, 1, 45, 11, 26, 'b', 'See [scripture id="isa?n=59:20~a=1"]Isa. 59:20-21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8572, 1, 45, 11, 29, 'c', 'i.e. ''irrevocable.'' The Greek only occurs here and [scripture id="2co?n=7:10"]2Cor. 7:10[/scripture], ''never to be regretted.''');
INSERT INTO `mse_bible_footnote` VALUES (8573, 1, 45, 11, 30, 'd', 'See [footnote id="n_ro?n=10:21a"]Note q, ch. 10:21[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8574, 1, 45, 11, 31, 'e', 'This means that the Jews would not believe in the mercy shown to the Gentiles, and thus lost the glad tidings of the grace of God for themselves; and thus, their right to the promises being gone, they come in at the end as objects of mere mercy, as any poor Gentile might be, though, by that mercy, God accomplishes his promises, to which, as to their present responsibility, they had lost all title. It is this which gives rise to the apostle''s expressions of admiration as to the wisdom of God.');
INSERT INTO `mse_bible_footnote` VALUES (8575, 1, 45, 11, 33, 'f', 'Or ''O depth of God''s riches, and wisdom, and knowledge.''');
INSERT INTO `mse_bible_footnote` VALUES (8576, 1, 45, 11, 35, 'g', 'See [scripture id="isa?n=40:13"]Isa. 40:13[/scripture]; [scripture id="job?n=41:11"]Job 41:11[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8577, 1, 45, 11, 36, 'h', '''Of,'' <I>ek</I>; ''through,'' <I>dia</I>; ''for,'' <I>eis</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8578, 1, 45, 12, 2, 'i', 'As [scripture id="1pe?n=1:14"]1Pet. 1:14[/scripture]: ''world'' as [scripture id="1co?n=1:20"]1Cor. 1:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8579, 1, 45, 12, 2, 'k', 'Lit. ''to your proving.''');
INSERT INTO `mse_bible_footnote` VALUES (8580, 1, 45, 12, 3, 'l', 'So as to have a sober judgment, ''to think soberly,'' as ''sensible,'' [scripture id="lu?n=8:35"]Luke 8:35[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8581, 1, 45, 12, 8, 'm', 'Or ''with liberality;'' from ''giving without hesitation,'' or ''not avoiding to give on false excuses,'' it has come to mean ''readily and liberally.''');
INSERT INTO `mse_bible_footnote` VALUES (8582, 1, 45, 12, 10, 'a', '''Taking the lead in paying it'' is a paraphrase, but I know not how to present the sense more briefly.');
INSERT INTO `mse_bible_footnote` VALUES (8583, 1, 45, 12, 11, 'b', 'Or ''by the Spirit.'' See [footnote id="n_ro?n=8:9"]Note at ch. 8:9[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8584, 1, 45, 12, 11, 'c', 'The word means ''to serve as a bondman'' <I>douleuo</I>. It is different from the words used for service in [scripture id="ro?n=12:1"]ver. 1[/scripture] (which is from <I>latreuo</I>), and for service in [scripture id="ro?n=12:7"]ver. 7[/scripture] (which is from <I>diakoneo</I>). See [scripture id="1co?n=4:1"]1Cor. 4:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8585, 1, 45, 12, 16, 'd', 'Or ''with what is lowly.''');
INSERT INTO `mse_bible_footnote` VALUES (8586, 1, 45, 12, 17, 'e', 'Taking care by forethought that there should be what is comely and seemly. See ''forethought,'' [scripture id="ro?n=13:14"]ch. 13:14[/scripture]; [scripture id="2co?n=8:21"]2Cor. 8:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8587, 1, 45, 12, 19, 'f', 'See [scripture id="de?n=32:35"]Deut. 32:35[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8588, 1, 45, 12, 20, 'g', 'See [scripture id="pr?n=25:21~a=1"]Prov. 25:21-22[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8589, 1, 45, 13, 1, 'h', 'Or ''let every soul subject itself:'' it is reflexive; see [footnote id="n_heb?n=1:3c"]Note e, Heb. 1:3[/footnote]. ''Sets himself in opposition,'' [scripture id="ro?n=13:2"]ver. 2[/scripture], is in direct contrast.');
INSERT INTO `mse_bible_footnote` VALUES (8590, 1, 45, 13, 2, 'i', 'Lit. ''shall receive.''');
INSERT INTO `mse_bible_footnote` VALUES (8591, 1, 45, 13, 8, 'k', 'Perfect tense. By the conduct which flows from love, the law is already fulfilled before its requirement is applied.');
INSERT INTO `mse_bible_footnote` VALUES (8592, 1, 45, 13, 9, 'l', 'See [scripture id="ex?n=20:13~a=4"]Ex. 20:13-17[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8593, 1, 45, 13, 9, 'm', 'See [scripture id="le?n=19:18"]Lev. 19:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8594, 1, 45, 13, 10, 'a', 'Lit. ''fulness of law.''');
INSERT INTO `mse_bible_footnote` VALUES (8595, 1, 45, 13, 11, 'b', '''This also;'' assigning another and additional reason for what he says.');
INSERT INTO `mse_bible_footnote` VALUES (8596, 1, 45, 13, 12, 'c', 'Or ''drawn nigh.''');
INSERT INTO `mse_bible_footnote` VALUES (8597, 1, 45, 14, 1, 'd', 'Or ''disputes in.''');
INSERT INTO `mse_bible_footnote` VALUES (8598, 1, 45, 14, 4, 'e', '<I>Oiketes</I>, as in [scripture id="1pe?n=2:18"]1Pet. 2:18[/scripture], ''household servants,'' not necessarily slaves.');
INSERT INTO `mse_bible_footnote` VALUES (8599, 1, 45, 14, 11, 'f', 'See [scripture id="isa?n=45:23"]Isa. 45:23[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8600, 1, 45, 14, 19, 'g', 'Lit. ''the things of peace.''');
INSERT INTO `mse_bible_footnote` VALUES (8601, 1, 45, 14, 19, 'h', 'Or ''the things of edification, in which one <I>builds up</I> another,'' or ''of mutual edification.''');
INSERT INTO `mse_bible_footnote` VALUES (8602, 1, 45, 14, 21, 'a', 'Not ''at'' but ''in'' which he stumbles when he does it.');
INSERT INTO `mse_bible_footnote` VALUES (8603, 1, 45, 15, 2, 'b', 'Or ''for,'' <I>pros</I> with accusative: see [footnote id="n_eph?n=4:12"]Note f, Eph. 4:12[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8604, 1, 45, 15, 3, 'c', 'See [scripture id="ps?n=69:9"]Ps. 69:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8605, 1, 45, 15, 9, 'd', 'See [scripture id="ps?n=18:49"]Ps. 18:49[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8606, 1, 45, 15, 10, 'e', 'See [scripture id="de?n=32:43"]Deut. 32:43[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8607, 1, 45, 15, 11, 'f', 'See [scripture id="ps?n=117:1"]Ps. 117:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8608, 1, 45, 15, 11, 'g', '''Praise'' in this verse is the present tense, see [footnote id="n_ro?n=6:13"]Note h, ch. 6:13[/footnote]. ''Laud'' is the aorist of the same verb, but with a strengthening preposition prefixed, giving the sense of ''let them have him lauded.''');
INSERT INTO `mse_bible_footnote` VALUES (8609, 1, 45, 15, 12, 'h', 'See [scripture id="isa?n=11:10"]Isa. 11:10[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8610, 1, 45, 15, 16, 'i', 'This gives the sense most accurately; the object of the gift was his being minister; ''for,'' <I>eis</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8611, 1, 45, 15, 16, 'k', 'An administrator officially employed: see [scripture id="php?n=2:25"]Phil. 2:25[/scripture]; [scripture id="heb?n=1:7"]Heb. 1:7[/scripture]; [scripture id="heb?n=8:2"]8:2[/scripture]. <I>Leitourgos</I>: as ''officers,'' [scripture id="ro?n=13:6"]ch. 13:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8612, 1, 45, 15, 21, 'a', 'See [scripture id="isa?n=52:15"]Isa. 52:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8613, 1, 45, 15, 24, 'b', 'Or ''whenever I go.'' It is the subjunctive; i.e. not a fact assumed to happen, though not yet come; nor treated as improbable, though it may happen, but between the two -- the possibility and intention expressed. The sentence does not follow grammatically, but is interrupted by ''for I hope,'' &c.');
INSERT INTO `mse_bible_footnote` VALUES (8614, 1, 45, 15, 24, 'c', 'Lit. ''with you.''');
INSERT INTO `mse_bible_footnote` VALUES (8615, 1, 45, 15, 27, 'd', 'It might be taken as a simple repetition of the beginning of [scripture id="ro?n=15:26"]ver. 26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8616, 1, 45, 16, 1, 'e', '<I>Diakonos</I>; deacon, or deaconess; the word ''minister'' here connects with ''to minister'' and ''ministry'' elsewhere, as [scripture id="ro?n=15:31"]ch. 15:31[/scripture]. She did the needed service in the assembly there; she was not properly a servant.');
INSERT INTO `mse_bible_footnote` VALUES (8617, 1, 45, 16, 2, 'f', '''Assist'' is ''to stand by her and help her.'' ''Helper,'' <I>prostatis</I>, in this verse is stronger and has a higher sense, and means ''helping as a patron. '' It was used for a Roman patron, and is applied in special honour to Phoebe as one whose help many had been dependent on and had profited by. It was a complimentary touch of heart in which the apostle never fails: <I>they</I> were to ''stand by her for assistance,'' but <I>she</I> had been ''a patron'' in the matter.');
INSERT INTO `mse_bible_footnote` VALUES (8618, 1, 45, 16, 4, 'g', 'Or ''risked,'' ''hazarded.''');
INSERT INTO `mse_bible_footnote` VALUES (8619, 1, 45, 16, 18, 'a', '<I>Douleuo</I>: ''to serve as a bondman'' as [scripture id="ro?n=12:11"]chs. 12:11[/scripture]; [scripture id="ro?n=14:18"]14:18[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8620, 1, 45, 16, 18, 'b', 'A strong form of the word, as in [scripture id="1ti?n=2:14"]1Tim. 2:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8621, 1, 45, 16, 19, 'c', 'i.e. ''come to the knowledge of.''');
INSERT INTO `mse_bible_footnote` VALUES (8622, 1, 45, 16, 20, 'd', 'As [scripture id="lu?n=9:39"]Luke 9:39[/scripture], ''crushing;'' [scripture id="re?n=2:27"]Rev. 2:27[/scripture]; ''broken in pieces.''');
INSERT INTO `mse_bible_footnote` VALUES (8623, 1, 45, 16, 25, 'e', 'It formed no part of what was unfolded in the ages of time, in which God developed his plans in creation; it was a purpose before, and eternally hidden; it was not in reckoned time. See [scripture id="1co?n=2:7~3"]1Cor. 2:7-10[/scripture]; [scripture id="eph?n=3:2~a=9"]Eph. 3:2-11[/scripture]; [scripture id="eph?n=5:32"]5:32[/scripture]; [scripture id="col?n=1:25~a=2"]Col. 1:25-27[/scripture]; [scripture id="col?n=2:2~a=1"]2:2, 3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8624, 1, 45, 16, 27, 'f', 'The natural construction would be ''to him.'' These breaks, through long parentheses, are common with Paul.');
INSERT INTO `mse_bible_footnote` VALUES (8625, 1, 8, 1, 1, 'a', '<I>Shaphat</I>, lit. ''judged.''');
INSERT INTO `mse_bible_footnote` VALUES (8626, 1, 8, 1, 1, 'b', 'Lit. ''fields,'' ''farmlands,'' as [scripture id="ru?n=1:6"]ver. 6[/scripture], and elsewhere. As [scripture id="2ki?n=8:3"]2Kings 8:3[/scripture], [scripture id="2ki?n=8:5"]5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8627, 1, 8, 1, 2, 'c', 'Meaning, ''whose God is king.''');
INSERT INTO `mse_bible_footnote` VALUES (8628, 1, 8, 1, 2, 'd', 'Meaning, ''my pleasantness.''');
INSERT INTO `mse_bible_footnote` VALUES (8629, 1, 8, 1, 20, 'a', 'Meaning, ''bitter,'' or ''bitterness.''');
INSERT INTO `mse_bible_footnote` VALUES (8630, 1, 8, 2, 1, 'b', 'Or ''friend,'' ''acquaintance.''');
INSERT INTO `mse_bible_footnote` VALUES (8631, 1, 8, 2, 1, 'c', 'Meaning, ''in him is strength.''');
INSERT INTO `mse_bible_footnote` VALUES (8632, 1, 8, 2, 3, 'd', 'Lit. ''portion of <I>the</I> field.''');
INSERT INTO `mse_bible_footnote` VALUES (8633, 1, 8, 2, 5, 'e', 'Or ''young man:'' also [scripture id="ru?n=2:6"]ver. 6[/scripture], as [scripture id="jud?n=19:3"]Judg. 19:3[/scripture] and [scripture id="jud?n=19:9"]9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8634, 1, 8, 2, 10, 'f', 'Or ''stranger.''');
INSERT INTO `mse_bible_footnote` VALUES (8635, 1, 8, 2, 13, 'g', 'Lit. ''spoken to the heart of.''');
INSERT INTO `mse_bible_footnote` VALUES (8636, 1, 8, 2, 13, 'h', 'Or ''bondwoman.''');
INSERT INTO `mse_bible_footnote` VALUES (8637, 1, 8, 2, 16, 'a', 'According to others, ''let fall.''');
INSERT INTO `mse_bible_footnote` VALUES (8638, 1, 8, 2, 16, 'b', 'Or ''sheaves;'' the word is only found in this place.');
INSERT INTO `mse_bible_footnote` VALUES (8639, 1, 8, 2, 20, 'c', 'See [scripture id="le?n=25:25"]Lev. 25:25[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8640, 1, 8, 3, 3, 'd', 'Or ''bathe,'' as elsewhere.');
INSERT INTO `mse_bible_footnote` VALUES (8641, 1, 8, 3, 9, 'e', 'Lit. ''wing,'' as [scripture id="ex?n=19:4"]Ex. 19:4[/scripture]; [scripture id="ps?n=36:7"]Ps. 36:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8642, 1, 8, 3, 15, 'a', 'Some read ''she went.''');
INSERT INTO `mse_bible_footnote` VALUES (8643, 1, 8, 4, 4, 'b', 'Lit. ''said.''');
INSERT INTO `mse_bible_footnote` VALUES (8644, 1, 8, 4, 4, 'c', 'Lit. ''uncover thine ear:'' so in [scripture id="1sa?n=9:15"]1Sam. 9:15[/scripture]; [scripture id="1sa?n=20:2"]20:2[/scripture], [scripture id="1sa?n=20:12~a=1"]12, 13[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8645, 1, 8, 4, 4, 'd', 'Or ''before those that sit here.''');
INSERT INTO `mse_bible_footnote` VALUES (8646, 1, 8, 4, 5, 'e', 'As ''country,'' [scripture id="ru?n=1:1"]ch. 1:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8647, 1, 8, 4, 14, 'a', 'Or ''without a redeemer;'' it refers to the son born, i.e. Obed.');
INSERT INTO `mse_bible_footnote` VALUES (8648, 1, 8, 4, 17, 'b', 'Meaning, ''worshipper'' or ''servant.''');
INSERT INTO `mse_bible_footnote` VALUES (8649, 1, 8, 4, 18, 'c', 'As [scripture id="ge?n=2:4"]Gen. 2:4[/scripture]');
INSERT INTO `mse_bible_footnote` VALUES (8650, 1, 8, 4, 20, 'd', 'Or Salma.');
INSERT INTO `mse_bible_footnote` VALUES (8651, 1, 22, 1, 1, 'a', 'The separate paragraphs indicate, as far as possible, the succession of the various speakers.');
INSERT INTO `mse_bible_footnote` VALUES (8652, 1, 22, 1, 2, 'b', 'Heb. ''loves:'' so [scripture id="so?n=1:4"]ver. 4[/scripture]; [scripture id="so?n=4:10"]chs. 4:10[/scripture]; [scripture id="so?n=7:12"]7:12[/scripture]. It is the same word as that used in the singular for the ''beloved,'' [scripture id="so?n=1:13"]ver. 13[/scripture], &c., whence also the name <I>David</I>.');
INSERT INTO `mse_bible_footnote` VALUES (8653, 1, 22, 1, 4, 'c', 'Or ''celebrate.''');
INSERT INTO `mse_bible_footnote` VALUES (8654, 1, 22, 1, 6, 'd', 'Or ''hath burned me.''');
INSERT INTO `mse_bible_footnote` VALUES (8655, 1, 22, 1, 7, 'e', 'Others, ''roving.''');
INSERT INTO `mse_bible_footnote` VALUES (8656, 1, 22, 1, 9, 'f', 'Or ''friend:'' it is feminine and occurs in [scripture id="so?n=1:15"]ver. 15[/scripture]; [scripture id="so?n=2:2"]chs. 2:2[/scripture], [scripture id="so?n=2:10"]10[/scripture], [scripture id="so?n=2:13"]13[/scripture]; [scripture id="so?n=4:1"]4:1[/scripture], [scripture id="so?n=4:7"]7[/scripture]; [scripture id="so?n=5:2"]5:2[/scripture]; [scripture id="so?n=6:4"]6:4[/scripture]. (It is not the same as [scripture id="so?n=2:7"]chs. 2:7[/scripture]; [scripture id="so?n=3:5"]3:5[/scripture]; [scripture id="so?n=7:6"]7:6[/scripture]; [scripture id="so?n=8:4"]8:4[/scripture].) This feminine form of the word is only found in this Book and [scripture id="jud?n=11:37~a=1"]Judg. 11:37, 38[/scripture], and [scripture id="ps?n=45:14"]Ps. 45:14[/scripture]. The masculine form ''friend'' occurs in [scripture id="so?n=5:1"]ch. 5:1[/scripture], [scripture id="so?n=5:16"]16[/scripture]; [scripture id="2sa?n=15:37"]2Sam. 15:37[/scripture]; [scripture id="2sa?n=16:16"]16:16[/scripture], &c.');
INSERT INTO `mse_bible_footnote` VALUES (8657, 1, 22, 1, 13, 'g', 'Or ''That passeth the night,'' or ''abideth.''');
INSERT INTO `mse_bible_footnote` VALUES (8658, 1, 22, 2, 1, 'a', 'Feminine, as [scripture id="isa?n=35:1"]Isa. 35:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8659, 1, 22, 2, 1, 'b', 'See [scripture id="1ch?n=27:29"]1Chron. 27:29[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8660, 1, 22, 2, 7, 'c', 'Or ''adjure,'' and so [scripture id="so?n=3:5"]chs. 3:5[/scripture]; [scripture id="so?n=5:8~a=1"]5:8, 9[/scripture]; [scripture id="so?n=8:4"]8:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8661, 1, 22, 2, 7, 'd', 'Lit. ''nor awaken love till it please:'' so [scripture id="so?n=3:5"]chs. 3:5[/scripture] and [scripture id="so?n=8:4"]8:4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8662, 1, 22, 2, 9, 'e', 'Or ''showing himself.''');
INSERT INTO `mse_bible_footnote` VALUES (8663, 1, 22, 2, 17, 'f', 'Or ''Return.''');
INSERT INTO `mse_bible_footnote` VALUES (8664, 1, 22, 2, 17, 'g', 'Meaning, cloven, or full of ravines.');
INSERT INTO `mse_bible_footnote` VALUES (8665, 1, 22, 3, 5, 'a', 'See [scripture id="so?n=2:7"]ch. 2:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8666, 1, 22, 3, 10, 'b', 'Or ''bottom part.''');
INSERT INTO `mse_bible_footnote` VALUES (8667, 1, 22, 3, 10, 'c', 'Or ''from.''');
INSERT INTO `mse_bible_footnote` VALUES (8668, 1, 22, 4, 3, 'd', 'Or ''cheeks;'' and so [scripture id="so?n=6:7"]ch. 6:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8669, 1, 22, 4, 8, 'a', '* Said to mean ''firmness'' or ''constancy.''');
INSERT INTO `mse_bible_footnote` VALUES (8670, 1, 22, 4, 14, 'b', 'As ''sweet cane;'' [scripture id="jer?n=6:20"]Jer. 6:20[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8671, 1, 22, 5, 8, 'a', 'Or ''adjure:'' and so in [scripture id="so?n=5:9"]ver. 9[/scripture]; [scripture id="so?n=8:4"]ch. 8:4[/scripture]; see also [scripture id="so?n=2:7"]ch. 2:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8672, 1, 22, 5, 10, 'b', 'Strictly, ''lifted up as a banner.''');
INSERT INTO `mse_bible_footnote` VALUES (8673, 1, 22, 5, 14, 'c', 'Or ''wrought-work of.''');
INSERT INTO `mse_bible_footnote` VALUES (8674, 1, 22, 6, 12, 'a', 'Singular of <I>Ammim</I>: see [scripture id="ps?n=7:8"]Ps. 7:8[/scripture]; ''willing,'' is as [scripture id="ps?n=47:9"]Ps. 47:9[/scripture]: see [scripture id="ps?n=110:3"]Ps. 110:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8675, 1, 22, 6, 13, 'b', 'As [scripture id="ge?n=32:2"]Gen. 32:2[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8676, 1, 22, 7, 9, 'c', 'The feminine speaker interrupts here.');
INSERT INTO `mse_bible_footnote` VALUES (8677, 1, 22, 7, 9, 'd', 'Or ''that floweth straight to.''');
INSERT INTO `mse_bible_footnote` VALUES (8678, 1, 22, 7, 13, 'e', '[scripture id="ge?n=30:14"]Gen. 30:14[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8679, 1, 22, 8, 2, 'f', 'Or ''She would.''');
INSERT INTO `mse_bible_footnote` VALUES (8680, 1, 22, 8, 4, 'a', 'See [scripture id="so?n=2:7"]chs. 2:7[/scripture]; [scripture id="so?n=3:5"]3:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8681, 1, 22, 8, 11, 'b', 'Meaning, ''Master of a multitude.''');
INSERT INTO `mse_bible_footnote` VALUES (8682, 1, 22, 8, 14, 'c', 'Lit. ''Break away.''');
INSERT INTO `mse_bible_footnote` VALUES (8683, 1, 56, 1, 1, 'b', 'In Greek <I>de</I>, a particular additional circumstance, more marked as a distinct relationship, giving occasion to consequences.');
INSERT INTO `mse_bible_footnote` VALUES (8684, 1, 56, 1, 1, 'c', '<I>Epignosis</I>, as [scripture id="col?n=1:9"]Col. 1:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8685, 1, 56, 1, 2, 'd', '<I>Epi</I>: the condition under which the apostolic mission exists.');
INSERT INTO `mse_bible_footnote` VALUES (8686, 1, 56, 1, 8, 'e', 'i.e. ''sober,'' or of ''sound mind,'' as [scripture id="tit?n=2:2"]ch. 2:2[/scripture], [scripture id="tit?n=2:5~a=1"]5, 6[/scripture], [scripture id="tit?n=2:12"]12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8687, 1, 56, 1, 8, 'f', '<I>Hosios</I>, as ''holy,'' [scripture id="heb?n=7:26"]Heb. 7:26[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8688, 1, 56, 1, 9, 'g', '<I>Elenko</I>, as [scripture id="tit?n=2:15"]ch. 2:15[/scripture], ''rebuke,'' see [footnote id="n_joh?n=3:20"]Note, John 3:20[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8689, 1, 56, 1, 13, 'a', '<I>Elenko</I>, as [scripture id="tit?n=1:9"]ver. 9[/scripture], and [scripture id="tit?n=2:15"]ch. 2:15[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8690, 1, 56, 2, 2, 'b', 'i e. ''sober'' or of ''sound mind,'' as [scripture id="tit?n=1:8"]ch. 1:8[/scripture]; [scripture id="tit?n=2:5~a=1"]2:5, 6[/scripture], [scripture id="tit?n=2:12"]12[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8691, 1, 56, 2, 2, 'c', 'Or ''endurance,'' as [scripture id="jas?n=5:11"]Jas. 5:11[/scripture]; but see [scripture id="2th?n=3:5"]2Thess. 3:5[/scripture]; [scripture id="re?n=1:9"]Rev. 1:9[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8692, 1, 56, 2, 4, 'd', 'To impart and enforce by will, counsel, and rebuke, rules of conduct, &c.');
INSERT INTO `mse_bible_footnote` VALUES (8693, 1, 56, 2, 9, 'e', '<I>Despotes</I>, as [scripture id="2ti?n=2:21"]2Tim. 2:21[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8694, 1, 56, 2, 9, 'f', 'The literal translation is ''to be,'' yet I judge I have given the true sense. It is elsewhere used of the Christian towards God or Christ; but to be acceptable is a fact. I cannot exhort a person to <I>be</I> it; to <I>make himself so</I> I can; and that is the sense here.');
INSERT INTO `mse_bible_footnote` VALUES (8695, 1, 56, 2, 9, 'g', 'Or ''contradictory;'' that is, opposing their masters when they speak to them.');
INSERT INTO `mse_bible_footnote` VALUES (8696, 1, 56, 2, 11, 'h', 'Or ''has appeared to all men,'' but I prefer the text.');
INSERT INTO `mse_bible_footnote` VALUES (8697, 1, 56, 2, 12, 'i', 'This word has certainly the sense of discipline, and always perhaps carries with it something of the thought of setting right; but it is used unquestionably for instructing, as in [scripture id="ac?n=7:22"]Acts 7:22[/scripture], and elsewhere.');
INSERT INTO `mse_bible_footnote` VALUES (8698, 1, 56, 2, 12, 'k', 'That is, with self-restraint and consideration, or sound mind; as ''discreet,'' [scripture id="tit?n=1:8"]ch. 1:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8699, 1, 56, 2, 12, 'l', '<I>Aion</I>: see [scripture id="mt?n=13:22"]Matt. 13:22[/scripture], but ''age'' hardly gives the sense. ''World'' gives the false idea of this world as one, and heaven another. It was, for the Jews, the present state of things in contrast with that to be introduced by Messiah.');
INSERT INTO `mse_bible_footnote` VALUES (8700, 1, 56, 2, 14, 'm', 'Or ''has given.'' Aorist.');
INSERT INTO `mse_bible_footnote` VALUES (8701, 1, 56, 3, 4, 'a', 'Lit. ''philanthropy.''');
INSERT INTO `mse_bible_footnote` VALUES (8702, 1, 56, 3, 5, 'b', '''Washing'' is right here. It is a bath, or the water for it. ''Regeneration'' is not the same word as ''being born again,'' nor is it used so in scripture. The force of the word is a change of position; a new state of things. The word is only used here, and in [scripture id="mt?n=19:28"]Matt. 19:28[/scripture] for the Saviour''s coming kingdom.');
INSERT INTO `mse_bible_footnote` VALUES (8703, 1, 56, 3, 7, 'c', 'This refers to our Saviour God, [scripture id="tit?n=3:4"]ver. 4[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8704, 1, 56, 3, 15, 'd', '<I>Phileo</I>: see [footnote id="n_joh?n=21:15"]Note b, John 21:15[/footnote].');
INSERT INTO `mse_bible_footnote` VALUES (8705, 1, 38, 1, 1, 'd', '(The title of this Book, ''Zechariah''), Said to mean, ''Whom Jah remembers.''');
INSERT INTO `mse_bible_footnote` VALUES (8706, 1, 38, 1, 1, 'e', 'As [scripture id="hag?n=1:1"]Hag. 1:1[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8707, 1, 38, 1, 3, 'f', 'This name is in Zech. 52 times.');
INSERT INTO `mse_bible_footnote` VALUES (8708, 1, 38, 1, 6, 'a', 'Or ''devised,'' ''purposed,'' as [scripture id="jer?n=4:28"]Jer. 4:28[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8709, 1, 38, 1, 8, 'b', 'Or ''that night.''');
INSERT INTO `mse_bible_footnote` VALUES (8710, 1, 38, 1, 17, 'c', 'Or ''spread out because of.''');
INSERT INTO `mse_bible_footnote` VALUES (8711, 1, 38, 1, 18, 'd', 'In the Hebrew, [scripture id="zec?n=2"]ch. 2[/scripture] begins here.');
INSERT INTO `mse_bible_footnote` VALUES (8712, 1, 38, 1, 20, 'e', 'Or ''smiths,'' ''carpenters.''');
INSERT INTO `mse_bible_footnote` VALUES (8713, 1, 38, 2, 4, 'a', 'Or ''be inhabited as the open country.''');
INSERT INTO `mse_bible_footnote` VALUES (8714, 1, 38, 2, 11, 'b', 'Or ''great,'' as [scripture id="eze?n=17:8"]Ezek. 17:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8715, 1, 38, 2, 13, 'c', 'Or ''is awaked,'' as [scripture id="zec?n=4:1"]ch. 4:1[/scripture]; [scripture id="zec?n=13:7"]13:7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8716, 1, 38, 3, 1, 'd', 'Or ''to be his adversary:'' from the same root as ''Satan,'' which has the article, as [scripture id="zec?n=3:2"]ver. 2[/scripture]: see [scripture id="ps?n=109:6"]Ps. 109:6[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8717, 1, 38, 3, 5, 'e', 'Or ''tiara;'' or ''diadem,'' as [scripture id="isa?n=62:3"]Isa. 62:3[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8718, 1, 38, 3, 7, 'f', 'Lit. ''give thee goings.''');
INSERT INTO `mse_bible_footnote` VALUES (8719, 1, 38, 3, 8, 'g', 'Men to be observed as signs, or types.');
INSERT INTO `mse_bible_footnote` VALUES (8720, 1, 38, 3, 8, 'h', 'Strictly, ''Sprout:'' see [scripture id="zec?n=6:12"]ch. 6:12[/scripture]; [scripture id="jer?n=23:5"]Jer. 23:5[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8721, 1, 38, 4, 2, 'a', 'Others ''its bowl.''');
INSERT INTO `mse_bible_footnote` VALUES (8722, 1, 38, 4, 2, 'b', 'Or ''seven and seven,'' i.e. seven pipes to each lamp.');
INSERT INTO `mse_bible_footnote` VALUES (8723, 1, 38, 4, 6, 'c', 'Or ''forces.''');
INSERT INTO `mse_bible_footnote` VALUES (8724, 1, 38, 4, 12, 'd', 'Or ''which, by means of ... tubes, empty.''');
INSERT INTO `mse_bible_footnote` VALUES (8725, 1, 38, 5, 3, 'e', 'Or ''earth,'' as [scripture id="1sa?n=2:8"]1Sam. 2:8[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8726, 1, 38, 5, 6, 'f', 'Or ''look;'' ''iniquity,'' LXX.');
INSERT INTO `mse_bible_footnote` VALUES (8727, 1, 38, 6, 3, 'a', 'Or ''vigorous;'' others ''red;'' so [scripture id="zec?n=6:7"]ver 7[/scripture].');
INSERT INTO `mse_bible_footnote` VALUES (8728, 1, 38, 6, 5, 'b', 'Or ''presenting themselves,'' as [scripture id="job?n=1:6"]Job 1:6[/scripture].');
